import React, { useContext } from 'react';
import { makeStyles } from '@material-ui/core/styles';

import Button from '@material-ui/core/Button';
import Box from '@material-ui/core/Box';
import Avatar from '@material-ui/core/Avatar';
import NotificationsIcon from '@material-ui/icons/Notifications';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import Badge from '@material-ui/core/Badge';
import CircularProgress from '@material-ui/core/CircularProgress';

import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';

import {UserContext} from '../providers/UserProvider';
import * as firebase from 'firebase';


const useStyles = makeStyles((theme) => ({
  iconButton: {
    color: 'rgba(43, 43, 40, 0.7)',
    cursor: 'pointer',
  },
  button: {
    margin: theme.spacing(1),
    padding: '10px',
    width: '100%',
    borderRadius: 30,
    fontFamily: 'Poppins',
    fontSize: '1em',
    fontWeight: '600',
    textTransform: 'none',
    color: 'rgba(43, 43, 40, 0.7)'
  },
  sideBar: {
    alignItems: 'center',
    justifyContent: 'center',
    verticalAlign: 'top',
    width: '12%',
    height: '-webkit-fill-available',
    backgroundColor: '#fefefe',
    borderRadius: '0 30px 0 0',
    boxShadow: '5px 0px 20px 0px rgba(0,0,0,0.1)',
    textAlign: 'center',
  },
  userMenuButton: {
    margin: '20px 0px',
    display: 'flex',
    flexDirection: 'row',
    justifyContent: 'center',
    alignItems: 'center',
    cursor: 'pointer',
  },
}));

export function SideBar() {
    const classes = useStyles();

    const user = useContext(UserContext);
    const {email, firstName, lastName} = user;

    const [invisible, setInvisible] = React.useState(false);

    const handleBadgeVisibility = () => {
        setInvisible(!invisible);
    };

    const [anchorEl, setAnchorEl] = React.useState(null);

    const handleClick = (event) => {
        setAnchorEl(event.currentTarget);
    };

    const handleClose = () => {
        setAnchorEl(null);
    };

    return (
        <Box className={classes.sideBar}>
            <Avatar alt={firstName + " " + lastName} src="/static/images/avatar/1.jpg" style={{alignSelf: 'center', margin: '15px auto', width: '50px', height: '50px'}} />

            {!user.email ? 
                <span  className={classes.userMenuButton}>
                    <CircularProgress size={25} thickness={5}/>
                </span> 
                :
                <span onClick={handleClick} className={classes.userMenuButton}>
                    <h3 style={{fontFamily:'Poppins', fontSize:'16px', fontWeight:'500', color:'#4d4d47'}} >{firstName + " " + lastName}</h3>
                    <ExpandMoreIcon style={{marginLeft: '5px'}}/>
                </span>
            }

            <Badge color="secondary" variant="dot" color="primary" className={classes.iconButton} onClick={handleBadgeVisibility} invisible={invisible}>
                <NotificationsIcon style={{ fontSize: '30px' }}/>
            </Badge>

            <Box paddingLeft={2} paddingRight={5} marginTop={'20px'}>
                <Button className={classes.button} onClick={loadData}>
                    Hlavný plán
                </Button>
                <Button className={classes.button}>
                    Denný plán
                </Button>
            </Box>

            <Menu
                classes={{ root: classes.taskSelect }}
                anchorEl={anchorEl}
                anchorOrigin={{ vertical: "bottom", horizontal: "center" }}
                transformOrigin={{ vertical: "top", horizontal: "center" }}
                getContentAnchorEl={null}
                open={Boolean(anchorEl)}
                onClose={handleClose}
            >
                <MenuItem onClick={handleClose} style={{fontFamily:'Poppins'}} >Nastavenia</MenuItem>
                <MenuItem onClick={() => {firebase.auth().signOut()}} style={{fontFamily:'Poppins'}}>Odhlásit sa</MenuItem>
            </Menu>
        </Box>
    );
  }
