import React, { Component, useState } from 'react';
import './App.css';
import {
  Route,
  BrowserRouter as Router,
  Switch,
  Redirect,
} from "react-router-dom";
import { ProductionTasksList } from './sections/ProductionTasksList';
import { WorkTasksList } from './sections/WorkTasksList';
import { CommTasksList } from './sections/CommTasksList';
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Button from '@material-ui/core/Button';
import Avatar from '@material-ui/core/Avatar';
import Box from '@material-ui/core/Box';
import NotificationsIcon from '@material-ui/icons/Notifications';
import Badge from '@material-ui/core/Badge';
import moment from 'moment';
import 'moment/locale/sk';
import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';

export const useStyles = makeStyles((theme) => ({
  main: {
    backgroundColor: '#f8f8f8',
    height: '100%',
  },
  sideBar: {
    alignItems: 'center',
    justifyContent: 'center',
    verticalAlign: 'top',
    width: '15%',
    height: '-webkit-fill-available',
    backgroundColor: '#fefefe',
    borderRadius: '0 30px 0 0',
    boxShadow: '5px 0px 20px 0px rgba(0,0,0,0.1)',
  },
  root: {
    padding: '2px 10px',
    display: 'flex',
    alignItems: 'center',
    width: 300,
    borderColor: '#f1d6ab',
    borderRadius: 50,
    background: '#00000040',
  },
  input: {
    marginLeft: theme.spacing(1),
    flex: 1,
    color: '#f1d6ab',
    fontFamily: 'Sen',
    fontSize: 18,
  },
  iconButton: {
    color: '#f1d6ab',
  },
  button: {
    margin: theme.spacing(1),
    padding: 10,
    width: '100%',
    borderRadius: 30,
    fontFamily: 'Sen',
    fontSize: 20,
    fontWeight: 'bold',
    textTransform: 'none',
  },
  taskListWindow: {
    justifyContent: 'center',
    margin: '40px 50px 0px 50px',
    display: 'flex',
    width: '95%',
    background: '#FAFAFA',
    borderRadius: '30px',
    padding: '20px 0px',
    boxShadow: '0px 0px 15px 0px rgba(0,0,0,0.1)',
  },
  taskBubble: {
    display: 'flex',
    borderRadius: 20,
    padding: 20,
    margin: '20px 0px',
    fontFamily:'Poppins',
    backgroundColor: '#fff',
    color: '#2b2b28',
    width: '95%',
    alignItems: 'center',
    cursor: 'pointer',
    boxShadow: '0px 0px 15px 0px rgba(0,0,0,0.1)',
    position: 'relative',
    right: '12px',
  },
  bubbleTitle: {
    color: '#2b2b2b',
    fontWeight: '700',
    fontSize: 18,
    marginBlockStart: 0,
    marginBlockEnd: 0,
  },
  bubbleDesc: {
    fontWeight: '600',
    fontSize: 15,
    marginBlockStart: 0,
    marginBlockEnd: 0,
  },
  bubbleWorkTime: {
    fontWeight: '600',
    fontSize: '1em',
    marginBlockStart: 0,
    marginBlockEnd: 0,
    paddingTop: '2px',
  },
}));

function SideBar() {
  const classes = useStyles();
  const [invisible, setInvisible] = React.useState(false);

  const handleBadgeVisibility = () => {
    setInvisible(!invisible);
  };

  return (
    <Box className={classes.sideBar}>
      <Box alignSelf="center">
        <Avatar alt="Remy Sharp" src="/static/images/avatar/1.jpg" />
      </Box>
      <Badge color="secondary" variant="dot" className={classes.iconButton} onClick={handleBadgeVisibility} invisible={invisible} aria-label="notification" style={{cursor: 'pointer'}}>
        <NotificationsIcon style={{ fontSize: 25 }}/>
      </Badge>
      <Box paddingLeft={2} paddingRight={5} marginTop={5}>
        <Button className={classes.button}>
          Hlavný plán
        </Button>
        <Button className={classes.button}>
          Denný plán
        </Button>
      </Box>
    </Box>
  );
}



function App() {
  const classes = useStyles();
  moment().locale('sk');
  
  const options = [
    'one', 'two', 'three'
  ];
  const defaultOption = options[0];
  
  return (
    <div style={{ width: '100%', height: '100%', backgroundColor: '#f8f8f8', display:'flex', flexDirection:'row',}} >

      <SideBar />
      
      <div style={{width: '100%', flexDirection:'column',}}>
        <div style={{alignItems:'center', display: 'flex', flexDirection:'row', paddingLeft: '20px'}}>
          <h3>1.</h3>
          <h3>Pondelok</h3>
          <Box className={classes.taskListWindow}>
            <ProductionTasksList />
            <WorkTasksList />
            <CommTasksList /> 
          </Box>
        </div>
        
        <div style={{alignItems:'center', display: 'flex', flexDirection:'row', paddingLeft: '20px'}}>
          <h3>1.</h3>
          <h3>Pondelok</h3>
          <Box className={classes.taskListWindow}>
            <ProductionTasksList />
            <WorkTasksList />
            <CommTasksList /> 
          </Box>
        </div>

        
        <div style={{margin:'50px 20px'}}>
          <div style={{alignItems:'center', display: 'flex', flexDirection:'row', paddingLeft: '20px'}}>
            <Button className={classes.button} onClick={() => setWeek(0)}>
              1.
            </Button>

            <Button className={classes.button} onClick={() => setWeek(1)}>
              2.
            </Button>

            <Button className={classes.button} onClick={() => setWeek(2)}>
              3.
            </Button>

            <Button className={classes.button} onClick={() => setWeek(3)}>
              4.
            </Button>

            <Button className={classes.button} onClick={() => setWeek(4)}>
              5.
            </Button>
          </div>
          <div style={{alignItems:'center', paddingLeft: '20px'}}>
            {weeks[weekState]}
          </div>
          
        </div>
      </div>
    </div>
  );
}

export default App;
