const getMinutes = (index) => {
    if (index % 4 === 0) {
        return '00'
    } else if ( index % 2 === 0 ) {
        return '30'
    } else if ( index % 4 === 1 ) {
        return '15'
    } else {
        return '45'
    }
}

const startTimeSlots = Array.from(new Array(24 * 4)).map(
    (_, index) => `${Math.floor(index / 4)}:${getMinutes(index)}`
);

const endTimeSlots = Array.from(new Array(24 * 4)).map(
    (_, index) => `${Math.floor(index / 4)}:${getMinutes(index)}`
);
