import MomentUtils from '@date-io/moment';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import IconButton from '@material-ui/core/IconButton';
import { withStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import CloseIcon from '@material-ui/icons/Close';
import PlaceIcon from '@material-ui/icons/Place';
import TimerIcon from '@material-ui/icons/Timer';
import TodayIcon from '@material-ui/icons/Today';
import { Alert } from '@material-ui/lab';
import Autocomplete from '@material-ui/lab/Autocomplete';
import { DatePicker, MuiPickersUtilsProvider } from '@material-ui/pickers';
import gql from 'graphql-tag';
import moment from 'moment';
import PropTypes from 'prop-types';
import React, { useContext, useEffect, useState } from 'react';
import GooglePlacesAutocomplete from 'react-google-places-autocomplete';
import { v4 as uuidv4 } from 'uuid';
import '../../../App.css';
import { Status, useAddMeetingTaskMutation } from '../../../generated/graphql';
import { AuthContext } from '../../../providers/UserProvider';
import { endTimeSlots, startTimeSlots } from '../helpers';

const CssTextField = withStyles({
  root: {
    '& .MuiFilledInput-root': {
      fontFamily: 'Poppins',
      paddingTop: 0,
      background: 'rgba(171, 171, 171, 0.15)'
    }
  }
})(TextField);

interface InputProps {
  onClose: () => void;
  open: boolean;
}

export function AddMeetingTask({ onClose, open }: InputProps) {
  const { currentUser: user } = useContext(AuthContext);

  const handleClose = () => {
    onClose();
  };

  const [error, setError] = useState('');

  const [formState, setFormState] = useState({
    id: uuidv4(),
    userId: user.uid,
    date: new Date().toISOString(),
    description: '',
    title: '',
    pending: false,
    status: Status.NotDone,
    endTime: '',
    startTime: '',
    address: '',
    category: ''
  });

  useEffect(() => {
    if (user.uid !== formState.userId)
      setFormState({ ...formState, userId: user.uid });

    return;
  }, [setFormState, formState, user.uid]);

  const [addMeetingTask] = useAddMeetingTaskMutation();

  const handleForm = (e) => {
    e.preventDefault();

    if (formState.title === '') return setError('Title must be filled in!');
    if (formState.description === '')
      return setError('Description must be filled in!');

    const epoch = new Date().getTime();

    addMeetingTask({
      variables: {
        ...formState,
        index: parseInt(`1100${epoch}`)
      },
      update: (store, { data }) => {
        try {
          if (data == null) return;

          store.modify({
            id: 'ROOT_QUERY',
            fields: {
              meetingTaskMany(existingMeetingTasksRef, { readField }) {
                const newTaskRef = store.writeFragment({
                  data: data.createTask,
                  fragment: gql`
                    fragment NewMeetingTask on MeetingTask {
                      id
                      user {
                        id
                      }
                      date
                      description
                      title
                      status
                      pending
                      endTime
                      startTime
                      address
                      category
                    }
                  `
                });

                const newTask = data.createTask as any;

                if (
                  existingMeetingTasksRef.items.some(
                    (ref) => readField('id', ref) === newTask.id
                  )
                ) {
                  return {
                    items: [...existingMeetingTasksRef.items]
                  };
                }

                return {
                  items: [...existingMeetingTasksRef.items, newTaskRef]
                };
              }
            }
          });
        } catch (e) {
          console.error(e);
        }
      }
    });

    setFormState({
      id: uuidv4(),
      userId: '1a5s89dwd5as654nmji6d',
      date: new Date().toISOString(),
      description: '',
      title: '',
      pending: false,
      status: Status.NotDone,
      endTime: '',
      startTime: '',
      address: '',
      category: ''
    });

    handleClose();
  };

  return (
    <MuiPickersUtilsProvider utils={MomentUtils}>
      <Dialog
        onClose={handleClose}
        open={open}
        fullWidth={true}
        maxWidth={'xs'}
        PaperProps={{
          style: {
            backgroundColor: '#fff',
            color: 'black',
            borderRadius: '30px',
            padding: '35px',
            boxShadow: '0px 0px 15px 0px rgba(0,0,0,0.15)'
          },
          elevation: 0
        }}
        BackdropProps={{ style: { backgroundColor: 'rgba(0, 0, 0, 0.25)' } }}
      >
        <form onSubmit={(e) => handleForm(e)} style={{ display: 'contents' }}>
          <h1 className="detail-title">VytvoriÅ¥ Ãºlohu</h1>
          <IconButton
            aria-label="close"
            className="close-button"
            onClick={onClose}
          >
            <CloseIcon />
          </IconButton>

          <TextField
            label="NÃ¡zov"
            variant="outlined"
            value={formState.title}
            onChange={(e) =>
              setFormState({ ...formState, title: e.target.value })
            }
            className="add-task-input"
          />

          <TextField
            label="Popis"
            multiline
            rows={3}
            variant="outlined"
            value={formState.description}
            onChange={(e) =>
              setFormState({ ...formState, description: e.target.value })
            }
            className="add-task-input"
          />

          <span
            style={{
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'start',
              padding: '10px 0px',
              paddingLeft: '10px'
            }}
          >
            <PlaceIcon />

            <GooglePlacesAutocomplete
              selectProps={{
                placeholder: 'PridaÅ¥ miesto',
                styles: {
                  control: (provider, state) => ({
                    ...provider,
                    background: 'none',
                    fontFamily: 'Poppins',
                    width: '97.8%',
                    height: '3.8em',
                    borderColor: 'rgba(0, 0, 0, 0.23)',
                    '&:hover': {
                      borderColor: state.isFocused
                        ? '-webkit-focus-ring-color'
                        : 'black'
                    }
                  }),
                  container: (provided) => ({
                    ...provided,
                    width: '100%',
                    marginLeft: '10px'
                  }),
                  input: (provided) => ({
                    ...provided,
                    fontFamily: 'Poppins',
                    color: '#2b2b2b'
                  }),
                  option: (provided) => ({
                    ...provided,
                    fontFamily: 'Poppins',
                    color: '#2b2b2b'
                  }),
                  menu: (provided) => ({
                    ...provided,
                    width: '97.8%'
                  }),
                  singleValue: (provided) => ({
                    ...provided,
                    fontFamily: 'Poppins',
                    color: '#2b2b2b'
                  }),
                  dropdownIndicator: (provider) => ({
                    ...provider,
                    color: 'rgba(0, 0, 0, 0.23)'
                  }),
                  indicatorSeparator: (provider) => ({
                    ...provider,
                    color: 'rgba(0, 0, 0, 0.23)'
                  })
                },
                address: formState.address,
                onChange: (e) =>
                  setFormState({ ...formState, address: e.value.description })
              }}
            />
          </span>

          <div
            style={{
              alignItems: 'center',
              display: 'flex',
              flexDirection: 'row',
              justifyContent: 'space-between',
              paddingLeft: '10px',
              paddingRight: '10px'
            }}
          >
            <span
              style={{
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'start',
                padding: '10px 0px'
              }}
            >
              <TodayIcon />
              <DatePicker
                autoOk
                disableToolbar
                variant="inline"
                inputVariant="outlined"
                style={{
                  width: 90,
                  marginLeft: 10,
                  background: 'rgba(171, 171, 171, 0.15)',
                  borderRadius: '5px',
                  border: 'none'
                }}
                format="D.M.YYYY"
                value={moment(formState.date).format('YYYY-MM-DD')}
                onChange={(date) =>
                  setFormState({
                    ...formState,
                    date: date?.toDate().toISOString() ?? ''
                  })
                }
              />
            </span>

            <span
              style={{
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'start',
                padding: '10px 0px'
              }}
            >
              <TimerIcon />
              <Autocomplete
                id="time-picker"
                options={startTimeSlots}
                style={{ width: 90, marginLeft: 10 }}
                disableClearable
                freeSolo
                value={formState.startTime}
                inputValue={formState.startTime}
                onInputChange={(event, newInputValue) => {
                  setFormState({ ...formState, startTime: newInputValue });
                }}
                renderInput={(params) => (
                  <CssTextField
                    {...params}
                    variant="outlined"
                    style={{
                      background: 'rgba(171, 171, 171, 0.15)',
                      borderRadius: '5px'
                    }}
                  />
                )}
              />
              <h3
                style={{
                  marginLeft: '8px',
                  marginRight: '8px',
                  fontSize: '20px',
                  fontWeight: 400
                }}
              >
                -
              </h3>
              <Autocomplete
                id="time-picker"
                options={endTimeSlots}
                style={{ width: 90 }}
                disableClearable
                freeSolo
                value={formState.endTime}
                inputValue={formState.endTime}
                onInputChange={(event, newInputValue) => {
                  setFormState({ ...formState, endTime: newInputValue });
                }}
                renderInput={(params) => (
                  <CssTextField
                    {...params}
                    variant="outlined"
                    style={{
                      background: 'rgba(171, 171, 171, 0.15)',
                      borderRadius: '5px'
                    }}
                  />
                )}
              />
            </span>
          </div>

          <Button type="submit" className="create-task-button">
            VytvoriÅ¥
          </Button>
        </form>
        {error === '' ? (
          ''
        ) : (
          <Alert severity="error" onClose={() => setError('')}>
            {error}
          </Alert>
        )}
      </Dialog>
    </MuiPickersUtilsProvider>
  );
}

AddMeetingTask.propTypes = {
  onClose: PropTypes.func.isRequired,
  open: PropTypes.bool.isRequired
};
