import React, { useState, createContext, useEffect } from "react";
import * as firebase from "firebase";
import moment from "moment";

import { getISOWeekDates } from "../DateCalc";

export const DataContext = createContext({ currWeek: null, currMonth: null });

function DataProvider(props) {
  const [weekState, setWeek] = useState(moment().week());

  let ISOWeekDates = getISOWeekDates(weekState);
  let currWeek = ISOWeekDates[0];
  let currMonth = ISOWeekDates[1];

  let modWeek = [];

  useEffect(() => {
    // const requestOptions = {
    //   method: "POST",
    //   headers: { "Content-Type": "application/json", Accept: "text/html" },
    //   body: JSON.stringify({ idToken: idToken }),
    // };
    // console.log(idToken);
    // fetch(
    //   "https://europe-west1-plannerapp-960c1.cloudfunctions.net/app/api/auth",
    //   requestOptions
    // )
    //   .then((response) => response.json())
    //   .then((data) => setUser(data));
    for (const i of currWeek) {
      modWeek.push(moment(i).format("DD/MM/yyyy"));
    }

    console.log(modWeek);

    const requestOptions = {
      method: "POST",
      headers: { "Content-Type": "application/json", Accept: "*/*" },
      body: JSON.stringify({ date: modWeek }),
    };
    fetch(
      "https://europe-west1-plannerapp-960c1.cloudfunctions.net/app/api/get-work-tasks",
      requestOptions
    )
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
      });
  }, []);

  return (
    <DataContext.Provider value={{ currWeek: currWeek, currMonth: currMonth }}>
      {props.children}
    </DataContext.Provider>
  );
}

export default DataProvider;
