import MomentUtils from '@date-io/moment';
import { IconButton } from '@material-ui/core';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import FormControl from '@material-ui/core/FormControl';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import Select from '@material-ui/core/Select';
import TextField from '@material-ui/core/TextField';
import { Close, Flag, FormatAlignLeft, Timer, Today } from '@material-ui/icons';
import Autocomplete from '@material-ui/lab/Autocomplete';
import { DatePicker, MuiPickersUtilsProvider } from '@material-ui/pickers';
import moment from 'moment';
import React, { useContext, useEffect, useState } from 'react';
import '../../../App.css';
import {
  Status,
  useDeleteWorkTaskMutation,
  useEditWorkTaskMutation,
  WorkTask,
  WorkTasksDocument
} from '../../../generated/graphql';
import { WeekContext } from '../../../providers/WeekProvider';
import { notification } from '../../Notification';
import { getTaskStatusData } from '../Bubble/TaskStatusData';
import { menuProps, timeSlots } from '../helpers';
import { PickWorkTask } from '../RenderTaskList';

interface InputProps {
  open: boolean;
  onClose: () => void;
  selectedTask: Partial<Omit<PickWorkTask, 'status'>> & {
    status: Status | undefined;
    statusChanged: boolean;
  };
  setSelectedTask: React.Dispatch<
    React.SetStateAction<
      Partial<PickWorkTask> & {
        statusChanged: boolean;
      }
    >
  >;
  previousState: Partial<
    Pick<WorkTask, 'id' | 'date' | 'status'> & {
      userId: string;
    }
  >;
  setPreviousStatusState: React.Dispatch<
    React.SetStateAction<
      Partial<
        Pick<WorkTask, 'id' | 'date' | 'status'> & {
          userId: string;
        }
      >
    >
  >;
}

export function EditWorkTask({
  onClose,
  selectedTask,
  setSelectedTask,
  open,
  setPreviousStatusState,
  previousState
}: InputProps) {
  const { currWeek } = useContext(WeekContext);

  const handleClose = () => {
    onClose();
  };

  const [formState, setFormState] = useState({
    id: selectedTask.id,
    userId: selectedTask?.user?.id,
    date: selectedTask.date,
    description: selectedTask.description,
    title: selectedTask.title,
    status: selectedTask.status,
    workTime: selectedTask.workTime,
    pending: selectedTask.pending
  });

  useEffect(() => {
    if (selectedTask.id !== formState.id)
      setFormState({
        id: selectedTask.id,
        userId: selectedTask?.user?.id,
        date: selectedTask.date,
        description: selectedTask.description,
        title: selectedTask.title,
        status: selectedTask.status,
        workTime: selectedTask.workTime,
        pending: selectedTask.pending
      });
    if (selectedTask.statusChanged === true) {
      setSelectedTask({
        ...selectedTask,
        statusChanged: false
      });
      setFormState({
        ...formState,
        status: selectedTask.status
      });
    }
  }, [selectedTask, formState, setSelectedTask]);

  const [anchorEl, setAnchorEl] = useState(null);
  const handleClickOnMore = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleCloseMore = () => {
    setAnchorEl(null);
  };

  const [editTask] = useEditWorkTaskMutation({
    variables: {
      date: formState.date ?? '',
      description: formState.description ?? '',
      id: formState.id ?? '',
      pending: formState.pending ?? false,
      status: formState.status ?? Status.NotDone,
      userId: formState.userId ?? '',
      title: formState.title ?? '',
      workTime: formState.workTime ?? ''
    },
    optimisticResponse: {
      editTask: {
        ...selectedTask,
        date: formState.date ?? '',
        description: formState.description ?? '',
        id: formState.id ?? '',
        status: formState.status ?? Status.NotDone,
        title: formState.title ?? '',
        workTime: formState.workTime ?? ''
      }
    },
    update: (store, { data }) => {
      store.readQuery<any>({
        query: WorkTasksDocument,
        variables: {
          userId: selectedTask?.user?.id ?? '',
          dateFrom: new Date(currWeek[0]).toISOString(),
          dateTo: new Date(currWeek[6]).toISOString()
        }
      });

      return store.writeQuery({
        query: WorkTasksDocument,
        variables: {
          userId: selectedTask?.user?.id ?? '',
          dateFrom: new Date(currWeek[0]).toISOString(),
          dateTo: new Date(currWeek[6]).toISOString()
        },
        data: { items: [data?.editTask] }
      });
    }
  });

  const [deleteTask] = useDeleteWorkTaskMutation({
    variables: {
      id: selectedTask.id ?? ''
    },
    update: (store, { data }) => {
      if (data == null) return;

      store.modify({
        id: 'ROOT_QUERY',
        fields: {
          workTaskMany(existingWorkTasksRef, { readField }) {
            return {
              items: existingWorkTasksRef.items.filter(
                (taskRef) => selectedTask.id !== readField('id', taskRef)
              )
            };
          }
        }
      });
    }
  });

  return (
    <MuiPickersUtilsProvider utils={MomentUtils}>
      <Dialog
        onClose={handleClose}
        open={open}
        fullWidth={true}
        maxWidth={'md'}
        PaperProps={{
          style: {
            height: '60%',
            backgroundColor: '#fff',
            color: 'black',
            borderRadius: '30px',
            padding: '35px',
            boxShadow: '0px 0px 15px 0px rgba(0,0,0,0.15)',
            transition: 'all 1s'
          },
          elevation: 0
        }}
        BackdropProps={{ style: { backgroundColor: 'rgba(0, 0, 0, 0.25)' } }}
      >
        <div style={{}}>
          <TextField
            style={{ width: '70%', marginBottom: '1em' }}
            className="task-title-input"
            value={formState.title}
            onChange={(e) =>
              setFormState({ ...formState, title: e.target.value })
            }
          />

          <Button
            className="update-task-button"
            onClick={() => {
              editTask();
              setPreviousStatusState({
                ...previousState,
                status: formState.status
              });
              notification({
                text: 'Úspešne uložené.',
                background: 'green-background'
              });
            }}
          >
            Uložiť
          </Button>

          <IconButton
            aria-label="close"
            className="close-button"
            onClick={handleClose}
          >
            <Close />
          </IconButton>

          <Button
            className="more-button"
            onClick={(e) => {
              handleClickOnMore(e);
            }}
          >
            Viac
          </Button>

          <Menu
            anchorEl={anchorEl}
            anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
            transformOrigin={{ vertical: 'top', horizontal: 'center' }}
            getContentAnchorEl={null}
            open={Boolean(anchorEl)}
            onClose={handleCloseMore}
          >
            <MenuItem
              onClick={() => {
                deleteTask();
                handleClose();
              }}
              style={{ fontFamily: 'Poppins', color: '#b32929' }}
            >
              Vymazať
            </MenuItem>
          </Menu>

          <span
            style={{
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'start',
              padding: '10px 0px'
            }}
          >
            <Flag />
            <FormControl>
              <Select
                disableUnderline
                classes={{ root: 'task-select' }}
                MenuProps={menuProps}
                value={formState?.status?.toLowerCase()}
                onChange={(e) =>
                  setFormState({
                    ...formState,
                    status: (e.target.value as string).toUpperCase() as Status
                  })
                }
              >
                <MenuItem value={'done'}>
                  {renderTaskStatusBubble(getTaskStatusData(Status.Done))}
                </MenuItem>
                <MenuItem value={'in_progress'}>
                  {renderTaskStatusBubble(getTaskStatusData(Status.InProgress))}
                </MenuItem>
                <MenuItem value={'not_done'}>
                  {renderTaskStatusBubble(getTaskStatusData(Status.NotDone))}
                </MenuItem>
              </Select>
            </FormControl>
          </span>

          <span
            style={{
              display: 'flex',
              alignItems: 'flex-start',
              justifyContent: 'start',
              padding: '10px 0px'
            }}
          >
            <FormatAlignLeft style={{ marginTop: '0.3em' }} />
            <TextField
              multiline
              rows={2}
              rowsMax={5}
              variant="outlined"
              style={{ width: '66%', marginLeft: '0.9em' }}
              className="task-desc-input"
              value={formState.description}
              onChange={(e) =>
                setFormState({ ...formState, description: e.target.value })
              }
            />
          </span>

          <span
            style={{
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'start',
              padding: '10px 0px'
            }}
          >
            <Today />
            <DatePicker
              autoOk
              disableToolbar
              variant="inline"
              inputVariant="outlined"
              style={{
                width: 90,
                marginLeft: 10,
                background: 'rgba(171, 171, 171, 0.15)',
                borderRadius: '5px'
              }}
              format="D.M.YYYY"
              value={moment(formState.date).format('YYYY-MM-DD')}
              onChange={(date) =>
                setFormState({
                  ...formState,
                  date: date?.toDate().toISOString() ?? ''
                })
              }
            />
          </span>

          <span
            style={{
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'start',
              padding: '10px 0px'
            }}
          >
            <Timer />
            <Autocomplete
              id="time-picker"
              options={timeSlots}
              style={{ width: 90, marginLeft: 10 }}
              disableClearable
              freeSolo
              value={formState.workTime}
              inputValue={formState.workTime}
              onInputChange={(event, newInputValue) => {
                setFormState({ ...formState, workTime: newInputValue });
              }}
              renderInput={(params) => (
                <TextField
                  {...params}
                  variant="outlined"
                  style={{
                    background: 'rgba(171, 171, 171, 0.15)',
                    borderRadius: '5px'
                  }}
                />
              )}
            />
          </span>
        </div>
      </Dialog>
    </MuiPickersUtilsProvider>
  );
}

const renderTaskStatusBubble = ({
  color,
  title
}: {
  color: string;
  title: string;
}) => {
  return (
    <span className={'task-status'} style={{ backgroundColor: color }}>
      {title}
    </span>
  );
};
