import '../../App.css';
import React, { useState, useContext } from 'react';
import { makeStyles, withStyles } from '@material-ui/core/styles';
import moment from 'moment';

import PropTypes from 'prop-types';
import Dialog from '@material-ui/core/Dialog';

import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import { DatePicker, MuiPickersUtilsProvider } from "@material-ui/pickers";
import MomentUtils from '@date-io/moment';
import Autocomplete from '@material-ui/lab/Autocomplete';

import TimerIcon from '@material-ui/icons/Timer';
import IconButton from '@material-ui/core/IconButton';
import CloseIcon from '@material-ui/icons/Close';
import TodayIcon from '@material-ui/icons/Today';

import { DataContext } from "../../providers/DataProvider";
import { UserContext } from "../../providers/UserProvider";

const CssTextField = withStyles({
    root: {
      '& .MuiFilledInput-root': {
        fontFamily: 'Poppins',
        paddingTop: 0,
        background: 'rgba(171, 171, 171, 0.15)'
      },
    },
})(TextField);

const styles = makeStyles((theme) => ({
    detailTitle: {
        fontFamily: 'Poppins',
        color: '#2b2b2b',
        fontWeight: '700',
        fontSize: '1.6em',
        paddingBottom: '15px',
        marginBlockStart: 0,
        marginBlockEnd: 0,
    },
    detailText: {
        fontFamily: 'Poppins',
        color: '#2b2b2b',
        fontWeight: '500',
        fontSize: '1.1em',
        marginBlockStart: 0,
        marginBlockEnd: 0,
        paddingLeft: '20px',
    },
    closeButton: {
        position: 'absolute',
        right: theme.spacing(2),
        top: theme.spacing(3.5),
        color: '#1f1f1d',
    },
    editButton: {
        position: 'absolute',
        right: theme.spacing(8),
        top: theme.spacing(3.5),
        color: '#2B2B28',
    },
    taskSelect: {
        width: '90%',
        background: '#FFEAC8',
        borderStyle:'none',
        borderWidth: 2,
        borderRadius: '10px',
        marginLeft: 18,
        padding: '10px 0px',
        "&:focus":{
            borderRadius: '10px',
            background: 'transparent',
        },
    },
    addTaskInput: {
        fontFamily: 'Poppins',
        margin: '10px',
        "& label": {
            fontFamily: 'Poppins',
        },
        "& input": {
            fontFamily: 'Poppins',
        }
    },
    createTaskButton: {
        width: '60%',
        alignSelf: 'center',
        margin: '20px',
        fontFamily: 'Poppins',
        fontSize: '14px',
        fontWeight: '600',
        color: 'white',
        background: '#E3B04B',
        '&:hover': {
            background: '#ffc44f',
        }
    }
}));

export function WorkAddTaskDialog(props) {

    const { onClose, open } = props;

    const handleClose = () => {
        onClose();
    };

    const [taskTitle, setTaskTitle] = useState("");
    const [taskDesc, setTaskDesc] = useState("");
    const [selectedTime, handleTimeChange] = useState("1h30m");
    const [selectedDate, handleDateChange] = useState(new Date());

    const { setRefresh } = useContext(DataContext);
    const user = useContext(UserContext);

    // moves the menu below the select input
    const menuProps = {
        anchorOrigin: {
        vertical: "bottom",
            horizontal: "left"
        },
        transformOrigin: {
        vertical: "top",
            horizontal: "left"
        },
        getContentAnchorEl: null
    };

    const handleForm = e => {
        e.preventDefault();
        try {
            const requestOptions = {
                method: 'POST',
                headers: { 'Content-Type': 'application/json', 'Accept': '*/*', 'Authorization': 'Bearer ' + localStorage.getItem("@token") },
                body: JSON.stringify({ 
                    token: user.uid,
                    title: taskTitle,
                    desc: taskDesc,
                    date: moment(selectedDate).format("DD/MM/yyyy"),
                    workTime: selectedTime
                    })
            };
            fetch('https://europe-west1-plannerapp-960c1.cloudfunctions.net/app/api/create-work-task', requestOptions)
                .then(response => response.json())
                .then(data => setRefresh(data.taskId));
        } catch(error) {
            console.log(error);
            alert("Something went wrong. Please try again later.")
        }

        setTaskTitle("");
        setTaskDesc("");
        handleTimeChange("1h30m");
        handleDateChange(new Date());
        handleClose();
    };

    return (
        <MuiPickersUtilsProvider utils={MomentUtils}>

            <Dialog
            onClose={handleClose}
            open={open}
            fullWidth={true}
            maxWidth={'xs'}
            PaperProps={{ style: { backgroundColor: '#FFEAC8', color: 'black', borderRadius: '30px', padding: '35px', boxShadow: '0px 0px 15px 0px rgba(0,0,0,0.15)', }, elevation: 0 }}
            BackdropProps={{ style: { backgroundColor: "rgba(0, 0, 0, 0.25)" } }}
            >
                <form onSubmit={e => handleForm(e)} style={{display: 'contents'}}>
                    <h1 className="detail-title">Vytvoriť úlohu</h1>
                    <IconButton aria-label="close" className="close-button" onClick={onClose}>
                        <CloseIcon />
                    </IconButton>

                    <TextField
                        label="Názov"
                        variant="outlined"
                        value={taskTitle}
                        onChange={e => setTaskTitle(e.target.value)}
                        className="add-task-input"
                    />

                    <TextField
                        label="Popis"
                        multiline
                        rows={3}
                        variant="outlined"
                        value={taskDesc}
                        onChange={e => setTaskDesc(e.target.value)}
                        className="add-task-input"
                    />

                    <div style={{alignItems:'center', display: 'flex', flexDirection:'row', justifyContent: 'space-between', marginLeft: '30px', marginRight: '30px'}}>
                        <span style={{ display: 'flex', alignItems: 'center', justifyContent: 'start', padding: '10px 0px' }}>
                            <TodayIcon />
                            <DatePicker
                                autoOk
                                disableToolbar
                                variant="inline"
                                inputVariant="outlined"
                                style={{ width: 90, marginLeft: 15, background: 'rgba(171, 171, 171, 0.15)', borderRadius: '5px', border: 'none' }}
                                format="D.M.YYYY"
                                value={selectedDate}
                                onChange={date => handleDateChange(date)}
                            />
                        </span>

                        <span style={{ display: 'flex', alignItems: 'center', justifyContent: 'start', padding: '10px 0px' }}>
                            <TimerIcon />
                            <Autocomplete
                                id="time-picker"
                                options={timeSlots}
                                style={{ width: 90, marginLeft: 15 }}
                                disableClearable
                                freeSolo
                                value={selectedTime}
                                inputValue={selectedTime}
                                onInputChange={(event, newInputValue) => {
                                    handleTimeChange(newInputValue);
                                }}
                                renderInput={(params) => (
                                    <CssTextField {...params} variant="outlined" style={{background: 'rgba(171, 171, 171, 0.15)', borderRadius: '5px'}} />
                                )}
                            />
                        </span>
                    </div>

                    <Button type="submit" className="create-task-button">
                        Vytvoriť
                    </Button>

                </form>
            </Dialog>

        </MuiPickersUtilsProvider>

    );
}

WorkAddTaskDialog.propTypes = {
    onClose: PropTypes.func.isRequired,
    open: PropTypes.bool.isRequired,
};


const getMinutes = (index) => {
    if (index % 4 === 0) {
        return '00'
    } else if ( index % 2 === 0 ) {
        return '30'
    } else if ( index % 4 === 1 ) {
        return '15'
    } else {
        return '45'
    }
}

const timeSlots = Array.from(new Array(10 * 4)).map(
     (_, index) => index > 0 ? `${Math.floor(index / 4)}h${getMinutes(index)}m` : ''
  );
