import '../../App.css';
import React, { useState } from 'react';
import { makeStyles, withStyles } from '@material-ui/core/styles';

import PropTypes from 'prop-types';
import Dialog from '@material-ui/core/Dialog';

import Select from '@material-ui/core/Select';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import TextField from '@material-ui/core/TextField';
import { TimePicker, MuiPickersUtilsProvider } from "@material-ui/pickers";
import MomentUtils from '@date-io/moment';
import Autocomplete from '@material-ui/lab/Autocomplete';

import FormatAlignLeftIcon from '@material-ui/icons/FormatAlignLeft';
import TimerIcon from '@material-ui/icons/Timer';
import IconButton from '@material-ui/core/IconButton';
import CloseIcon from '@material-ui/icons/Close';
import FlagIcon from '@material-ui/icons/Flag';

const CssTextField = withStyles({
    root: {
      '& .MuiFilledInput-root': {
        fontFamily: 'Poppins',
        paddingTop: 0
      },
    },
})(TextField);

const styles = makeStyles((theme) => ({
    detailTitle: {
        fontFamily: 'Poppins',
        color: '#2b2b2b',
        fontWeight: '700',
        fontSize: '1.6em',
        paddingBottom: '15px',
        marginBlockStart: 0,
        marginBlockEnd: 0,
    },
    detailText: {
        fontFamily: 'Poppins',
        color: '#2b2b2b',
        fontWeight: '500',
        fontSize: '1.1em',
        marginBlockStart: 0,
        marginBlockEnd: 0,
        paddingLeft: '20px',
    },
    closeButton: {
        position: 'absolute',
        right: theme.spacing(2),
        top: theme.spacing(3.5),
        color: '#1f1f1d',
    },
    editButton: {
        position: 'absolute',
        right: theme.spacing(8),
        top: theme.spacing(3.5),
        color: '#2B2B28',
    },
    taskStatus: {
        fontFamily: 'Poppins',
        color: '#2b2b2b',
        fontWeight: '500',
        fontSize: '1.1em',
        padding: '0.4em 0.7em',
        borderRadius: '10px',
    },
    taskSelect: {
        width: '90%',
        background: '#FFEAC8',
        borderStyle:'none',
        borderWidth: 2,
        borderRadius: '10px',
        marginLeft: 18,
        padding: '10px 0px',
        "&:focus":{
            borderRadius: '10px',
            background: 'transparent',
        },
    },
    addTaskInput: {
        margin: '10px',
        "& input::placeholder": {
            fontFamily: 'Poppins',
            color: 'red',
        },
        "& input": {
            fontFamily: 'Poppins',
        }
    }
}));

export function WorkAddTaskDialog(props) {
    const classes = styles();

    const { onClose, open } = props;

    const handleClose = () => {
        onClose();
    };

    const [val,setVal] = useState(null);

    const handleStatusChange = (event) => {
        setVal(event.target.value);
    };

    const [selectedDate, handleDateChange] = useState("2021-01-01T01:00:00.000Z");

    // moves the menu below the select input
    const menuProps = {
        anchorOrigin: {
        vertical: "bottom",
            horizontal: "left"
        },
        transformOrigin: {
        vertical: "top",
            horizontal: "left"
        },
        getContentAnchorEl: null
    };

    return (
        <MuiPickersUtilsProvider utils={MomentUtils}>
            <Dialog
            onClose={handleClose}
            open={open}
            fullWidth={true}
            maxWidth={'xs'}
            PaperProps={{ style: { backgroundColor: '#FFEAC8', color: 'black', borderRadius: '30px', padding: '35px', boxShadow: '0px 0px 15px 0px rgba(0,0,0,0.15)', }, elevation: 0 }}
            BackdropProps={{ style: { backgroundColor: "rgba(0, 0, 0, 0.25)" } }}
            >
                <h1 className={classes.detailTitle}>Vytvoriť úlohu</h1>
                <IconButton aria-label="close" className={classes.closeButton} onClick={onClose}>
                    <CloseIcon />
                </IconButton>

                <TextField
                    id="outlined-multiline-static"
                    label="Názov"
                    variant="outlined"
                    className={classes.addTaskInput}
                />

                <TextField
                    id="outlined-multiline-static"
                    label="Popis"
                    multiline
                    rows={3}
                    variant="outlined"
                    className={classes.addTaskInput}
                />

                <span style={{ display: 'flex', alignItems: 'flex-start', justifyContent: 'start', padding: '10px 0px' }}>
                    <FormatAlignLeftIcon style={{ paddingTop: '2px' }} />
                    <h3 className={classes.detailText}>
                        {}
                    </h3>
                </span>

                <span style={{ display: 'flex', alignItems: 'center', justifyContent: 'start', padding: '10px 0px' }}>
                    <TimerIcon />
                    <Autocomplete
                        id="time-picker"
                        options={timeSlots}
                        style={{ width: 80, marginLeft: 20 }}
                        disableClearable
                        freeSolo
                        renderInput={(params) => (
                            <CssTextField {...params} variant="filled" />
                        )}
                    />
                </span>

            </Dialog>
        </MuiPickersUtilsProvider>

    );
}

WorkAddTaskDialog.propTypes = {
    onClose: PropTypes.func.isRequired,
    open: PropTypes.bool.isRequired,
};


const getMinutes = (index) => {
    if (index % 2 === 0) {
        return '00'
    } else if ( index % 3 === 0 ) {
        return '45'
    } else if ( index % 2 === 2) {
        return '30'
    } else if ( index % 3 === 0 ){
        return '15'
    }
}

const timeSlots = Array.from(new Array(12 * 4)).map(
    (_, index) => `${index < 20 ? '0' : ''}${Math.floor(index / 4)}:${getMinutes(index)}`,
  );
