import { makeStyles } from '@material-ui/core/styles';
import { AnimatePresence, motion } from 'framer-motion';
import { ToastContainer } from 'material-react-toastify';
import 'material-react-toastify/dist/ReactToastify.css';
import 'moment/locale/sk';
import React, { useContext } from 'react';
import { Helmet } from 'react-helmet';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import './App.css';
import { notification } from './components/Notification';
import { SideBar } from './components/SideBar';
import { TopBar } from './components/TopBar';
import './dialog.sass';
import { useNotificationSubscription } from './generated/graphql';
import './index';
import { Tasks } from './pages/Tasks';
import { Teams } from './pages/Teams';
import { TimelineRender } from './pages/Teams/components/Timeline';
import { AuthContext } from './providers/UserProvider';

const pageVariants = {
  initial: {
    opacity: 0,
    duration: 0.1
  },
  in: {
    opacity: 1,
    duration: 0.1
  },
  out: {
    opacity: 0,
    duration: 0.1
  }
};

export const useStyles = makeStyles((theme) => ({
  main: {
    height: '-webkit-fill-available',
    scrollBehavior: 'smooth'
  },
  input: {
    marginLeft: theme.spacing(1),
    flex: 1,
    color: '#f1d6ab',
    fontFamily: 'Sen',
    fontSize: 18
  },
  taskListWindow: {
    justifyContent: 'center',
    margin: '5px 30px 20px 30px',
    display: 'flex',
    width: '95%',
    minHeight: '8em',
    background: '#fff',
    borderRadius: '20px',
    padding: '20px 0px',
    '&:focus': {
      outline: '0px'
    }
  },
  taskBubble: {
    display: 'flex',
    borderRadius: '15px',
    padding: '1.1em 1.6em',
    marginBottom: '1em',
    fontFamily: 'Poppins',
    backgroundColor: '#fff',
    color: '#2b2b28',
    width: '95%',
    alignItems: 'center',
    cursor: 'pointer',
    position: 'relative',
    right: '12px',
    transition: 'background-color 1s'
  },
  taskTypeTitle: {
    fontFamily: 'Poppins',
    fontSize: '1.3em',
    fontWeight: 600
  },
  bubbleTitle: {
    color: '#2b2b2b',
    fontWeight: 600,
    fontSize: '0.9em',
    marginBlockStart: 0,
    marginBlockEnd: 0,
    textTransform: 'capitalize'
  },
  bubbleDesc: {
    fontWeight: 600,
    fontSize: 15,
    marginBlockStart: 0,
    marginBlockEnd: 0
  },
  bubbleWorkTime: {
    fontWeight: 600,
    fontSize: '0.8em',
    marginBlockStart: 0,
    marginBlockEnd: 0,
    paddingTop: '2px'
  },
  taskSelect: {
    fontFamily: 'Poppins'
  },
  root: {
    width: '100%',
    height: '100%',
    backgroundColor: '#F1F1F1',
    display: 'flex',
    flexDirection: 'row'
  },
  headings: {
    alignItems: 'center',
    display: 'flex',
    flexDirection: 'column',
    width: '100%',
    paddingLeft: '10px'
  },
  titles: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'space-around',
    flexDirection: 'row',
    width: '95%',
    height: '3em'
  }
}));

export function App() {
  const classes = useStyles();

  const { currentUser: user } = useContext(AuthContext);

  const { loading } = useNotificationSubscription({
    variables: { userId: user.uid }
  });

  if (!loading)
    notification({
      text: 'Pozvanie na team meeting',
      background: 'black-background'
    });

  return (
    <Router>
      <div className={classes.root}>
        <SideBar />
        <Route
          render={({ location }) => (
            <AnimatePresence exitBeforeEnter initial={false}>
              <Switch location={location} key={location.pathname}>
                <Route exact path="/">
                  <motion.div
                    initial="initial"
                    animate="in"
                    exit="out"
                    variants={pageVariants}
                    style={{
                      alignItems: 'center',
                      display: 'flex',
                      flexDirection: 'column',
                      width: '100%',
                      paddingLeft: '10px',
                      overflowX: 'hidden'
                    }}
                  >
                    <Helmet>
                      <title>Consult - Week</title>
                    </Helmet>

                    <TopBar />

                    <span className={classes.titles}>
                      <h2 className={classes.taskTypeTitle}>Meetingy</h2>
                      <h2 className={classes.taskTypeTitle}>Práca</h2>
                      <h2 className={classes.taskTypeTitle}>Komunikácia</h2>
                    </span>

                    <Tasks />
                  </motion.div>
                </Route>
                <Route path="/dashboard">
                  <TimelineRender
                    userIds={['HgjmgWoZxraQlnl6GT49dpXtjs12']}
                    dateFrom={new Date('2021-06-11T00:00:00Z')}
                    dateTo={new Date('2021-06-11T23:59:59Z')}
                  />
                </Route>
                <Route path="/teams">
                  <motion.div
                    initial="initial"
                    animate="in"
                    exit="out"
                    variants={pageVariants}
                    style={{ width: '100%', height: '100%', display: 'flex' }}
                  >
                    <Teams />
                  </motion.div>
                </Route>
              </Switch>
            </AnimatePresence>
          )}
        />
      </div>
      <ToastContainer
        position="bottom-right"
        autoClose={5000}
        pauseOnFocusLoss
        newestOnTop={false}
        closeOnClick
        rtl={false}
        draggable
        pauseOnHover
      />
    </Router>
  );
}

export default App;
