import { useStyles } from '../App';
import '../App.css';
import React, { useContext } from 'react';
import { GetTaskStatusData } from './TaskStatusData';
import { WorkDetailDialog } from '../dialogWindows/WorkDetailDialog';
import Box from '@material-ui/core/Box';
import moment from 'moment';
import 'moment/locale/sk';

import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";
import TimerIcon from '@material-ui/icons/Timer';

import { DataContext } from "../providers/DataProvider";


export function WorkTaskBubble(date) {
  const classes = useStyles();

  const { taskData } = useContext(DataContext);

  let filteredData = [];

  if (taskData.workTasks !== undefined) {
    filteredData = taskData.workTasks.filter((task) => task.date.includes(moment(date).format("DD/MM/yyyy"))); //filter tasks by specified type
  }

  const [open, setOpen] = React.useState(false);
  const [selectedTask, setSelectedTask] = React.useState({ id: '-1', title: 'Test', desc: 'Test', workTime: '100h', status: 'not_done' });

  const handleClickOpen = (task) => {
    setSelectedTask(task);
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  }

  const move = (source, destination, droppableSource, droppableDestination) => {
    const sourceClone = Array.from(source);
    const destClone = Array.from(destination);
    const [removed] = sourceClone.splice(droppableSource.index, 1);

    destClone.splice(droppableDestination.index, 0, removed);

    const result = {};
    result[droppableSource.droppableId] = sourceClone;
    result[droppableDestination.droppableId] = destClone;

    return result;
  };

  const onDragEnd = (result) => {
    const { source, destination } = result;

    // dropped outside the list
    if (!destination) {
        return;
    }

    if (source.droppableId === destination.droppableId) {
        const items = reorder(
            this.getList(source.droppableId),
            source.index,
            destination.index
        );

        let state = { items };

        if (source.droppableId === 'droppable2') {
            state = { selected: items };
        }

        this.setState(state);
    } else {
        const result = move(
            this.getList(source.droppableId),
            this.getList(destination.droppableId),
            source,
            destination
        );

        this.setState({
            items: result.droppable,
            selected: result.droppable2
        });
    }
  };

  if (filteredData === undefined || filteredData.length === 0) {
    return (
      <div style={{ justifyContent: "center", height: "100%" }}>
        <h3 style={{ textAlign: "center", paddingTop: "18px", color: "#bdbdbd", fontFamily: "Poppins", fontSize: "18px", fontWeight: "500" }}>
          Prázdne
        </h3>
      </div>
    )

  } else {
    return (
      <div>
        <DragDropContext onDragEnd={onDragEnd()}>
          <Droppable droppableId={`${moment(date).unix()}`}>
            {(provided) => (
              <div {...provided.droppableProps} ref={provided.innerRef}>
                {filteredData.map((item, index) => (
                  <Draggable key={item.id} draggableId={item.id} index={index} >
                    {(provided) => (
                      <div {...provided.draggableProps} {...provided.dragHandleProps}>
                        <Box className={classes.taskBubble} style={{ backgroundColor: GetTaskStatusData(item.status).color, transition: 'background-color 1s' }} onClick={() => handleClickOpen(item)} ref={provided.innerRef}>
                          <Box flexGrow={1}>
                            <h3 className={classes.bubbleTitle}>{item.title}</h3>
                          </Box>
                          <TimerIcon style={{ marginRight: '5px' }} />
                          <h3 className={classes.bubbleWorkTime}>{item.workTime}</h3>
                        </Box>
                      </div>
                    )}
                  </Draggable>
                ))}
                {provided.placeholder}
              </div>
            )}
          </Droppable>
        </DragDropContext>
        <WorkDetailDialog onClose={handleClose} open={open} selectedTask={selectedTask} />
      </div>
    )
  }
};
