import Box from '@material-ui/core/Box';
import React from 'react';
import { useStyles } from '../../App';
import {
  CommunicationTask,
  MeetingTask,
  User,
  WorkTask
} from '../../generated/graphql';
import { CommTaskList } from './TaskList/CommTaskList';
import { MeetingTaskList } from './TaskList/MeetingTaskList';
import { WorkTaskList } from './TaskList/WorkTaskList';

export type PickCommunicationTask = Pick<
  CommunicationTask,
  | 'id'
  | 'pending'
  | 'date'
  | 'description'
  | 'title'
  | 'status'
  | 'workTime'
  | 'email'
  | 'phoneNumber'
  | 'index'
> & { user: Pick<User, 'id'> };

export type PickWorkTask = Pick<
  WorkTask,
  | 'id'
  | 'pending'
  | 'date'
  | 'description'
  | 'title'
  | 'status'
  | 'workTime'
  | 'index'
> & { user: Pick<User, 'id'> };

export type PickMeetingTask = Pick<
  MeetingTask,
  | 'id'
  | 'pending'
  | 'date'
  | 'description'
  | 'title'
  | 'status'
  | 'endTime'
  | 'startTime'
  | 'address'
  | 'category'
  | 'index'
> & { user: Pick<User, 'id'> };

interface InputProps {
  date: number;
  commData: PickCommunicationTask[] | undefined;
  meetingData: PickMeetingTask[] | undefined;
  workData: PickWorkTask[] | undefined;
}

export function RenderTaskList({
  date,
  meetingData,
  workData,
  commData
}: InputProps) {
  const classes = useStyles();

  return (
    <Box className={classes.taskListWindow} tabIndex={0} id={date.toString()}>
      <MeetingTaskList date={date} meetingData={meetingData ?? []} />
      <WorkTaskList date={date} workData={workData ?? []} />
      <CommTaskList date={date} commData={commData ?? []} />
    </Box>
  );
}
