import { useApolloClient } from '@apollo/client';
import MomentUtils from '@date-io/moment';
import { Avatar, Paper, Tooltip, Typography } from '@material-ui/core';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import IconButton from '@material-ui/core/IconButton';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import { CheckCircle, Warning } from '@material-ui/icons';
import CloseIcon from '@material-ui/icons/Close';
import GroupIcon from '@material-ui/icons/Group';
import { MuiPickersUtilsProvider } from '@material-ui/pickers';
import { Field, Formik, FormikState } from 'formik';
import { AnimatePresence, motion } from 'framer-motion';
import React, { useContext, useState } from 'react';
import { v4 as uuidv4 } from 'uuid';
import * as yup from 'yup';
import '../../../App.css';
import { User } from '../../../generated/graphql';
import { GET_USER_BY_EMAIL } from '../../../graphql/graphql';
import { AuthContext } from '../../../providers/UserProvider';

const useStyles = makeStyles(() => ({
  addUsersField: {
    margin: '1em',
    borderRadius: '5px',
    background: 'rgba(171, 171, 171, 0.15)',
    marginBlockStart: 0,
    marginBlockEnd: 0,
    '& label': {
      fontFamily: 'Poppins'
    },
    '& .MuiOutlinedInput-input': {
      fontFamily: 'Poppins'
    }
  },

  addUserButton: {
    backgroundColor: '#2b2b28',
    padding: '0.9em',
    fontFamily: 'Poppins',
    fontWeight: 400,
    fontSize: '1em',
    color: 'white',
    borderRadius: '5px',
    '&:hover': {
      color: '#2b2b28'
    },
    marginLeft: '10px'
  },

  createTeamBtn: {
    marginTop: '1em',
    alignSelf: 'center',
    width: '20em',
    fontFamily: 'Poppins',
    fontSize: '15px',
    padding: '1em 0.8em',
    fontWeight: 500,
    color: 'white',
    backgroundColor: '#E3B04B',
    boxShadow: 'none',
    borderRadius: '10px',
    textTransform: 'none',
    '&:hover': {
      backgroundColor: '#ffc44f'
    }
  },

  chipsWindow: {
    listStyle: 'none',
    padding: '0em',
    margin: 0,
    boxShadow: 'none',
    height: '16em',
    overflowY: 'auto',
    width: '100%'
  },
  inputText: {
    backgroundColor: 'rgba(171, 171, 171, 0.15)'
  },
  errorText: {
    color: 'red',
    fontWeight: 600,
    position: 'absolute',
    top: '4.5em'
  }
}));

interface InputProps {
  onClose: () => void;
  open: boolean;
  members: Pick<User, 'id' | 'name' | 'email'>[];
}

type ResetForm = (
  nextState?:
    | Partial<
        FormikState<{
          email: string;
        }>
      >
    | undefined
) => void;

const validationSchema = yup.object({
  name: yup.string().min(1, 'Názov tímu nesmie byť prázdny !'),
  email: yup
    .string()
    .min(1, 'Email nesmie byť prázdny !')
    .email('Neplatná emailová adresa !')
});

interface EmailChip {
  email: string;
  name?: string;
  id?: string;
  isValid: boolean;
}

export function AddMember({ onClose, open, members }: InputProps) {
  const classes = useStyles();

  const { currentUser } = useContext(AuthContext);

  const client = useApolloClient();

  const handleClose = () => {
    onClose();
    setUserList([]);
  };

  const [userList, setUserList] = useState<EmailChip[]>([]);

  const handleUserAdd = async ({
    isValid,
    email,
    resetForm
  }: {
    isValid: boolean;
    resetForm: ResetForm;
    email: string;
  }) => {
    if (isValid) {
      const { data } = await client.query({
        query: GET_USER_BY_EMAIL,
        variables: {
          email
        }
      });

      const updatedUser =
        email === data?.userByEmail?.email
          ? {
              email,
              name: data.userByEmail.name,
              id: data.userByEmail.id,
              isValid: true
            }
          : { email, isValid: false };

      setUserList([...userList, updatedUser]);

      resetForm({ values: { email: '' } });
    }
  };

  const handleUserDelete = (userToDelete: string) => () => {
    setUserList((users) => users.filter(({ email }) => email !== userToDelete));
  };

  function validateEmailExists(value: string) {
    if (
      userList.some(({ email }) => email.toLowerCase() === value.toLowerCase())
    ) {
      return 'Duplicitný email !';
    }
    if (currentUser.email.toLowerCase() === value.toLowerCase()) {
      return 'Nemôžeš pridať sám seba !';
    }
    if (
      members.some(({ email }) => email.toLowerCase() === value.toLowerCase())
    ) {
      return 'Email už v tíme existuje !';
    }
  }

  const variantsEmail = {
    open: {
      x: ['-100%', '0%'],
      transition: {
        duration: 0.15
      }
    },
    close: {
      x: ['0%', '-100%'],
      transition: {
        duration: 0.15
      }
    }
  };

  return (
    <MuiPickersUtilsProvider utils={MomentUtils}>
      <AnimatePresence>
        <Dialog
          onClose={handleClose}
          open={open}
          fullWidth={true}
          maxWidth={'xs'}
          PaperProps={{
            style: {
              backgroundColor: '#fff',
              color: 'black',
              borderRadius: '30px',
              padding: '35px',
              boxShadow: '0px 0px 15px 0px rgba(0,0,0,0.15)',
              width: '100%'
            },
            elevation: 0
          }}
          BackdropProps={{ style: { backgroundColor: 'rgba(0, 0, 0, 0.25)' } }}
        >
          <Formik
            initialValues={{
              email: ''
            }}
            onSubmit={(values) => {
              const id = uuidv4();
              const memberIds = userList
                .filter((user) => user.isValid === true)
                .map(({ id }) => id ?? '');

              const members = userList
                .filter((user) => user.isValid === true)
                .map(({ id, name, email }) => ({
                  id: id ?? '',
                  name: name ?? '',
                  email
                }));

              const owner = {
                email: currentUser.email,
                name: currentUser.displayName,
                id: currentUser.uid
              };

              handleClose();
            }}
            validationSchema={validationSchema}
            isInitialValid={false}
          >
            {(props) => {
              const {
                values,
                touched,
                errors,
                handleChange,
                handleSubmit,
                isValid,
                resetForm
              } = props;
              return (
                <form onSubmit={handleSubmit} style={{ width: '100%' }}>
                  <h1 className="detail-title">Pridať člena</h1>
                  <IconButton
                    aria-label="close"
                    className="close-button"
                    onClick={handleClose}
                  >
                    <CloseIcon />
                  </IconButton>
                  <span
                    style={{
                      boxSizing: 'border-box',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'space-evenly',
                      width: '100%',
                      padding: '10px'
                    }}
                  >
                    <GroupIcon />

                    <Field
                      component={TextField}
                      fullWidth
                      id="email"
                      name="email"
                      label="Email člena"
                      variant="outlined"
                      value={values.email}
                      onChange={handleChange}
                      validate={validateEmailExists}
                      error={touched.email && Boolean(errors.email)}
                      helperText={errors.email}
                      className="add-task-input"
                      InputProps={{
                        className: classes.inputText
                      }}
                      FormHelperTextProps={{ className: classes.errorText }}
                    />

                    <Button
                      className={classes.addUserButton}
                      onClick={() => {
                        if (values.email.length === 0) return;
                        return handleUserAdd({
                          resetForm,
                          isValid,
                          email: values.email
                        });
                      }}
                    >
                      Pridať
                    </Button>
                  </span>
                  <h3
                    style={{
                      fontFamily: 'Poppins',
                      marginTop: '1em',
                      marginBottom: '0.5em'
                    }}
                  >
                    Emaily
                  </h3>
                  <AnimatePresence>
                    <Paper component="ul" className={classes.chipsWindow}>
                      {userList.map((emailChip) => {
                        return (
                          <motion.div
                            key={emailChip.email}
                            variants={variantsEmail}
                            animate={variantsEmail.open}
                            exit={variantsEmail.close}
                          >
                            <li
                              style={{
                                boxSizing: 'border-box',
                                width: '100%',
                                height: '4em',
                                display: 'flex',
                                alignItems: 'center',
                                paddingLeft: '3em'
                              }}
                            >
                              {emailChip.isValid === true && (
                                <>
                                  <Avatar
                                    alt={emailChip.name}
                                    src="/static/images/avatar/1.jpg"
                                  />
                                  <div
                                    style={{
                                      height: '100%',
                                      display: 'flex',
                                      flexDirection: 'column',
                                      justifyContent: 'center',
                                      marginLeft: '1em'
                                    }}
                                  >
                                    <Typography
                                      variant="body1"
                                      style={{
                                        fontFamily: 'Poppins',
                                        fontWeight: 600
                                      }}
                                    >
                                      {emailChip.name}
                                    </Typography>
                                    <Typography
                                      variant="body2"
                                      style={{
                                        fontFamily: 'Poppins',
                                        fontSize: '0.8em'
                                      }}
                                    >
                                      {emailChip.email}
                                    </Typography>
                                  </div>
                                </>
                              )}
                              {emailChip.isValid === false && (
                                <Typography
                                  variant="body1"
                                  style={{ fontFamily: 'Poppins' }}
                                >
                                  {emailChip.email}
                                </Typography>
                              )}
                              <div
                                style={{
                                  marginLeft: 'auto',
                                  display: 'flex',
                                  alignItems: 'center'
                                }}
                              >
                                {emailChip.isValid === true && <CheckCircle />}
                                {emailChip.isValid === false && (
                                  <Tooltip
                                    title="Daný email neexistuje v našej databáze"
                                    placement="top"
                                  >
                                    <Warning />
                                  </Tooltip>
                                )}
                                <IconButton
                                  aria-label="deleteEmail"
                                  style={{
                                    color: '#2B2B28',
                                    marginRight: 0
                                  }}
                                  onClick={handleUserDelete(emailChip.email)}
                                >
                                  <CloseIcon />
                                </IconButton>
                              </div>
                            </li>
                          </motion.div>
                        );
                      })}
                    </Paper>
                  </AnimatePresence>
                  <span
                    style={{
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      width: '100%'
                    }}
                  >
                    <Button
                      type="submit"
                      className={classes.createTeamBtn}
                      disabled={userList.length === 0}
                    >
                      Odoslať
                    </Button>
                  </span>
                </form>
              );
            }}
          </Formik>
        </Dialog>
      </AnimatePresence>
    </MuiPickersUtilsProvider>
  );
}
