import '../App.css';
import React, { useState, useContext } from 'react';
import { styles } from './css/dialog_css';
import { GetTaskStatusData } from '../taskBubble/TaskStatusData';
import moment from 'moment';

import PropTypes from 'prop-types';
import Dialog from '@material-ui/core/Dialog';

import Button from '@material-ui/core/Button'
import Select from '@material-ui/core/Select';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import TextField from '@material-ui/core/TextField';
import { DatePicker, MuiPickersUtilsProvider } from "@material-ui/pickers";
import MomentUtils from '@date-io/moment';

import { 
    FormatAlignLeftIcon,
    IconButton,
    DeleteIcon,
    CreateIcon,
    CloseIcon,
    FlagIcon,
    TimerIcon,
    TodayIcon
} from '../imports/icons';

import { DataContext } from "../providers/DataProvider";
import { UserContext } from "../providers/UserProvider";


const renderTaskStatusBubble = (taskStatusData) => {
    const classes = styles();
    return (
        <span className={classes.taskStatus} style={{backgroundColor: taskStatusData.color}}>
            {taskStatusData.title}
        </span>
    )
}

export function WorkDetailDialog(props) {
    const classes = styles();

    const { onClose, selectedTask, open } = props;
    const { setRefresh } = useContext(DataContext);
    const user = useContext(UserContext);

    const handleClose = () => {
        enterEditMode(false);
        onClose(selectedTask);
    };

    const taskStatusData = GetTaskStatusData(selectedTask.status);

    const [editMode, enterEditMode] = useState(false);

    const [val,setVal] = useState(taskStatusData.id);

    const handleStatusChange = (event) => {
        try {
            const requestOptions = {
                method: 'POST',
                headers: { 'Content-Type': 'application/json', 'Accept': '*/*', 'Authorization': 'Bearer ' + localStorage.getItem("@token") },
                body: JSON.stringify({ 
                    collection: "work_tasks",
                    token: user.uid,
                    taskId: selectedTask.id,
                    status: event.target.value,
                    })
            };
            fetch('https://europe-west1-plannerapp-960c1.cloudfunctions.net/app/api/update-task-status', requestOptions)
                .then(response => response.json())
                .then(data => setRefresh(data.taskId));
        } catch(error) {
            console.log(error);
            alert("Something went wrong. Please try again later.")
        }
        setVal(event.target.value);
        selectedTask.status = event.target.value;
    };

    const [titleState, setTitle] = useState('');
    const [descState, setDesc] = useState('');
    const [workTimeState, setWorkTime] = useState('');
    const [selectedDate, handleDateChange] = useState(new Date());

    const editModeOn = () => {
        enterEditMode(true);
        setTitle(selectedTask.title);
        setDesc(selectedTask.desc);
        setWorkTime(selectedTask.workTime);
        handleDateChange(moment(selectedTask.date, "DD/MM/yyy"));
    }

    const editModeOff = () => {
        enterEditMode(false);
        setTitle('');
        setDesc('');
        setWorkTime('');
    }

    // moves the menu below the select input
    const menuProps = {
        anchorOrigin: {
        vertical: "bottom",
            horizontal: "left"
        },
        transformOrigin: {
        vertical: "top",
            horizontal: "left"
        },
        getContentAnchorEl: null
    };

    async function updateTask() {
        try {
            const requestOptions = {
                method: 'POST',
                headers: { 'Content-Type': 'application/json', 'Accept': '*/*', 'Authorization': 'Bearer ' + localStorage.getItem("@token") },
                body: JSON.stringify({ 
                    token: user.uid,
                    taskId: selectedTask.id,
                    title: selectedTask.title,
                    desc: selectedTask.desc,
                    date: moment(selectedDate).format("DD/MM/yyy"),
                    status: selectedTask.status,
                    workTime: selectedTask.workTime,
                })
            };
            await fetch('https://europe-west1-plannerapp-960c1.cloudfunctions.net/app/api/update-work-task', requestOptions)
            .then(response => response);
        } catch(error) {
            console.log(error);
            alert("Something went wrong. Please try again later.")
        }

        setRefresh(selectedTask.id);
        editModeOff();
    };

    async function deleteTask() {
        try {
            const requestOptions = {
                method: 'POST',
                headers: { 'Content-Type': 'application/json', 'Accept': '*/*', 'Authorization': 'Bearer ' + localStorage.getItem("@token") },
                body: JSON.stringify({ taskId: selectedTask.id, token: user.uid })
            };
            await fetch('https://europe-west1-plannerapp-960c1.cloudfunctions.net/app/api/delete-work-task', requestOptions)
            .then(response => response);
        } catch(error) {
            console.log(error);
            alert("Something went wrong. Please try again later.")
        }

        setRefresh(selectedTask.id);
        handleClose();
    };

    return (
        <MuiPickersUtilsProvider utils={MomentUtils}>
        <Dialog
            onClose={handleClose}
            open={open}
            fullWidth={true}
            maxWidth={'xs'}
            PaperProps={{ style: { backgroundColor: '#FFEAC8', color: 'black', borderRadius: '30px', padding: '35px', boxShadow: '0px 0px 15px 0px rgba(0,0,0,0.15)', }, elevation: 0 }}
            BackdropProps={{ style: { backgroundColor: "rgba(0, 0, 0, 0.25)" } }}
        >
            { editMode
                ? <TextField style={{width: "70%"}} className={classes.taskTitleInput} value={selectedTask.title}/> 
                : <h1 className={classes.detailTitle}>{selectedTask.title}</h1>
            }
            { editMode 
                ?   <IconButton aria-label="edit" className={classes.editButton} onClick={() => {deleteTask()}}>
                        <DeleteIcon style={{color: '#c45a5a'}}/>
                    </IconButton>
                :   <IconButton aria-label="edit" className={classes.editButton} onClick={() => {editModeOn()}}>
                        <CreateIcon />
                    </IconButton>
            }

            <IconButton aria-label="close" className={classes.closeButton} onClick={handleClose}>
                <CloseIcon />
            </IconButton>

            <span style={{ display: 'flex', alignItems: 'center', justifyContent: 'start', padding: '10px 0px' }}>
                <FlagIcon style={{ paddingTop: '2px' }} />
                <FormControl>
                    <Select
                        disableUnderline
                        classes={{ root: classes.taskSelect }}
                        MenuProps={menuProps}
                        value={taskStatusData.id}
                        onChange={handleStatusChange}
                    >
                        <MenuItem value={"done"}>{renderTaskStatusBubble(GetTaskStatusData('done'))}</MenuItem>
                        <MenuItem value={"in_progress"}>{renderTaskStatusBubble(GetTaskStatusData('in_progress'))}</MenuItem>
                        <MenuItem value={"not_done"}>{renderTaskStatusBubble(GetTaskStatusData('not_done'))}</MenuItem>
                    </Select>
                </FormControl>
            </span>

            <span style={{ display: 'flex', alignItems: 'flex-start', justifyContent: 'start', padding: '10px 0px' }}>
                <FormatAlignLeftIcon style={{ paddingTop: '2px' }} />
                { editMode 
                    ?   <TextField multiline style={{width: "70%"}} className={classes.taskDescInput} value={descState} onChange={e => setDesc(e.target.value)}/> 
                    :   <h3 className={classes.detailText}>
                            {selectedTask.desc}
                        </h3>
                }

            </span>

            <span style={{ display: 'flex', alignItems: 'flex-start', justifyContent: 'start', padding: '10px 0px' }}>
                <TimerIcon />
                <h3 className={classes.detailText}>
                    {selectedTask.workTime}
                </h3>
            </span>

            { editMode 
                ?   <span style={{ display: 'flex', alignItems: 'center', justifyContent: 'start', padding: '10px 0px' }}>
                        <TodayIcon />
                        <DatePicker
                            autoOk
                            disableToolbar
                            variant="inline"
                            inputVariant="outlined"
                            style={{ width: 90, marginLeft: 10, background: 'rgba(171, 171, 171, 0.15)', borderRadius: '5px', border: 'none' }}
                            format="D.M.YYYY"
                            value={selectedDate}
                            onChange={date => handleDateChange(date)}
                        />
                    </span>
                :   ""
            }

            { editMode
                ?   <Button className={classes.updateTaskButton} onClick={() => {editModeOff();updateTask()}}>
                        Aktualizova≈•
                    </Button>
                :   ""
            }

        </Dialog>
        </MuiPickersUtilsProvider>
    );
}

WorkDetailDialog.propTypes = {
    onClose: PropTypes.func.isRequired,
    open: PropTypes.bool.isRequired,
    selectedTask: PropTypes.object.isRequired,
};
