import '../../App.css';
import React, { useState, useContext } from 'react';
import { styles } from '../css/dialog.sass';
import { GetTaskStatusData } from '../../taskBubble/TaskStatusData';
import moment from 'moment';

import PropTypes from 'prop-types';

import Button from '@material-ui/core/Button';
import Select from '@material-ui/core/Select';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import TextField from '@material-ui/core/TextField';
import { DatePicker } from "@material-ui/pickers";
import GooglePlacesAutocomplete from 'react-google-places-autocomplete';

import { 
    FormatAlignLeftIcon,
    IconButton,
    CloseIcon,
    FlagIcon,
    PlaceIcon,
    ScheduleIcon,
    TimerIcon,
    DeleteIcon,
    TodayIcon
} from '../../imports/icons';

import { DataContext } from "../../providers/DataProvider";
import { UserContext } from "../../providers/UserProvider";
import { renderTaskStatusBubble, menuProps } from '../../const/dialogConsts';

export function MeetEditDialog(props) {

    const classes = styles();

    const { onClose, selectedTask, enterEditMode } = props;
    const { setRefresh } = useContext(DataContext);
    const user = useContext(UserContext);

    const handleClose = () => {
        enterEditMode(false);
        onClose();
    };

    const taskStatusData = GetTaskStatusData(selectedTask.status);

    const handleStatusChange = (event) => {
        try {
            const requestOptions = {
                method: 'POST',
                headers: { 'Content-Type': 'application/json', 'Accept': '*/*', 'Authorization': 'Bearer ' + localStorage.getItem("@token") },
                body: JSON.stringify({ 
                    token: user.uid,
                    collection: "meeting_tasks",
                    taskId: selectedTask.id,
                    status: event.target.value,
                    })
            };
            fetch('https://europe-west1-plannerapp-960c1.cloudfunctions.net/app/api/update-task-status', requestOptions)
                .then(response => response.json())
                .then(data => setRefresh(data.taskId));
        } catch(error) {
            console.log(error);
            alert("Something went wrong. Please try again later.")
        }
        selectedTask.status = event.target.value;
    };

    const [titleState, setTitle] = useState('');
    const [descState, setDesc] = useState('');
    const [workTimeState, setWorkTime] = useState('');
    const [selectedDate, handleDateChange] = useState(new Date());
    const [taskAddress, setAddress] = useState("");

    var workTime = moment.duration(moment(selectedTask.endTime, "HH:mm").diff(moment(selectedTask.startTime, "HH:mm")));

    const editModeOff = () => {
        enterEditMode(false);
        setTitle('');
        setDesc('');
        setWorkTime('');
    }

    async function updateTask() {
        try {
            const requestOptions = {
                method: 'POST',
                headers: { 'Content-Type': 'application/json', 'Accept': '*/*', 'Authorization': 'Bearer ' + localStorage.getItem("@token") },
                body: JSON.stringify({ 
                    token: user.uid,
                    taskId: selectedTask.id,
                    title: selectedTask.title,
                    desc: selectedTask.desc,
                    date: moment(selectedDate).format("DD/MM/yyy"),
                    status: selectedTask.status,
                    address: selectedTask.address,
                    startTime: selectedTask.startTime,
                    endTime: selectedTask.endTime,
                    category: "",
                })
            };
            await fetch('https://europe-west1-plannerapp-960c1.cloudfunctions.net/app/api/update-meeting-task', requestOptions)
            .then(response => response);
        } catch(error) {
            console.log(error);
            alert("Something went wrong. Please try again later.")
        }

        setRefresh(selectedTask.id);
        editModeOff();
    };

    async function deleteTask() {
        try {
            const requestOptions = {
                method: 'POST',
                headers: { 'Content-Type': 'application/json', 'Accept': '*/*', 'Authorization': 'Bearer ' + localStorage.getItem("@token") },
                body: JSON.stringify({ taskId: selectedTask.id, token: user.uid })
            };
            await fetch('https://europe-west1-plannerapp-960c1.cloudfunctions.net/app/api/delete-meeting-task', requestOptions)
            .then(response => response);
        } catch(error) {
            console.log(error);
            alert("Something went wrong. Please try again later.")
        }

        setRefresh(selectedTask.id);
        handleClose();
    };

    return (
        <div>
            <TextField style={{width: "70%"}} className={classes.taskTitleInput} value={selectedTask.title}/>

            <IconButton aria-label="edit" className={classes.editButton} onClick={() => {deleteTask()}}>
                <DeleteIcon style={{color: '#c45a5a'}}/>
            </IconButton>

            <IconButton aria-label="close" className={classes.closeButton} onClick={handleClose}>
                <CloseIcon />
            </IconButton>

            <span style={{ display: 'flex', alignItems: 'center', justifyContent: 'start', padding: '10px 0px' }}>
                <FlagIcon style={{ paddingTop: '2px' }} />
                <FormControl>
                    <Select
                        disableUnderline
                        classes={{ root: classes.taskSelect }}
                        MenuProps={menuProps}
                        value={taskStatusData.id}
                        onChange={handleStatusChange}
                    >
                        <MenuItem value={"done"}>{renderTaskStatusBubble(GetTaskStatusData('done'))}</MenuItem>
                        <MenuItem value={"in_progress"}>{renderTaskStatusBubble(GetTaskStatusData('in_progress'))}</MenuItem>
                        <MenuItem value={"not_done"}>{renderTaskStatusBubble(GetTaskStatusData('not_done'))}</MenuItem>
                    </Select>
                </FormControl>
            </span>

            <span style={{ display: 'flex', alignItems: 'flex-start', justifyContent: 'start', padding: '10px 0px' }}>
                <FormatAlignLeftIcon style={{ paddingTop: '2px' }} />
                <TextField multiline style={{width: "70%"}} className={classes.taskDescInput} value={descState} onChange={e => setDesc(e.target.value)}/>
            </span>

            <span style={{ display: 'flex', alignItems: 'flex-start', justifyContent: 'start', padding: '10px 0px' }}>
                <PlaceIcon />
                <GooglePlacesAutocomplete
                    selectProps={{
                        styles: {
                            control: (provider, state) => ({
                                ...provider,
                                background: 'none',
                                fontFamily: 'Poppins',
                                width: '97.8%',
                                height: '3.8em',
                                borderColor: 'rgba(0, 0, 0, 0.23)',
                                "&:hover": {
                                    borderColor: state.isFocused ? "-webkit-focus-ring-color" : "black"
                                }
                            }),
                            container: (provided) => ({
                                ...provided,
                                width: '100%',
                                marginLeft: '10px',
                            }),
                            input: (provided) => ({
                                ...provided,
                                fontFamily: 'Poppins',
                                color: '#2b2b2b',
                            }),
                            option: (provided) => ({
                                ...provided,
                                fontFamily: 'Poppins',
                                color: '#2b2b2b',
                            }),
                            menu: (provided) => ({
                                ...provided,
                                width: '97.8%',
                            }),
                            singleValue: (provided) => ({
                                ...provided,
                                fontFamily: 'Poppins',
                                color: '#2b2b2b',
                            }),
                            dropdownIndicator: (provider) => ({
                                ...provider,
                                color: 'rgba(0, 0, 0, 0.23)',
                            }),
                            indicatorSeparator: (provider) => ({
                                ...provider,
                                color: 'rgba(0, 0, 0, 0.23)',
                            }),
                        },
                        taskAddress,
                        onChange: setAddress,
                    }}
                />
            </span>

            <span style={{ display: 'flex', alignItems: 'flex-start', justifyContent: 'start', padding: '10px 0px' }}>
                <ScheduleIcon />
                <h3 className={classes.detailText}>
                    {selectedTask.startTime} - {selectedTask.endTime}
                </h3>
            </span>

            <span style={{ display: 'flex', alignItems: 'flex-start', justifyContent: 'start', padding: '10px 0px' }}>
                <TimerIcon />
                <h3 className={classes.detailText}>
                    {parseInt(workTime.asHours())}h{parseInt(workTime.asMinutes())%60}m
                </h3>
            </span>

            <span style={{ display: 'flex', alignItems: 'center', justifyContent: 'start', padding: '10px 0px' }}>
                <TodayIcon />
                <DatePicker
                    autoOk
                    disableToolbar
                    variant="inline"
                    inputVariant="outlined"
                    style={{ width: 90, marginLeft: 10, background: 'rgba(171, 171, 171, 0.15)', borderRadius: '5px', border: 'none' }}
                    format="D.M.YYYY"
                    value={selectedDate}
                    onChange={date => handleDateChange(date)}
                />
            </span>

            <Button className={classes.updateTaskButton} onClick={() => {editModeOff();updateTask()}}>
                Aktualizova≈•
            </Button>
        </div>
    );
}

MeetEditDialog.propTypes = {
    onClose: PropTypes.func.isRequired,
    open: PropTypes.bool.isRequired,
    selectedTask: PropTypes.object.isRequired,
    editMode: PropTypes.bool.isRequired,
    enterEditMode: PropTypes.func.isRequired
};
