import React from 'react';
import { makeStyles } from '@material-ui/core/styles';

import Button from '@material-ui/core/Button';
import Box from '@material-ui/core/Box';
import Avatar from '@material-ui/core/Avatar';
import NotificationsIcon from '@material-ui/icons/Notifications';
import Badge from '@material-ui/core/Badge';


const useStyles = makeStyles((theme) => ({
  root: {
    '& > *': {
      margin: theme.spacing(1),
    },
  },
  iconButton: {
    color: 'rgba(43, 43, 40, 0.7)',
    cursor: 'pointer',
  },
  button: {
    margin: theme.spacing(1),
    padding: 10,
    width: '100%',
    borderRadius: 30,
    fontFamily: 'Sen',
    fontSize: 20,
    fontWeight: 'bold',
    textTransform: 'none',
  },
  sideBar: {
    alignItems: 'center',
    justifyContent: 'center',
    verticalAlign: 'top',
    width: '15%',
    height: '-webkit-fill-available',
    backgroundColor: '#fefefe',
    borderRadius: '0 30px 0 0',
    boxShadow: '5px 0px 20px 0px rgba(0,0,0,0.1)',
    textAlign: 'center',
  },
}));


export function SideBar() {
    const classes = useStyles();
    const [invisible, setInvisible] = React.useState(false);

    const handleBadgeVisibility = () => {
      setInvisible(!invisible);
    };

    return (

      <Box className={classes.sideBar}>
        <Avatar alt="John Doe" src="/static/images/avatar/1.jpg" style={{alignSelf: 'center', margin: '15px auto', width: '50px', height: '50px'}} />
        <h3 style={{margin:'20px', fontFamily:'Poppins', fontSize:'16px', fontWeight:'500', color:'#4d4d47'}}>John Doe</h3>
        <Badge color="secondary" variant="dot" color="primary" className={classes.iconButton} onClick={handleBadgeVisibility} invisible={invisible} aria-label="notification">
          <NotificationsIcon style={{ fontSize: '30px' }}/>
        </Badge>
        <Box paddingLeft={2} paddingRight={5} marginTop={5}>
          <Button className={classes.button}>
            Hlavný plán
          </Button>
          <Button className={classes.button}>
            Denný plán
          </Button>
        </Box>
      </Box>
    );
  }
