import React, { useState } from 'react';
import './App.css';

import { ProductionTasksList } from './sections/ProductionTasksList';
import { WorkTasksList } from './sections/WorkTasksList';
import { CommTasksList } from './sections/CommTasksList';
import { TaskList } from './sections/TaskList';
import { getDatesFromMonth } from './DateCalc';
import { SideBar } from './sideBar/SideBar';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Box from '@material-ui/core/Box';
import moment from 'moment';
import 'moment/locale/sk';
import { RenderTaskList } from './sections/RenderTaskList';



export const useStyles = makeStyles((theme) => ({
  main: {
    backgroundColor: '#f8f8f8',
    height: '-webkit-fill-available',
  },
  sideBar: {
    alignItems: 'center',
    justifyContent: 'center',
    verticalAlign: 'top',
    width: '15%',
    height: '-webkit-fill-available',
    backgroundColor: '#fefefe',
    borderRadius: '0 30px 0 0',
    boxShadow: '5px 0px 20px 0px rgba(0,0,0,0.1)',
  },
  root: {
    padding: '2px 10px',
    display: 'flex',
    alignItems: 'center',
    width: 300,
    height: '-webkit-fill-available',
    borderColor: '#f1d6ab',
    borderRadius: 50,
    background: '#00000040',
  },
  input: {
    marginLeft: theme.spacing(1),
    flex: 1,
    color: '#f1d6ab',
    fontFamily: 'Sen',
    fontSize: 18,
  },
  iconButton: {
    color: '#f1d6ab',
  },
  button: {
    margin: theme.spacing(1),
    padding: 10,
    width: '100%',
    borderRadius: 30,
    fontFamily: 'Sen',
    fontSize: 20,
    fontWeight: 'bold',
    textTransform: 'none',
  },
  taskListWindow: {
    justifyContent: 'center',
    margin: '40px 50px 0px 50px',
    display: 'flex',
    width: '95%',
    minHeight: '8em',
    background: '#FAFAFA',
    borderRadius: '30px',
    padding: '20px 0px',
    boxShadow: '0px 0px 15px 0px rgba(0,0,0,0.1)',
  },
  taskBubble: {
    display: 'flex',
    borderRadius: 20,
    padding: 20,
    margin: '20px 0px',
    fontFamily:'Poppins',
    backgroundColor: '#fff',
    color: '#2b2b28',
    width: '95%',
    alignItems: 'center',
    cursor: 'pointer',
    boxShadow: '0px 0px 15px 0px rgba(0,0,0,0.1)',
    position: 'relative',
    right: '12px',
  },
  bubbleTitle: {
    color: '#2b2b2b',
    fontWeight: '600',
    fontSize: '1.2em',
    marginBlockStart: 0,
    marginBlockEnd: 0,
    textTransform: 'capitalize',
  },
  bubbleDesc: {
    fontWeight: '600',
    fontSize: 15,
    marginBlockStart: 0,
    marginBlockEnd: 0,
  },
  bubbleWorkTime: {
    fontWeight: '600',
    fontSize: '1em',
    marginBlockStart: 0,
    marginBlockEnd: 0,
    paddingTop: '2px',
  },
}));

function DateText(day) {
  const number = moment(day).format('D');
  const dayText = moment(day).format('dddd');

  return (
    <div style={{alignItems:'center', display: 'flex', flexDirection:'column', width:'4.5em'}}>
      <h3 style={{fontWeight:'800', fontSize:'1.6em', color:'#2b2b2b'}}>
        {number}.
      </h3>

      <h3 style={{fontWeight:'600', fontSize:'1.2em', color:'#6c6c6c', textTransform:'capitalize'}}>
        {dayText}
      </h3>
    </div>
  )
}

function App() {
  const classes = useStyles();
  moment().locale('sk');

  let weeks = getDatesFromMonth("1");
  const [weekState, setWeek] = useState(0);

  console.log(weeks[0]);

  return (
    <div style={{ width: '100%', height: '100%', backgroundColor: '#f8f8f8', display:'flex', flexDirection:'row',}} >

      <SideBar />
      <div style={{alignItems:'center', display: 'flex', flexDirection:'column', width:'100%'}}>
        <div style={{alignItems:'center', display: 'flex', flexDirection:'row'}}>
          <Button className={classes.button} onClick={() => setWeek(0)}>
            1.
          </Button>

          <Button className={classes.button} onClick={() => setWeek(1)}>
            2.
          </Button>

          <Button className={classes.button} onClick={() => setWeek(2)}>
            3.
          </Button>

          <Button className={classes.button} onClick={() => setWeek(3)}>
            4.
          </Button>

          <Button className={classes.button} onClick={() => setWeek(4)}>
            5.
          </Button>
        </div>

        <div style={{width: '100%', flexDirection:'column', overflowY: 'scroll'}}>
          <div style={{alignItems:'center', display: 'flex', flexDirection:'column', paddingLeft: '20px'}}>
            {weeks[weekState].map(({day, hello}) => (
              <div style={{alignItems:'center', display: 'flex', flexDirection:'row', width: '100%'}}>
                {console.log(day)}
                {DateText(hello)}
                <RenderTaskList date={day}/>
              </div>
            ))}
          </div>
        </div>

      </div>

    </div>
  );
}

export default App;
