import {
    FormControl,
    Grid,
    IconButton,
    MenuItem,
    Select,
    Tab,
    Tabs
  } from '@material-ui/core';
import Dialog from '@material-ui/core/Dialog';
import React, { useContext, useEffect, useState } from 'react';
import TimerIcon from '@material-ui/icons/Timer';
import { useStyles } from '../App';


interface InputProps {
  open: boolean;
  onClose: () => void;
  selectedUser: string;
}

const StyledTabs = withStyles({
  indicator: {
    display: 'flex',
    justifyContent: 'center',
    backgroundColor: 'transparent',
    '& > span': {
      maxWidth: 40,
      width: '100%',
      backgroundColor: '#635ee7',
    },
  },
})((props: StyledTabsProps) => <Tabs {...props} TabIndicatorProps={{ children: <span /> }} />);

interface StyledTabProps {
  label: string;
}

const StyledTab = withStyles((theme: Theme) =>
  createStyles({
    root: {
      textTransform: 'none',
      color: '#fff',
      fontWeight: theme.typography.fontWeightRegular,
      fontSize: theme.typography.pxToRem(15),
      marginRight: theme.spacing(1),
      '&:focus': {
        opacity: 1,
      },
    },
  }),
)((props: StyledTabProps) => <Tab disableRipple {...props} />);

export function CompactPlannerView({
  onClose,
  open,
  selectedUser
}: InputProps) {

  const handleClose = () => {
    onClose();
  };

  const [value, setValue] = React.useState(0);

  const handleChange = (event: React.ChangeEvent<{}>, newValue: number) => {
    setValue(newValue);
  };

  const classes = useStyles();

  return (
      <Dialog
        onClose={handleClose}
        open={open}
        fullWidth={true}
        maxWidth={'sm'}
        PaperProps={{
          style: {
            height: '80%',
            backgroundColor: '#fff',
            color: 'black',
            borderRadius: '30px',
            padding: '35px',
            boxShadow: '0px 0px 15px 0px rgba(0,0,0,0.15)',
            transition: 'all 1s'
          },
          elevation: 0
        }}
        BackdropProps={{ style: { backgroundColor: 'rgba(0, 0, 0, 0.25)' } }}
      >

        <div style={{display:'flex', justifyContent:'center', alignItems:'center', fontStyle:'Poppins', fontSize:'1.5em', fontWeight:700}}>
          {selectedUser}'s tasks
        </div>

        <StyledTabs
        value={value}
        onChange={handleChange}
        indicatorColor='primary'
        textColor="primary"
        centered
        style={{margin: '1.5em'}}
        >
          <StyledTab label="Meetingy" />
          <StyledTab label="Práca" />
          <StyledTab label="Komunikácia" />
        </StyledTabs>

        <div
            className={classes.taskBubble}
            style={{
                backgroundColor: '#DFFFEC',
                display: 'flex',
                alignSelf: 'center',
                flexDirection: 'column',
                minHeight: '1.5em',
                margin: '0px',
                width: '80%'
            }}
        >
            <Grid container>
                <Grid
                container
                item
                xs={8}
                md={8}
                lg={8}
                justify="flex-start"
                >
                <div>
                    <h3 className={classes.bubbleTitle}>New task</h3>
                </div>
                </Grid>

                <Grid
                container
                item
                xs={4}
                md={4}
                lg={4}
                justify="flex-end"
                alignItems="center"
                >
                <div
                    style={{ display: 'flex', alignItems: 'center' }}
                >
                    <TimerIcon style={{ marginRight: '5px' }} />
                    <h3 className={classes.bubbleWorkTime}>
                        2h30m
                    </h3>
                </div>
                </Grid>
            </Grid>
        </div>
      </Dialog>
  );
}
