import '../App.css';
import React, { useState, useContext } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { GetTaskStatusData } from '../taskBubble/TaskStatusData';

import PropTypes from 'prop-types';
import Dialog from '@material-ui/core/Dialog';

import Button from '@material-ui/core/Button';
import Select from '@material-ui/core/Select';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import TextField from '@material-ui/core/TextField';
import { DatePicker, MuiPickersUtilsProvider } from "@material-ui/pickers";
import MomentUtils from '@date-io/moment';

import FormatAlignLeftIcon from '@material-ui/icons/FormatAlignLeft';
import TimerIcon from '@material-ui/icons/Timer';
import IconButton from '@material-ui/core/IconButton';
import CloseIcon from '@material-ui/icons/Close';
import CreateIcon from '@material-ui/icons/Create';
import FlagIcon from '@material-ui/icons/Flag';
import DoneIcon from '@material-ui/icons/Done';
import TodayIcon from '@material-ui/icons/Today';
import PhoneIcon from '@material-ui/icons/Phone';
import EmailIcon from '@material-ui/icons/Email';

import { DataContext } from "../providers/DataProvider";

const styles = makeStyles((theme) => ({
    detailTitle: {
        fontFamily: 'Poppins',
        color: '#2b2b2b',
        fontWeight: '700',
        fontSize: '1.6em',
        paddingBottom: '15px',
        marginBlockStart: 0,
        marginBlockEnd: 0,
    },
    detailText: {
        fontFamily: 'Poppins',
        color: '#2b2b2b',
        fontWeight: '500',
        fontSize: '1.1em',
        marginBlockStart: 0,
        marginBlockEnd: 0,
        paddingLeft: '20px',
    },
    closeButton: {
        position: 'absolute',
        right: theme.spacing(2),
        top: theme.spacing(3.5),
        color: '#1f1f1d',
    },
    editButton: {
        position: 'absolute',
        right: theme.spacing(8),
        top: theme.spacing(3.5),
        color: '#2B2B28',
    },
    taskStatus: {
        fontFamily: 'Poppins',
        color: '#2b2b2b',
        fontWeight: '500',
        fontSize: '1.1em',
        padding: '0.4em 0.7em',
        borderRadius: '10px',
    },
    taskSelect: {
        width: '90%',
        background: '#FFEAC8',
        borderStyle:'none',
        borderWidth: 2,
        borderRadius: '10px',
        marginLeft: 18,
        padding: '10px 0px',
        "&:focus":{
            borderRadius: '10px',
            background: 'transparent',
        },
    },
    taskTitleInput: {
        paddingBottom: '10px',
        marginBlockStart: 0,
        marginBlockEnd: 0,
        "& input": {
            fontFamily: 'Poppins',
            color: '#2b2b2b',
            fontWeight: '700',
            fontSize: '1.6em',
        }
    },
    taskDescInput: {
        paddingLeft: '20px',
        marginBlockStart: 0,
        marginBlockEnd: 0,
        "& textarea": {
            fontFamily: 'Poppins',
            color: '#2b2b2b',
            fontWeight: '500',
            fontSize: '1.1em',
        }
    },
    deleteTaskButton: {
        width: '60%',
        alignSelf: 'center',
        margin: '20px 20px 0 20px',
        fontFamily: 'Poppins',
        fontSize: '14px',
        fontWeight: '600',
        color: 'white',
        background: '#c45a5a',
        '&:hover': {
            background: '#d96262',
        }
    },
}));

const renderTaskStatusBubble = (taskStatusData) => {
    const classes = styles();
    return (
        <span className={classes.taskStatus} style={{backgroundColor: taskStatusData.color}}>
            {taskStatusData.title}
        </span>
    )
}

export function CommDetailDialog(props) {
    const classes = styles();

    const { onClose, selectedTask, open } = props;
    const { setRefresh } = useContext(DataContext);

    const handleClose = () => {
        enterEditMode(false);
        onClose(selectedTask);
    };

    const taskStatusData = GetTaskStatusData(selectedTask.status);

    const [editMode, enterEditMode] = useState(false);

    const [val,setVal] = useState(taskStatusData.id);

    const handleStatusChange = (event) => {
        try {
            const requestOptions = {
                method: 'POST',
                headers: { 'Content-Type': 'application/json', 'Accept': '*/*' },
                body: JSON.stringify({ 
                    collection: "communication_tasks",
                    taskId: selectedTask.id,
                    status: event.target.value,
                    })
            };
            fetch('https://europe-west1-plannerapp-960c1.cloudfunctions.net/app/api/update-task-status', requestOptions)
                .then(response => response.json())
                .then(data => setRefresh(data.taskId));
        } catch(error) {
            console.log(error);
            alert("Something went wrong. Please try again later.")
        }
        setVal(event.target.value);
        selectedTask.status = event.target.value;
    };

    const [titleState, setTitle] = useState('');
    const [descState, setDesc] = useState('');
    const [workTimeState, setWorkTime] = useState('');
    const [selectedDate, handleDateChange] = useState(new Date());

    const editModeOn = () => {
        enterEditMode(true);
        setTitle(selectedTask.title);
        setDesc(selectedTask.desc);
        setWorkTime(selectedTask.workTime);
    }

    const editModeOff = () => {
        enterEditMode(false);
        setTitle('');
        setDesc('');
        setWorkTime('');
    }

    // moves the menu below the select input
    const menuProps = {
        anchorOrigin: {
        vertical: "bottom",
            horizontal: "left"
        },
        transformOrigin: {
        vertical: "top",
            horizontal: "left"
        },
        getContentAnchorEl: null
    };

    const deleteTask = () => {
        try {
            const requestOptions = {
                method: 'POST',
                headers: { 'Content-Type': 'application/json', 'Accept': '*/*' },
                body: JSON.stringify({ taskId: selectedTask.id })
            };
            fetch('https://europe-west1-plannerapp-960c1.cloudfunctions.net/app/api/delete-comm-task', requestOptions)
            .then(response => response);
        } catch(error) {
            console.log(error);
            alert("Something went wrong. Please try again later.")
        }

        setRefresh(selectedTask.id);
        onClose();
    };

    return (
        <MuiPickersUtilsProvider utils={MomentUtils}>
        <Dialog
            onClose={handleClose}
            open={open}
            fullWidth={true}
            maxWidth={'xs'}
            PaperProps={{ style: { backgroundColor: '#FFEAC8', color: 'black', borderRadius: '30px', padding: '35px', boxShadow: '0px 0px 15px 0px rgba(0,0,0,0.15)', }, elevation: 0 }}
            BackdropProps={{ style: { backgroundColor: "rgba(0, 0, 0, 0.25)" } }}
        >
            { editMode
                ? <TextField style={{width: "70%"}} className={classes.taskTitleInput} value={selectedTask.title}/> 
                : <h1 className={classes.detailTitle}>{selectedTask.title}</h1>
            }
            <IconButton aria-label="edit" className={classes.editButton} onClick={() => {editMode ? editModeOff() : editModeOn()}}>
                { editMode 
                    ?   <DoneIcon style={{fontWeight:'700'}}/>
                    :   <CreateIcon />
                }

            </IconButton>
            <IconButton aria-label="close" className={classes.closeButton} onClick={handleClose}>
                <CloseIcon />
            </IconButton>

            <span style={{ display: 'flex', alignItems: 'center', justifyContent: 'start', padding: '10px 0px' }}>
                <FlagIcon style={{ paddingTop: '2px' }} />
                <FormControl>
                    <Select
                        disableUnderline
                        classes={{ root: classes.taskSelect }}
                        MenuProps={menuProps}
                        value={taskStatusData.id}
                        onChange={handleStatusChange}
                    >
                        <MenuItem value={"done"}>{renderTaskStatusBubble(GetTaskStatusData('done'))}</MenuItem>
                        <MenuItem value={"in_progress"}>{renderTaskStatusBubble(GetTaskStatusData('in_progress'))}</MenuItem>
                        <MenuItem value={"not_done"}>{renderTaskStatusBubble(GetTaskStatusData('not_done'))}</MenuItem>
                    </Select>
                </FormControl>
            </span>

            <span style={{ display: 'flex', alignItems: 'flex-start', justifyContent: 'start', padding: '10px 0px' }}>
                <FormatAlignLeftIcon style={{ paddingTop: '2px' }} />
                { editMode 
                    ?   <TextField multiline style={{width: "70%"}} className={classes.taskDescInput} value={descState} onChange={e => setDesc(e.target.value)}/> 
                    :   <h3 className={classes.detailText}>
                            {selectedTask.desc}
                        </h3>
                } 
            </span>

            <div style={{ display: 'flex', flexDirection: 'row', justifyContent: 'space-between', marginRight: '15%'}}>
                <span style={{ display: 'flex', alignItems: 'flex-start', justifyContent: 'start', padding: '10px 0px' }}>
                    <PhoneIcon />
                    <h3 className={classes.detailText}>
                        {selectedTask.contact.phoneNumber}
                    </h3>
                </span>

                <span style={{ display: 'flex', alignItems: 'flex-start', justifyContent: 'start', padding: '10px 0px' }}>
                    <EmailIcon />
                    <h3 className={classes.detailText}>
                        {selectedTask.contact.email}
                    </h3>
                </span>
            </div>

            <span style={{ display: 'flex', alignItems: 'flex-start', justifyContent: 'start', padding: '10px 0px' }}>
                <TimerIcon />
                <h3 className={classes.detailText}>
                    {selectedTask.workTime}
                </h3>
            </span>

            { editMode 
                ?   <span style={{ display: 'flex', alignItems: 'center', justifyContent: 'start', padding: '10px 0px' }}>
                        <TodayIcon />
                        <DatePicker
                            autoOk
                            disableToolbar
                            variant="inline"
                            inputVariant="outlined"
                            style={{ width: 90, marginLeft: 10, background: 'rgba(171, 171, 171, 0.15)', borderRadius: '5px', border: 'none' }}
                            format="D.M.YYYY"
                            value={selectedDate}
                            onChange={date => handleDateChange(date)}
                        />
                    </span>
                :   ""
            }

            { editMode
                ?   <Button className={classes.deleteTaskButton} onClick={() => deleteTask()}>
                        Vymazať
                    </Button>
                :   ""
            }

        </Dialog>
        </MuiPickersUtilsProvider>
    );
}

CommDetailDialog.propTypes = {
    onClose: PropTypes.func.isRequired,
    open: PropTypes.bool.isRequired,
    selectedTask: PropTypes.object.isRequired,
};
