import MomentUtils from '@date-io/moment';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import IconButton from '@material-ui/core/IconButton';
import { withStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import CloseIcon from '@material-ui/icons/Close';
import EmailIcon from '@material-ui/icons/Email';
import PhoneIcon from '@material-ui/icons/Phone';
import ScheduleIcon from '@material-ui/icons/Timer';
import TodayIcon from '@material-ui/icons/Today';
import { Alert } from '@material-ui/lab';
import Autocomplete from '@material-ui/lab/Autocomplete';
import { DatePicker, MuiPickersUtilsProvider } from '@material-ui/pickers';
import gql from 'graphql-tag';
import moment from 'moment';
import React, { useContext, useEffect, useState } from 'react';
import { v4 as uuidv4 } from 'uuid';
import '../../../App.css';
import {
  Status,
  useAddCommunicationTaskMutation
} from '../../../generated/graphql';
import { AuthContext } from '../../../providers/UserProvider';
import { startTimeSlots } from '../helpers';

const CssTextField = withStyles({
  root: {
    '& .MuiFilledInput-root': {
      fontFamily: 'Poppins',
      paddingTop: 0,
      background: 'rgba(171, 171, 171, 0.15)'
    }
  }
})(TextField);

interface InputProps {
  onClose: () => void;
  open: boolean;
}

export function AddCommTask({ onClose, open }: InputProps) {
  const { currentUser: user } = useContext(AuthContext);

  const handleClose = () => {
    onClose();
  };

  const [error, setError] = useState('');

  const [formState, setFormState] = useState({
    id: uuidv4(),
    userId: user.uid,
    date: new Date().toISOString(),
    description: '',
    title: '',
    pending: false,
    status: Status.NotDone,
    workTime: '0h30m',
    email: '',
    phoneNumber: ''
  });

  useEffect(() => {
    if (user.uid !== formState.userId)
      setFormState({ ...formState, userId: user.uid });

    return;
  }, [setFormState, formState, user.uid]);

  const [addCommTask] = useAddCommunicationTaskMutation();

  const handleForm = (e) => {
    e.preventDefault();

    if (formState.title === '') return setError('Title must be filled in!');
    if (formState.description === '')
      return setError('Description must be filled in!');

    const epoch = new Date().getTime();

    addCommTask({
      variables: {
        ...formState,
        index: parseInt(`1100${epoch}`)
      },
      optimisticResponse: {
        createTask: {
          __typename: 'CommunicationTask',
          index: parseInt(`1100${epoch}`),
          date: formState.date,
          description: formState.description,
          id: formState.id,
          pending: formState.pending,
          status: formState.status,
          title: formState.title,
          user: {
            id: formState.userId
          },
          workTime: formState.workTime,
          email: formState.email,
          phoneNumber: formState.phoneNumber
        }
      },
      update: (store, { data }) => {
        try {
          if (data == null) return;

          store.modify({
            id: 'ROOT_QUERY',
            fields: {
              communicationTaskMany(
                existingCommunicationTasksRef,
                { readField }
              ) {
                const newTaskRef = store.writeFragment({
                  data: data.createTask,
                  fragment: gql`
                    fragment NewCommTask on CommunicationTask {
                      id
                      user {
                        id
                      }
                      date
                      description
                      title
                      status
                      pending
                      workTime
                      email
                      phoneNumber
                    }
                  `
                });

                return {
                  items: [...existingCommunicationTasksRef.items, newTaskRef]
                };
              }
            }
          });
        } catch (e) {
          console.error(e);
        }
      }
    });

    setFormState({
      id: uuidv4(),
      userId: '1a5s89dwd5as654nmji6d',
      date: new Date().toISOString(),
      description: '',
      title: '',
      pending: false,
      status: Status.NotDone,
      workTime: '0h30m',
      email: '',
      phoneNumber: ''
    });

    handleClose();
  };

  return (
    <MuiPickersUtilsProvider utils={MomentUtils}>
      <Dialog
        onClose={handleClose}
        open={open}
        fullWidth={true}
        maxWidth={'xs'}
        PaperProps={{
          style: {
            backgroundColor: '#fff',
            color: 'black',
            borderRadius: '30px',
            padding: '35px',
            boxShadow: '0px 0px 15px 0px rgba(0,0,0,0.15)'
          },
          elevation: 0
        }}
        BackdropProps={{ style: { backgroundColor: 'rgba(0, 0, 0, 0.25)' } }}
      >
        <form onSubmit={(e) => handleForm(e)} style={{ display: 'contents' }}>
          <h1 className="detail-title">Vytvoriť úlohu</h1>

          <IconButton
            aria-label="close"
            className="close-button"
            onClick={onClose}
          >
            <CloseIcon />
          </IconButton>

          <TextField
            label="Názov"
            variant="outlined"
            value={formState.title}
            onChange={(e) =>
              setFormState({ ...formState, title: e.target.value })
            }
            className="add-task-input"
          />

          <TextField
            label="Popis"
            multiline
            rows={3}
            variant="outlined"
            value={formState.description}
            onChange={(e) =>
              setFormState({ ...formState, description: e.target.value })
            }
            className="add-task-input"
          />

          <div
            style={{
              alignItems: 'center',
              display: 'flex',
              flexDirection: 'row',
              justifyContent: 'space-between',
              paddingLeft: '10px'
            }}
          >
            <span
              style={{
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'start',
                padding: '10px 0px'
              }}
            >
              <PhoneIcon />
              <TextField
                label="Tel. číslo"
                variant="outlined"
                value={formState.phoneNumber}
                onChange={(e) =>
                  setFormState({ ...formState, phoneNumber: e.target.value })
                }
                className="add-task-input"
              />
            </span>

            <span
              style={{
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'start',
                padding: '10px 0px'
              }}
            >
              <EmailIcon />
              <TextField
                label="Email"
                variant="outlined"
                value={formState.email}
                onChange={(e) =>
                  setFormState({ ...formState, email: e.target.value })
                }
                className="add-task-input"
              />
            </span>
          </div>

          <div
            style={{
              alignItems: 'center',
              display: 'flex',
              flexDirection: 'row',
              marginLeft: '10px'
            }}
          >
            <span
              style={{
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'start',
                padding: '10px 0px'
              }}
            >
              <TodayIcon />
              <DatePicker
                autoOk
                disableToolbar
                variant="inline"
                inputVariant="outlined"
                style={{
                  width: 90,
                  marginLeft: 10,
                  background: 'rgba(171, 171, 171, 0.15)',
                  borderRadius: '5px',
                  border: 'none'
                }}
                format="D.M.YYYY"
                value={moment(formState.date).format('YYYY-MM-DD')}
                onChange={(date) =>
                  setFormState({
                    ...formState,
                    date: date?.toDate().toISOString() ?? ''
                  })
                }
              />
            </span>

            <span
              style={{
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'start',
                padding: '10px 0px',
                marginLeft: '20px'
              }}
            >
              <ScheduleIcon />
              <Autocomplete
                id="time-picker"
                options={startTimeSlots}
                style={{ width: 90, marginLeft: 10 }}
                disableClearable
                freeSolo
                value={formState.workTime}
                inputValue={formState.workTime}
                onInputChange={(event, newInputValue) => {
                  setFormState({ ...formState, workTime: newInputValue });
                }}
                renderInput={(params) => (
                  <CssTextField
                    {...params}
                    variant="outlined"
                    style={{
                      background: 'rgba(171, 171, 171, 0.15)',
                      borderRadius: '5px'
                    }}
                  />
                )}
              />
            </span>
          </div>

          <Button type="submit" className="create-task-button">
            Vytvoriť
          </Button>
        </form>
        {error === '' ? (
          ''
        ) : (
          <Alert severity="error" onClose={() => setError('')}>
            {error}
          </Alert>
        )}
      </Dialog>
    </MuiPickersUtilsProvider>
  );
}
