import React, { useState, useEffect } from 'react';
import './App.css';

import { getDatesFromMonth, getISOWeekDates } from './DateCalc';
import { SideBar } from './sideBar/SideBar';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import ToggleButton from '@material-ui/lab/ToggleButton';
import ToggleButtonGroup from '@material-ui/lab/ToggleButtonGroup';
import Select from '@material-ui/core/Select';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import moment from 'moment';
import 'moment/locale/sk';
import { RenderTaskList } from './sections/RenderTaskList';
import Tooltip from '@material-ui/core/Tooltip';

import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';
import ChevronRightIcon from '@material-ui/icons/ChevronRight';


export const useStyles = makeStyles((theme) => ({
  main: {
    backgroundColor: '#f8f8f8',
    height: '-webkit-fill-available',
    scrollBehavior: 'smooth',
  },
  root: {
    padding: '2px 10px',
    display: 'flex',
    alignItems: 'center',
    width: 300,
    height: '-webkit-fill-available',
    borderColor: '#f1d6ab',
    borderRadius: 50,
    background: '#00000040',
  },
  input: {
    marginLeft: theme.spacing(1),
    flex: 1,
    color: '#f1d6ab',
    fontFamily: 'Sen',
    fontSize: 18,
  },
  taskListWindow: {
    justifyContent: 'center',
    margin: '20px 30px 20px 30px',
    display: 'flex',
    width: '95%',
    minHeight: '8em',
    background: '#FAFAFA',
    borderRadius: '20px',
    padding: '20px 0px',
    boxShadow: '0px 0px 15px 0px rgba(0,0,0,0.1)',
    "&:focus":{
      outline: '0px',
    },
  },
  taskBubble: {
    display: 'flex',
    borderRadius: 20,
    padding: '18px 20px',
    marginBottom: '20px',
    fontFamily:'Poppins',
    backgroundColor: '#fff',
    color: '#2b2b28',
    width: '95%',
    alignItems: 'center',
    cursor: 'pointer',
    boxShadow: '0px 0px 15px 0px rgba(0,0,0,0.1)',
    position: 'relative',
    right: '12px',
  },
  bubbleTitle: {
    color: '#2b2b2b',
    fontWeight: '600',
    fontSize: '1em',
    marginBlockStart: 0,
    marginBlockEnd: 0,
    textTransform: 'capitalize',
  },
  bubbleDesc: {
    fontWeight: '600',
    fontSize: 15,
    marginBlockStart: 0,
    marginBlockEnd: 0,
  },
  bubbleWorkTime: {
    fontWeight: '600',
    fontSize: '0.8em',
    marginBlockStart: 0,
    marginBlockEnd: 0,
    paddingTop: '2px',
  },
  chevronButtons: {
    cursor: 'pointer',
    fontSize: '2em',
    fontWeight: '500',
    color: 'rgba(43,43,43,0.7)',
  },
  taskSelect: {
    fontFamily: 'Poppins',
  },
  monthTitle: {
    fontFamily: 'Poppins',
    color: '#2b2b2b',
    fontWeight: '500',
    fontSize: '1.4em',
    marginBlockStart: 0,
    marginBlockEnd: 0,
    textTransform: 'capitalize',
    padding: '0px 30px'
  },
  taskTypeTitle: {
    fontFamily: 'Poppins',
    fontSize: '1.3em',
    fontWeight: '600',
  },
}));

function DateText(day) {
  const number = moment(day).format('D');
  const dayText = moment(day).format('dddd');

  if(moment(day).dayOfYear() === moment().dayOfYear()) {
    return (
      <div style={{alignItems:'center', display: 'flex', flexDirection:'column', width:'3em', backgroundColor:'rgba(241, 214, 171, 0.5)', padding:'20px', borderRadius:'20px'}}>
        <h3 style={{fontWeight:'800', fontSize:'1.4em', color:'#2b2b2b'}}>
          {number}.
        </h3>

        <h3 style={{fontWeight:'600', fontSize:'1.1em', color:'#6c6c6c', textTransform:'capitalize'}}>
          {dayText}
        </h3>
      </div>
    )
  } else {
    return (
      <div style={{alignItems:'center', display: 'flex', flexDirection:'column', width:'3em', padding:'20px'}}>
        <h3 style={{fontFamily:'Poppins', fontWeight:'800', fontSize:'1.4em', color:'#2b2b2b'}}>
          {number}.
        </h3>

        <h3 style={{fontFamily:'Poppins', fontWeight:'600', fontSize:'1.1em', color:'#6c6c6c', textTransform:'capitalize'}}>
          {dayText}
        </h3>
      </div>
    )
  }
}


function App() {
  const classes = useStyles();

  const [selectedMonth,setMonth] = useState(moment().format('M'));

  const handleMonthChange = (event) => {
    setMonth(event.target.value);
    week = getDatesFromMonth(selectedMonth);
    setWeek(0);
  };

  const [weekState, setWeek] = useState(moment().week());
  let week = getISOWeekDates(weekState)[0];
  let month = getISOWeekDates(weekState)[1];

  // moves the menu below the select input
  const menuProps = {
      anchorOrigin: {
      vertical: "bottom",
          horizontal: "left"
      },
      transformOrigin: {
      vertical: "top",
          horizontal: "left"
      },
      getContentAnchorEl: null
  };

  const findCurrDate = () => {
    setMonth(moment().format('M'));
    setWeek(moment().week());

    setTimeout(() => {  
      let currDate = moment().startOf('day').valueOf();
      document.getElementById(currDate).scrollIntoView({
        block: 'center',
        behavior: 'smooth'
      });
    }, 1000);
  };


  return (
    <div style={{ width: '100%', height: '100%', backgroundColor: '#f8f8f8', display:'flex', flexDirection:'row',}} >

      <SideBar />

      <div style={{alignItems:'center', display: 'flex', flexDirection:'column', width:'100%', paddingLeft:'10px'}}>

        <div style={{alignItems:'center', display: 'flex', flexDirection:'row', margin:'1em 0px', alignContent:'center', width:'95%', justifyContent:'space-between'}}>
          {/* <FormControl>
            <Select
                disableUnderline
                classes={{ root: classes.taskSelect }}
                MenuProps={menuProps}
                value={selectedMonth}
                onChange={handleMonthChange}
            >
                <MenuItem value={"1"}>January</MenuItem>
                <MenuItem value={"2"}>February</MenuItem>
                <MenuItem value={"3"}>March</MenuItem>
                <MenuItem value={"4"}>April</MenuItem>
                <MenuItem value={"5"}>May</MenuItem>
                <MenuItem value={"6"}>June</MenuItem>
                <MenuItem value={"7"}>July</MenuItem>
                <MenuItem value={"8"}>August</MenuItem>
                <MenuItem value={"9"}>September</MenuItem>
                <MenuItem value={"10"}>October</MenuItem>
                <MenuItem value={"11"}>November</MenuItem>
                <MenuItem value={"12"}>December</MenuItem>
            </Select>
          </FormControl> */}
          <div style={{display: 'flex', flexDirection:'row', alignItems:'center'}}>
            <div style={{display:'flex', alignItems:'center'}}>
              <Tooltip title='Previous week'>
                <ChevronLeftIcon className={classes.chevronButtons} onClick={() => setWeek(parseInt(weekState)-1)} />
              </Tooltip>

              <Button onClick={() => {findCurrDate()}} style={{margin:'0px 5px', color:'rgba(43,43,43,1)', fontFamily:'Poppins', fontSize:'0.9em', fontWeight:'600', border:'solid 1px lightgray'}}>
                DNES
              </Button>

              <Tooltip title='Next week'>
                <ChevronRightIcon className={classes.chevronButtons} onClick={() => setWeek(parseInt(weekState)+1)} />
              </Tooltip>
            </div>

            <h2 className={classes.monthTitle}>
              {month}
            </h2>
          </div>

          <Button onClick={() => {console.log('add')}} style={{marginRight:'2em', color:'rgba(43,43,43,1)', fontFamily:'Poppins', fontSize:'0.9em', fontWeight:'600', border:'solid 1px lightgray', float:'right'}}>
            PRIDAŤ
          </Button>

          {/* <ToggleButtonGroup
            value={weekState.toString()}
            exclusive
            onChange={handleWeekChange}
            aria-label="week select"
          >
            <ToggleButton value="0">
              1.
            </ToggleButton>
            <ToggleButton value="1">
              2.
            </ToggleButton>
            <ToggleButton value="2">
              3.
            </ToggleButton>
            <ToggleButton value="3">
              4.
            </ToggleButton>
            <ToggleButton value="4">
              5.
            </ToggleButton>
          </ToggleButtonGroup> */}
        </div>

        <span style={{display: 'flex', alignItems: 'center', justifyContent: 'space-around', flexDirection:'row', width:'90%', marginLeft:'7em', height:'3em'}}>
            <h2 className={classes.taskTypeTitle}>
                Meetingy
            </h2>
            <h2 className={classes.taskTypeTitle}>
                Práca
            </h2>
            <h2 className={classes.taskTypeTitle}>
                Komunikácia
            </h2>
            {/* <Button style={{maxWidth: '20px', maxHeight: '20px', minWidth: '20px', minHeight: '20px', marginLeft: '8px'}}>
                <AddIcon />
            </Button> */}
        </span>


        <div style={{width: '100%', flexDirection:'column', overflowY: 'scroll'}}>
          <div style={{alignItems:'center', display: 'flex', flexDirection:'column', paddingLeft: '20px'}}>
            {week.map((day, i) => (
              <div key={day} style={{alignItems:'center', display: 'flex', flexDirection:'row', width: '100%'}}>
                {DateText(day)}
                <RenderTaskList date={day}/>
              </div>
            ))}
          </div>
        </div>
      </div>

    </div>
  );
}

export default App;
