import { makeStyles } from '@material-ui/core/styles';

export const styles = makeStyles((theme) => ({
    detailTitle: {
        fontFamily: 'Poppins',
        color: '#2b2b2b',
        fontWeight: '700',
        fontSize: '1.6em',
        paddingBottom: '15px',
        marginBlockStart: 0,
        marginBlockEnd: 0,
    },
    detailText: {
        fontFamily: 'Poppins',
        color: '#2b2b2b',
        fontWeight: '500',
        fontSize: '1.1em',
        marginBlockStart: 0,
        marginBlockEnd: 0,
        paddingLeft: '20px',
    },
    closeButton: {
        position: 'absolute',
        right: theme.spacing(2),
        top: theme.spacing(3.5),
        color: '#1f1f1d',
    },
    editButton: {
        position: 'absolute',
        right: theme.spacing(8),
        top: theme.spacing(3.5),
        color: '#2B2B28',
    },
    taskStatus: {
        fontFamily: 'Poppins',
        color: '#2b2b2b',
        fontWeight: '500',
        fontSize: '1.1em',
        padding: '0.4em 0.7em',
        borderRadius: '10px',
    },
    taskSelect: {
        width: '90%',
        background: '#FFEAC8',
        borderStyle:'none',
        borderWidth: 2,
        borderRadius: '10px',
        marginLeft: 18,
        padding: '10px 0px',
        "&:focus":{
            borderRadius: '10px',
            background: 'transparent',
        },
    },
    taskTitleInput: {
        paddingBottom: '10px',
        marginBlockStart: 0,
        marginBlockEnd: 0,
        "& input": {
            fontFamily: 'Poppins',
            color: '#2b2b2b',
            fontWeight: '700',
            fontSize: '1.6em',
        }
    },
    taskDescInput: {
        paddingLeft: '20px',
        marginBlockStart: 0,
        marginBlockEnd: 0,
        "& textarea": {
            fontFamily: 'Poppins',
            color: '#2b2b2b',
            fontWeight: '500',
            fontSize: '1.1em',
        }
    },
    updateTaskButton: {
        width: '60%',
        alignSelf: 'center',
        margin: '20px 20px 0 20px',
        fontFamily: 'Poppins',
        fontSize: '14px',
        fontWeight: '600',
        color: 'white',
        background: '#66c660',
        '&:hover': {
            background: '#5ab054',
        }
    },
}));
