import React, { useContext } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Tooltip from '@material-ui/core/Tooltip';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';

import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';
import ChevronRightIcon from '@material-ui/icons/ChevronRight';

import { DataContext } from '../providers/DataProvider';

export function TopBar() {
    const {currWeek, currMonth, taskData} = useContext(DataContext);
    console.log(taskData.meetingTasks);

    return (
        <div style={{alignItems:'center', display: 'flex', flexDirection:'row', margin:'1em 0px', alignContent:'center', width:'95%', justifyContent:'space-between'}}>

          <div style={{display: 'flex', flexDirection:'row', alignItems:'center'}}>
            <div style={{display:'flex', alignItems:'center'}}>
              <Tooltip title='Previous week'>
                <ChevronLeftIcon className={classes.chevronButtons} onClick={() => setWeek(parseInt(weekState)-1)} />
              </Tooltip>

              <Button onClick={() => {findCurrDate()}} style={{margin:'0px 5px', color:'rgba(43,43,43,1)', fontFamily:'Poppins', fontSize:'0.9em', fontWeight:'600', border:'solid 1px lightgray'}}>
                DNES
              </Button>
              <Tooltip title='Next week'>
                <ChevronRightIcon className={classes.chevronButtons} onClick={() => setWeek(parseInt(weekState)+1)} />
              </Tooltip>
            </div>

            <h2 className={classes.monthTitle}>
              {month}
            </h2>
          </div>

          <Button onClick={handleClick} style={{marginRight:'2em', color:'rgba(43,43,43,1)', fontFamily:'Poppins', fontSize:'0.9em', fontWeight:'600', border:'solid 1px lightgray', float:'right'}}>
            PRIDAŤ
          </Button>

          <Menu
            anchorEl={anchorEl}
            anchorOrigin={{ vertical: "bottom", horizontal: "center" }}
            transformOrigin={{ vertical: "top", horizontal: "center" }}
            getContentAnchorEl={null}
            open={Boolean(anchorEl)}
            onClose={handleClose}
          >
              <MenuItem onClick={handleClose} style={{fontFamily:'Poppins'}} >Stretnutie</MenuItem>
              <MenuItem onClick={handleClose} style={{fontFamily:'Poppins'}}>Práca</MenuItem>
              <MenuItem onClick={handleClose} style={{fontFamily:'Poppins'}}>Komunikácia</MenuItem>
          </Menu>

        </div>
    )
}
