import { useStyles } from '../App';
import React, { Component } from 'react';
import Button from '@material-ui/core/Button';
import Box from '@material-ui/core/Box';
import AddIcon from '@material-ui/icons/Add';
import TimerIcon from '@material-ui/icons/Timer';

export function CommTasksList() {
  const classes = useStyles();

  const [userTasks, changeValue] = React.useState([{ id: '1', title: 'Pripravit zmluvu', desc: 'Developer', workTime: '2h', state: '#DFFFEC' },
  { id: '2', title: 'Title 2', desc: 'Designer', workTime: '1h30m', state: 'red' }]);

  const addItem = () => {
    changeValue([...userTasks, {
      id: userTasks.length,
      title: 'Test 3',
      desc: 'kgjfgf',
      workTime: '3h15m',
      state: '#fff',
    }]);
  };

  const state = {
    userData: [
        { id: '1', title: 'Pripravit zmluvu', desc: 'Developer', workTime: '2h', state: '#DFFFEC' },
        { id: '2', title: 'Title 2', desc: 'Designer', workTime: '1h30m', state: '#DFFFEC' }
    ]
  };
  
  const renderItems = () => {
    const data = state.userData;
  
    const mapRows = data.map((item, index) => (
  
        <React.Fragment key={item.id}>
          <Box className={classes.taskBubble} style={{ backgroundColor: item.state }} >
            <Box flexGrow={1}>
              <h3 className={classes.bubbleTitle}>{item.title}</h3>
            </Box>
            
            <TimerIcon style={{marginRight:'5px'}}/>
            <h3 className={classes.bubbleWorkTime}>{item.workTime}</h3>
            
          </Box>
        </React.Fragment>
    ));
    return mapRows;
  };

  return (
    <Box padding={'0px 50px'} width={'25%'}>
      <span style={{display: 'flex', alignItems: 'center', justifyContent: 'center'}}>
        <h2>
          Komunik√°cia
        </h2>
        <Button style={{maxWidth: '20px', maxHeight: '20px', minWidth: '20px', minHeight: '20px', marginLeft: '8px'}} onClick={addItem}>
          <AddIcon />
        </Button>
      </span>

      {renderItems()}
    </Box>
  );
}
