import {
  FormControl,
  Grid,
  IconButton,
  MenuItem,
  Select
} from '@material-ui/core';
import { Close, Create, Flag, FormatAlignLeft } from '@material-ui/icons';
import TimerIcon from '@material-ui/icons/Timer';
import { AnimatePresence, motion } from 'framer-motion';
import moment from 'moment';
import 'moment/locale/sk';
import React, { useContext, useEffect, useState } from 'react';
import { Draggable, Droppable } from 'react-beautiful-dnd';
import { useStyles } from '../../../App';
import '../../../App.css';
import {
  Status,
  useEditWorkTaskStatusMutation,
  WorkTask,
  WorkTasksDocument
} from '../../../generated/graphql';
import { WeekContext } from '../../../providers/WeekProvider';
import { EditWorkTask } from '../EditTask/EditWorkTask';
import { menuProps } from '../helpers';
import { PickWorkTask } from '../RenderTaskList';
import { getTaskStatusData } from './TaskStatusData';

export function WorkTaskBubble({
  date,
  workData
}: {
  date: number;
  workData: PickWorkTask[];
}) {
  const [selectedTask, setSelectedTask] = useState<PickWorkTask>();

  const [taskToEdit, setTaskToEdit] = useState<
    Partial<PickWorkTask> & { statusChanged: boolean }
  >({
    ...selectedTask,
    statusChanged: false
  });

  const [formState, setFormState] = useState<
    Partial<Pick<WorkTask, 'id' | 'date' | 'status'> & { userId: string }>
  >({
    id: selectedTask?.id,
    userId: selectedTask?.user.id,
    date: selectedTask?.date,
    status: selectedTask?.status
  });

  useEffect(() => {
    if (selectedTask?.id !== formState.id) {
      setTaskToEdit({ ...taskToEdit, ...selectedTask });
      setFormState({
        id: selectedTask?.id,
        userId: selectedTask?.user.id,
        date: selectedTask?.date,
        status: selectedTask?.status
      });
    }
  }, [selectedTask, formState.id, taskToEdit]);

  const [openEdit, setOpenEdit] = useState<boolean>(false);

  const handleCloseEdit = () => {
    setOpenEdit(false);
  };

  if (workData == null || workData.length === 0) {
    return (
      <div
        style={{
          justifyContent: 'center',
          height: '100%'
        }}
      >
        <Droppable
          key={moment(date).unix() + 7}
          droppableId={moment(date).unix() + 'w'}
          type="WORK"
        >
          {(provided) => (
            <motion.div
              animate={{ opacity: [0, 1] }}
              initial={{ opacity: 0 }}
              transition={{ duration: 0.5 }}
              key={moment(date).unix() + 8}
              style={{ width: '100%' }}
            >
              <div {...provided.droppableProps} ref={provided.innerRef}>
                <h3
                  style={{
                    textAlign: 'center',
                    paddingTop: '18px',
                    color: '#bdbdbd',
                    fontFamily: 'Poppins',
                    fontSize: '18px',
                    fontWeight: 500
                  }}
                >
                  Prázdne
                </h3>

                {provided.placeholder}
              </div>
            </motion.div>
          )}
        </Droppable>
      </div>
    );
  } else {
    return (
      <div>
        <Droppable droppableId={moment(date).unix() + 'w'} type="WORK">
          {(provided) => (
            <div {...provided.droppableProps} ref={provided.innerRef}>
              {workData.map((item, index) => (
                <WorkTaskRender
                  item={item}
                  index={index}
                  key={index}
                  selectedTask={selectedTask}
                  setSelectedTask={setSelectedTask}
                  setOpenEdit={setOpenEdit}
                />
              ))}
              {provided.placeholder}
            </div>
          )}
        </Droppable>
        <EditWorkTask
          onClose={handleCloseEdit}
          open={openEdit}
          selectedTask={{
            ...selectedTask,
            status: taskToEdit.status,
            statusChanged: selectedTask?.status !== formState.status
          }}
          previousState={formState}
          setPreviousStatusState={setFormState}
          setSelectedTask={setTaskToEdit}
        />
      </div>
    );
  }
}

function WorkTaskRender({
  item,
  index,
  selectedTask,
  setSelectedTask,
  setOpenEdit
}: {
  item: PickWorkTask;
  index: number;
  selectedTask: PickWorkTask | undefined;
  setSelectedTask: React.Dispatch<
    React.SetStateAction<PickWorkTask | undefined>
  >;
  setOpenEdit: React.Dispatch<React.SetStateAction<boolean>>;
}) {
  const { currWeek } = useContext(WeekContext);

  const delayRegular = 0;
  const easeRegular = [0.83, 0, 0.17, 1];

  const [formState, setFormState] = useState({
    id: item.id,
    userId: item.user.id,
    date: item.date,
    status: item.status
  });

  const [editWorkTaskStatus] = useEditWorkTaskStatusMutation();

  const handleStatusChange = async (e) => {
    e.preventDefault();

    setFormState({ ...formState, status: e.target.value.toUpperCase() });

    setFormState((state) => {
      editWorkTaskStatus({
        variables: {
          ...state
        },
        optimisticResponse: {
          editTask: {
            ...item,
            ...state
          }
        },
        update: (store, { data }) => {
          store.readQuery<any>({
            query: WorkTasksDocument,
            variables: {
              userId: item.user.id,
              dateFrom: new Date(currWeek[0]).toISOString(),
              dateTo: new Date(currWeek[6]).toISOString()
            }
          });

          return store.writeQuery({
            query: WorkTasksDocument,
            variables: {
              userId: item.user.id,
              dateFrom: new Date(currWeek[0]).toISOString(),
              dateTo: new Date(currWeek[6]).toISOString()
            },
            data: { items: [data?.editTask] }
          });
        }
      });

      return state;
    });
  };

  const variants = {
    initial: {
      height: 0,
      transition: {
        duration: 0.35,
        delay: delayRegular,
        ease: easeRegular
      }
    },
    open: {
      height: '18em',
      transition: {
        duration: 0.35,
        delay: delayRegular,
        ease: easeRegular
      }
    },
    close: {
      height: 0,
      transition: {
        duration: 0.45,
        delay: delayRegular,
        ease: easeRegular
      }
    }
  };

  const variantsStatus = {
    open: {
      opacity: 1,
      scale: 1,
      transition: {
        delay: 0.05,
        duration: 0.35
      }
    },
    close: {
      opacity: 0,
      scale: 0,
      transition: {
        delay: 0.05,
        duration: 0.35
      }
    }
  };

  const variantsDescription = {
    open: {
      opacity: 1,
      scale: 1,
      transition: {
        delay: 0.1,
        duration: 0.35
      }
    },
    close: {
      opacity: 0,
      scale: 0,
      transition: {
        delay: 0.1,
        duration: 0.35
      }
    }
  };

  const variantsAddress = {
    open: {
      opacity: 1,
      scale: 1,
      transition: {
        delay: 0.15,
        duration: 0.35
      }
    },
    close: {
      opacity: 0,
      scale: 0,
      transition: {
        delay: 0.15,
        duration: 0.35
      }
    }
  };

  const variantsTime = {
    open: {
      opacity: 0,
      display: 'none',
      transition: {
        duration: 0.4
      }
    },
    close: {
      opacity: 1,
      transition: {
        delay: 0.2,
        duration: 0.4
      }
    }
  };

  const variantsTitle = {
    open: {
      fontSize: '1.3em',
      transition: {
        duration: 0.4
      }
    },
    close: {
      fontSize: '1em',
      transition: {
        duration: 0.4
      }
    }
  };

  const variantsCloseButton = {
    open: {
      opacity: 1,
      transition: {
        delay: 0.4,
        duration: 0.4
      }
    },
    close: {
      opacity: 0,
      transition: {
        duration: 0.4
      }
    }
  };

  const variantsScroll = {
    open: {
      opacity: 1,
      transition: {
        delay: 0.35,
        duration: 0.35
      }
    },
    close: {
      opacity: 0,
      transition: {
        duration: 0.35
      }
    }
  };

  const open = item.id === selectedTask?.id;
  const setOpen = (task: PickWorkTask | undefined) => {
    setSelectedTask(task);
  };

  const classes = useStyles();

  const handleClickOpenEdit = () => {
    setOpenEdit(true);
  };

  return (
    <Draggable
      key={item.id}
      draggableId={item.id}
      index={index}
      isDragDisabled={open}
    >
      {(provided) => (
        <div
          ref={provided.innerRef}
          {...provided.draggableProps}
          {...provided.dragHandleProps}
          style={{
            marginBottom: '1em',
            ...provided.draggableProps.style
          }}
        >
          <AnimatePresence>
            <div
              className={classes.taskBubble}
              style={{
                backgroundColor: open
                  ? '#ECEFF1'
                  : getTaskStatusData(item.status).color,
                display: 'flex',
                flexDirection: 'column',
                minHeight: '1.5em'
              }}
              onClick={() => {
                if (open === true) return;
                return setOpen(item);
              }}
            >
              <motion.div
                layout
                layoutId={item.id}
                variants={variants}
                initial="initial"
                animate={open ? 'open' : 'close'}
                exit="close"
                style={{ width: '100%' }}
                key={item.id}
              >
                <motion.div
                  style={{
                    position: 'absolute',
                    zIndex: 1,
                    top: 5,
                    right: 50,
                    width: '3em',
                    height: '3em',
                    display: 'flex'
                  }}
                  variants={variantsCloseButton}
                  animate={
                    open ? variantsCloseButton.open : variantsCloseButton.close
                  }
                  initial={variantsCloseButton.close}
                >
                  <IconButton
                    aria-label="edit"
                    style={{ color: '#1f1f1d' }}
                    onClick={() => {
                      handleClickOpenEdit();
                    }}
                  >
                    <Create />
                  </IconButton>
                  <IconButton
                    aria-label="close"
                    style={{ color: '#1f1f1d' }}
                    onClick={() => setSelectedTask(undefined)}
                  >
                    <Close />
                  </IconButton>
                </motion.div>
                <Grid container>
                  <Grid
                    container
                    item
                    xs={8}
                    md={8}
                    lg={8}
                    justify="flex-start"
                  >
                    <motion.div
                      style={{ width: '100%' }}
                      variants={variantsTitle}
                      animate={open ? variantsTitle.open : variantsTitle.close}
                      initial={variantsTitle.close}
                    >
                      <h3 className={classes.bubbleTitle}>{item.title}</h3>
                    </motion.div>
                  </Grid>

                  <Grid
                    container
                    item
                    xs={4}
                    md={4}
                    lg={4}
                    justify="flex-end"
                    alignItems="center"
                  >
                    <motion.div
                      style={{ display: 'flex', alignItems: 'center' }}
                      variants={variantsTime}
                      animate={open ? variantsTime.open : variantsTime.close}
                      initial={variantsTime.close}
                    >
                      <TimerIcon style={{ marginRight: '5px' }} />
                      <h3 className={classes.bubbleWorkTime}>
                        {item.workTime}
                      </h3>
                    </motion.div>
                  </Grid>
                </Grid>

                <motion.div
                  variants={variantsScroll}
                  animate={open ? variantsScroll.open : variantsScroll.close}
                  initial={variantsScroll.close}
                  exit={variantsScroll.close}
                  style={{
                    width: '100%',
                    height: '16em',
                    overflowY: 'auto',
                    marginTop: '1.2em'
                  }}
                >
                  {open && (
                    <motion.div
                      layout
                      variants={variantsStatus}
                      animate={
                        open ? variantsStatus.open : variantsStatus.close
                      }
                      initial={variantsStatus.close}
                      exit={variantsStatus.close}
                    >
                      <span
                        style={{
                          display: 'flex',
                          alignItems: 'center',
                          justifyContent: 'start',
                          padding: '0px 0px 10px 0px'
                        }}
                      >
                        <Flag style={{ paddingTop: '2px' }} />
                        <FormControl>
                          <Select
                            disableUnderline
                            classes={{ root: 'task-select' }}
                            MenuProps={menuProps}
                            value={formState.status.toLowerCase()}
                            onChange={handleStatusChange}
                          >
                            <MenuItem value={'done'}>
                              {renderTaskStatusBubble(
                                getTaskStatusData(Status.Done)
                              )}
                            </MenuItem>
                            <MenuItem value={'in_progress'}>
                              {renderTaskStatusBubble(
                                getTaskStatusData(Status.InProgress)
                              )}
                            </MenuItem>
                            <MenuItem value={'not_done'}>
                              {renderTaskStatusBubble(
                                getTaskStatusData(Status.NotDone)
                              )}
                            </MenuItem>
                          </Select>
                        </FormControl>
                      </span>
                    </motion.div>
                  )}

                  {open && (
                    <motion.div
                      layout
                      variants={variantsDescription}
                      animate={
                        open
                          ? variantsDescription.open
                          : variantsDescription.close
                      }
                      initial={variantsDescription.close}
                      exit={variantsDescription.close}
                      style={{ marginTop: '0.5em' }}
                    >
                      {item.description !== '' && (
                        <span className="task-detail-info">
                          <FormatAlignLeft style={{ paddingTop: '2px' }} />
                          <h3
                            className="detail-text"
                            style={{ textAlign: 'justify' }}
                          >
                            {item.description}
                          </h3>
                        </span>
                      )}
                    </motion.div>
                  )}

                  {open && (
                    <motion.div
                      layout
                      variants={variantsAddress}
                      animate={
                        open ? variantsAddress.open : variantsAddress.close
                      }
                      initial={variantsAddress.close}
                      exit={variantsAddress.close}
                      style={{ marginTop: '0.5em' }}
                    >
                      {item.workTime !== '' && (
                        <span className="task-detail-info">
                          <TimerIcon />
                          <h3 className="detail-text">{item.workTime}</h3>
                        </span>
                      )}
                    </motion.div>
                  )}
                </motion.div>
              </motion.div>
            </div>
          </AnimatePresence>
        </div>
      )}
    </Draggable>
  );
}

const renderTaskStatusBubble = (taskStatusData) => {
  return (
    <span
      className={'task-status'}
      style={{ backgroundColor: taskStatusData.color }}
    >
      {taskStatusData.title}
    </span>
  );
};
