import { Status } from '../../../generated/graphql';

interface StatusModel {
  id: string;
  color: string;
  title: string;
}

export function getTaskStatusData(status: Status): StatusModel {
  switch (status) {
    case Status.Done: {
      return {
        id: 'done',
        color: '#DFFFEC',
        title: 'Splnené'
      };
    }
    case Status.InProgress: {
      return {
        id: 'in_progress',
        color: '#FEFFE1',
        title: 'Rozpracované'
      };
    }
    case Status.NotDone: {
      return {
        id: 'not_done',
        color: '#FFD9D9',
        title: 'Nesplnené'
      };
    }
  }
}
