import { useStyles } from '../App';
import '../App.css';
import React, { useState } from 'react';
import { GetTaskStatusData } from './TaskStatusData';
import { makeStyles } from '@material-ui/core/styles';
import PropTypes from 'prop-types';
import Box from '@material-ui/core/Box';
import Dialog from '@material-ui/core/Dialog';
import moment from 'moment';
import 'moment/locale/sk';

import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";
import Select from '@material-ui/core/Select';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';

import FormatAlignLeftIcon from '@material-ui/icons/FormatAlignLeft';
import TimerIcon from '@material-ui/icons/Timer';
import IconButton from '@material-ui/core/IconButton';
import CloseIcon from '@material-ui/icons/Close';
import CreateIcon from '@material-ui/icons/Create';
import FlagIcon from '@material-ui/icons/Flag';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';

import data from '../MOCK_DATA.json';

const styles = makeStyles((theme) => ({
    detailTitle: {
        fontFamily: 'Poppins',
        color: '#2b2b2b',
        fontWeight: '700',
        fontSize: '1.6em',
        paddingBottom: '15px',
        marginBlockStart: 0,
        marginBlockEnd: 0,
    },
    detailText: {
        fontFamily: 'Poppins',
        color: '#2b2b2b',
        fontWeight: '500',
        fontSize: '1.1em',
        marginBlockStart: 0,
        marginBlockEnd: 0,
        paddingLeft: '20px',
    },
    closeButton: {
        position: 'absolute',
        right: theme.spacing(2),
        top: theme.spacing(3.5),
        color: '#1f1f1d',
    },
    editButton: {
        position: 'absolute',
        right: theme.spacing(8),
        top: theme.spacing(3.5),
        color: '#2B2B28',
    },
    taskStatus: {
        fontFamily: 'Poppins',
        color: '#2b2b2b',
        fontWeight: '500',
        fontSize: '1.1em',
        padding: '0.4em 0.7em',
        borderRadius: '10px',
        marginLeft: '20px',
    }
}));

function DetailDialog(props) {
    const classes = styles();

    const { onClose, selectedTask, open } = props;

    const handleClose = () => {
        onClose(selectedTask);
    };

    const taskStatusData = GetTaskStatusData(selectedTask.status);

    const [val,setVal] = useState(1);

    const handleChange = (event) => {
        setVal(event.target.value);
    };

    const iconComponent = (props) => {
        return (
            <ExpandMoreIcon />
        )};

    // moves the menu below the select input
    const menuProps = {
        anchorOrigin: {
        vertical: "bottom",
            horizontal: "left"
        },
        transformOrigin: {
        vertical: "top",
            horizontal: "left"
        },
        getContentAnchorEl: null
    };

    return (
        <Dialog
            onClose={handleClose}
            aria-labelledby="simple-dialog-title"
            open={open}
            fullWidth={true}
            maxWidth={'xs'}
            PaperProps={{ style: { backgroundColor: '#FFEAC8', color: 'black', borderRadius: '30px', padding: '35px', boxShadow: '0px 0px 15px 0px rgba(0,0,0,0.15)', }, elevation: 0 }}
            BackdropProps={{ style: { backgroundColor: "rgba(0, 0, 0, 0.25)" } }}
        >
            <h1 className={classes.detailTitle}>{selectedTask.title}</h1>
            <IconButton aria-label="close" className={classes.editButton} onClick={onClose}>
                <CreateIcon />
            </IconButton>
            <IconButton aria-label="close" className={classes.closeButton} onClick={onClose}>
                <CloseIcon />
            </IconButton>

            <span style={{ display: 'flex', alignItems: 'center', justifyContent: 'start', padding: '10px 0px' }}>
                <FlagIcon style={{ paddingTop: '2px' }} />
                <span className={classes.taskStatus} style={{backgroundColor: taskStatusData.color}}>
                    {taskStatusData.title}
                </span>
            </span>

            <FormControl>
                <Select
                    disableUnderline
                    MenuProps={menuProps}
                    IconComponent={iconComponent}
                    value={val}
                    onChange={handleChange}
                >
                    <MenuItem value={0}>Principle</MenuItem>
                    <MenuItem value={1}>Sketch</MenuItem>
                    <MenuItem value={2}>Photoshop</MenuItem>
                    <MenuItem value={3}>Framer</MenuItem>
                </Select>
            </FormControl>

            <span style={{ display: 'flex', alignItems: 'flex-start', justifyContent: 'start', padding: '10px 0px' }}>
                <FormatAlignLeftIcon style={{ paddingTop: '2px' }} />
                <h3 className={classes.detailText}>
                    {selectedTask.desc}
                </h3>
            </span>

            <span style={{ display: 'flex', alignItems: 'flex-start', justifyContent: 'start', padding: '10px 0px' }}>
                <TimerIcon />
                <h3 className={classes.detailText}>
                    {selectedTask.workTime}
                </h3>
            </span>

        </Dialog>
    );
}

DetailDialog.propTypes = {
    onClose: PropTypes.func.isRequired,
    open: PropTypes.bool.isRequired,
    selectedTask: PropTypes.object.isRequired,
};

export function TaskBubble(type, date) {
    const classes = useStyles();

    const filteredData = data.filter((task) => task.type.includes(type)).filter((task) => task.date.includes(moment(date).format("DD/MM/yyyy"))); //filter tasks by specified type

    const [open, setOpen] = React.useState(false);
    const [selectedTask, setSelectedTask] = React.useState({ id: '0', title: 'Test', desc: 'Test', workTime: '100h', status: 'not_done' });

    const handleClickOpen = (task) => {
        setOpen(true);
        setSelectedTask(task);
    };

    const handleClose = () => {
        setOpen(false);
    }

    if (filteredData === undefined || filteredData.length === 0) {
        return (
            <div style={{justifyContent:"center", height:"100%"}}>
                <h3 style={{textAlign:"center", paddingTop:"18px", color:"#bdbdbd", fontFamily:"Poppins", fontSize:"18px", fontWeight:"500"}}>
                    Empty
                </h3>
            </div>
        )

    } else {
        return (
            <DragDropContext>
                <Droppable droppableId="taskBubbles">
                    {(provided) => (
                        <div {...provided.droppableProps} ref={provided.innerRef}>
                            {filteredData.map((item, index) => (
                                <Draggable key={item.id} draggableId={item.id} index={index} >
                                    {(provided) => (
                                        <div {...provided.draggableProps} {...provided.dragHandleProps}>
                                            <Box  className={classes.taskBubble} style={{ backgroundColor: GetTaskStatusData(item.status).color }} onClick={() => handleClickOpen(item)} ref={provided.innerRef}>
                                                <Box flexGrow={1}>
                                                    <h3 className={classes.bubbleTitle}>{item.title}</h3>
                                                </Box>
                                                <TimerIcon style={{marginRight: '5px'}} />
                                                <h3 className={classes.bubbleWorkTime}>{item.workTime}</h3>
                                            </Box>
                                            <DetailDialog open={open} onClose={handleClose} selectedTask={selectedTask} />
                                        </div>
                                    )}
                                </Draggable>
                            ))}
                            {provided.placeholder}
                        </div>
                    )}
                </Droppable>
            </DragDropContext>
        )
    }


};
