import moment from 'moment';
import React from 'react';

interface InputProps {
  day: number;
}

export function DateText({ day }: InputProps) {
  const number = moment(day).format('D');
  const dayText = moment(day).format('dddd');

  if (moment(day).dayOfYear() === moment().dayOfYear()) {
    return (
      <div
        style={{
          alignItems: 'flex-end',
          display: 'flex',
          flexDirection: 'row',
          width: 'auto',
          backgroundColor: 'rgba(241, 214, 171, 0.5)',
          margin: '2em 0 0 5m',
          borderRadius: '10px',
          padding: '0.5em 1em'
        }}
      >
        <h3
          style={{
            fontFamily: 'Poppins',
            fontWeight: 800,
            fontSize: '1.5em',
            color: '#2b2b2b'
          }}
        >
          {number}.
        </h3>

        <h3
          style={{
            fontFamily: 'Poppins',
            fontWeight: 600,
            fontSize: '1.3em',
            color: '#6c6c6c',
            textTransform: 'capitalize',
            marginLeft: '0.5em',
            marginBottom: '0.1em'
          }}
        >
          {dayText}
        </h3>
      </div>
    );
  } else {
    return (
      <div
        style={{
          alignItems: 'flex-end',
          display: 'flex',
          flexDirection: 'row',
          width: 'auto',
          margin: '2em 0em 0em 5m',
          padding: '0.5em 1em'
        }}
      >
        <h3
          style={{
            fontFamily: 'Poppins',
            fontWeight: 800,
            fontSize: '1.5em',
            color: '#2b2b2b'
          }}
        >
          {number}.
        </h3>

        <h3
          style={{
            fontFamily: 'Poppins',
            fontWeight: 600,
            fontSize: '1.3em',
            color: '#6c6c6c',
            textTransform: 'capitalize',
            marginLeft: '0.5em',
            marginBottom: '0.1em'
          }}
        >
          {dayText}
        </h3>
      </div>
    );
  }
}
