import React from 'react';

import Button from '@material-ui/core/Button';
import Box from '@material-ui/core/Box';
import Avatar from '@material-ui/core/Avatar';
import NotificationsIcon from '@material-ui/icons/Notifications';
import Badge from '@material-ui/core/Badge';
import { useStyles } from '../App';

export function SideBar() {
    const classes = useStyles();
    const [invisible, setInvisible] = React.useState(false);

    const handleBadgeVisibility = () => {
      setInvisible(!invisible);
    };

    return (
      <Box className={classes.sideBar}>
        <Avatar alt="Remy Sharp" src="/static/images/avatar/1.jpg" style={{alignSelf: 'center', margin: 'auto', padding: '30px'}} />
        <Badge color="secondary" variant="dot" className={classes.iconButton} onClick={handleBadgeVisibility} invisible={invisible} aria-label="notification" style={{cursor: 'pointer'}}>
          <NotificationsIcon style={{ fontSize: 25 }}/>
        </Badge>
        <Box paddingLeft={2} paddingRight={5} marginTop={5}>
          <Button className={classes.button}>
            Hlavný plán
          </Button>
          <Button className={classes.button}>
            Denný plán
          </Button>
        </Box>
      </Box>
    );
  }
