import React, { useState, useContext } from 'react';
import {
  BrowserRouter as Router,
  Switch,
  Route
} from "react-router-dom";
import './App.css';
import './index';
import { Helmet } from 'react-helmet';

import { SideBar } from './sideBar/SideBar';
import { TopBar } from './sections/TopBar';
import { makeStyles } from '@material-ui/core/styles';
import moment from 'moment';
import 'moment/locale/sk';
import { RenderTaskList } from './sections/RenderTaskList';

import { DataContext } from "./providers/DataProvider";

export const useStyles = makeStyles((theme) => ({
  main: {
    height: '-webkit-fill-available',
    scrollBehavior: 'smooth',
  },
  root: {
    padding: '2px 10px',
    display: 'flex',
    alignItems: 'center',
    width: 300,
    height: '-webkit-fill-available',
    borderColor: '#f1d6ab',
    borderRadius: 50,
    background: '#00000040',
  },
  input: {
    marginLeft: theme.spacing(1),
    flex: 1,
    color: '#f1d6ab',
    fontFamily: 'Sen',
    fontSize: 18,
  },
  taskListWindow: {
    justifyContent: 'center',
    margin: '20px 30px 20px 30px',
    display: 'flex',
    width: '95%',
    minHeight: '8em',
    background: '#fff',
    borderRadius: '20px',
    padding: '20px 0px',
    boxShadow: '0px 0px 20px 0px rgba(0,0,0,0.05)',
    "&:focus":{
      outline: '0px',
    },
  },
  taskBubble: {
    display: 'flex',
    borderRadius: '15px',
    padding: '1.1em 1.6em',
    marginBottom: '1em',
    fontFamily:'Poppins',
    backgroundColor: '#fff',
    color: '#2b2b28',
    width: '95%',
    alignItems: 'center',
    cursor: 'pointer',
    position: 'relative',
    right: '12px',
  },
  bubbleTitle: {
    color: '#2b2b2b',
    fontWeight: '600',
    fontSize: '0.9em',
    marginBlockStart: 0,
    marginBlockEnd: 0,
    textTransform: 'capitalize',
  },
  bubbleDesc: {
    fontWeight: '600',
    fontSize: 15,
    marginBlockStart: 0,
    marginBlockEnd: 0,
  },
  bubbleWorkTime: {
    fontWeight: '600',
    fontSize: '0.8em',
    marginBlockStart: 0,
    marginBlockEnd: 0,
    paddingTop: '2px',
  },
  taskSelect: {
    fontFamily: 'Poppins',
  },
}));

function DateText(day) {
  const number = moment(day).format('D');
  const dayText = moment(day).format('dddd');

  if(moment(day).dayOfYear() === moment().dayOfYear()) {
    return (
      <div style={{alignItems:'flex-end', display: 'flex', flexDirection:'row', width:'auto', backgroundColor:'rgba(241, 214, 171, 0.5)', margin:'2em 0 0 5m', borderRadius:'20px', padding:'1em'}}>
        <h3 style={{fontFamily:'Poppins', fontWeight:'800', fontSize:'1.5em', color:'#2b2b2b'}}>
          {number}.
        </h3>

        <h3 style={{fontFamily:'Poppins', fontWeight:'600', fontSize:'1.3em', color:'#6c6c6c', textTransform:'capitalize', marginLeft:'0.5em', marginBottom:'0.1em' }}>
          {dayText}
        </h3>
      </div>
    )
  } else {
    return (
      <div style={{alignItems:'flex-end', display: 'flex', flexDirection:'row', width:'auto', margin:'2em 0em 0em 5m', padding:'1em'}}>
        <h3 style={{fontFamily:'Poppins', fontWeight:'800', fontSize:'1.5em', color:'#2b2b2b'}}>
          {number}.
        </h3>

        <h3 style={{fontFamily:'Poppins', fontWeight:'600', fontSize:'1.3em', color:'#6c6c6c', textTransform:'capitalize', marginLeft:'0.5em', marginBottom:'0.1em'}}>
          {dayText}
        </h3>
      </div>
    )
  }
}


export function App() {
  const classes = useStyles();
  const { currWeek } = useContext(DataContext);

  return (
    <Router>
      <div style={{ width: '100%', height: '100%', backgroundColor: 'rgb(252,252,252)', display:'flex', flexDirection:'row',}} >
        <SideBar />

        <Switch>
          <Route path="/">
            <div style={{alignItems:'center', display: 'flex', flexDirection:'column', width:'100%', paddingLeft:'10px'}}>
              <Helmet>
                <title>Consult App</title>
              </Helmet>

              <TopBar />

              <span style={{display: 'flex', alignItems: 'center', justifyContent: 'space-around', flexDirection:'row', width:'95%', height:'3em'}}>
                  <h2 className={classes.taskTypeTitle}>
                      Meetingy
                  </h2>
                  <h2 className={classes.taskTypeTitle}>
                      Práca
                  </h2>
                  <h2 className={classes.taskTypeTitle}>
                      Komunikácia
                  </h2>
              </span>

              <div style={{width: '100%', flexDirection:'column', overflowY: 'scroll'}}>
                <div style={{alignItems:'center', display: 'flex', flexDirection:'column', paddingLeft: '20px'}}>
                  {currWeek.map((day, i) => (
                    <div key={day} style={{alignItems:'start', display: 'flex', flexDirection:'column', width: '100%'}}>
                      {DateText(day)}
                      <RenderTaskList date={day}/>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          </Route>

          <Route path="/dashboard">
            <div>
              Dashboard
            </div>
          </Route>
        </Switch>
      </div>
    </Router>
  );
}

export default App;
