import { Box, Button, Grid, Paper, Typography } from '@material-ui/core';
import Avatar from '@material-ui/core/Avatar';
import { makeStyles } from '@material-ui/core/styles';
import PlaceIcon from '@material-ui/icons/Place';
import ScheduleIcon from '@material-ui/icons/Schedule';
import { AvatarGroup, Pagination } from '@material-ui/lab';
import { motion } from 'framer-motion';
import React, { useContext, useEffect, useState } from 'react';
import { CreateTeam } from './components/CreateTeam';
import {
  Team,
  User,
  useTeamManyQuery,
  useTeamMeetingTaskManyQuery
} from '../../generated/graphql';
import { AuthContext } from '../../providers/UserProvider';
import { AddMember } from './components/AddMember';
import { CompactPlannerView } from '../../components/CompactPlannerView';

const useStyles = makeStyles((theme) => ({
  teamCardNotSelected: {
    backgroundColor: '#ffffff',
    transition: theme.transitions.create(['background-color', 'transform'], {
      duration: 300
    }),
    padding: '1.5em 10% 1em 10%',
    boxShadow: 'none',
    borderRadius: '25px',
    cursor: 'pointer'
  },
  teamCardSelected: {
    backgroundColor: '#2B2B28',
    transition: theme.transitions.create(['background-color', 'transform'], {
      duration: 300
    }),
    color: 'white',
    padding: '1.5em 10% 1em 10%',
    boxShadow: 'none',
    borderRadius: '25px',
    cursor: 'pointer'
  },
  createTeamBtn: {
    width: '20em',
    fontFamily: 'Poppins',
    fontSize: '15px',
    padding: '1em 0.8em',
    fontWeight: 500,
    color: 'white',
    backgroundColor: '#E3B04B',
    boxShadow: 'none',
    borderRadius: '10px',
    textTransform: 'none',
    '&:hover': {
      backgroundColor: '#ffc44f'
    }
  },

  detailViewBtn: {
    width: '20em',
    alignSelf: 'center',
    fontFamily: 'Poppins',
    fontSize: '15px',
    padding: '1em 0.8em',
    fontWeight: 500,
    color: 'white',
    backgroundColor: '#2B2B28',
    boxShadow: 'none',
    borderRadius: '10px',
    textTransform: 'none',
    '&:hover': {
      backgroundColor: '#ffc44f'
    }
  },

  taskBubble: {
    alignSelf: 'center',
    display: 'flex',
    borderRadius: '15px',
    padding: '1.1em 1.6em',
    fontFamily: 'Poppins',
    backgroundColor: '#fff',
    color: '#2b2b28',
    width: '70%',
    alignItems: 'center',
    cursor: 'pointer',
    transition: 'background-color 1s'
  },
  bubbleTitle: {
    color: '#2b2b2b',
    fontWeight: 600,
    fontSize: '0.9em',
    marginBlockStart: 0,
    marginBlockEnd: 0,
    textTransform: 'capitalize'
  },
  bubbleWorkTime: {
    fontWeight: 600,
    fontSize: '0.8em',
    marginBlockStart: 0,
    marginBlockEnd: 0,
    paddingTop: '2px'
  },
  teamMember: {
    transition: 'all 0.5s',
    '&:hover': {
      backgroundColor: 'gray',
      transition: 'all 0.5s'
    }
  }
}));

type PickTeam = Pick<Team, 'id' | 'name' | 'description'> & {
  owner?: Pick<User, 'id' | 'name' | 'email'> | null;
  members: Pick<User, 'id' | 'name' | 'email'>[];
};

export function Teams() {
  const classes = useStyles();
  const { currentUser } = useContext(AuthContext);

  const { data } = useTeamManyQuery({
    variables: {
      userId: currentUser.uid
    }
  });

  const [didChange, setDidChange] = useState<boolean>(false);

  const [selectedTeam, setSelectedTeam] = useState<PickTeam>({
    id: '',
    description: '',
    members: [],
    name: ''
  });
  const [page, setPage] = useState(1);

  const [openCreateTeam, setOpenCreateTeam] = useState<boolean>(false);

  useEffect(() => {
    if (data != null && selectedTeam.id === '') {
      const item = data.teamMany.items[0];
      if (item == null) return;

      setSelectedTeam(item);
    }
  }, [setSelectedTeam, selectedTeam, data]);

  return (
    <Grid
      container
      style={{
        paddingLeft: '2%',
        paddingRight: '4%',
        overflow: 'hidden'
      }}
    >
      <Grid container item xs={5} md={5} lg={5} alignContent="center">
        <Grid container style={{ height: '88%' }} alignContent="flex-start">
          <Grid
            container
            item
            style={{
              height: '85%',
              width: '100%'
            }}
            alignContent="flex-start"
          >
            {data &&
              data.teamMany.items
                .slice((page - 1) * 8, page * 8)
                .map((team, index) => {
                  if (team == null) return <></>;
                  return (
                    <TeamCard
                      team={team}
                      isSelected={selectedTeam?.id === team.id ? true : false}
                      setSelectedTeam={setSelectedTeam}
                      setDidChange={setDidChange}
                      key={index}
                    />
                  );
                })}
          </Grid>

          <Grid
            container
            item
            xs={12}
            md={12}
            lg={12}
            justify="center"
            style={{ paddingLeft: '4%' }}
          >
            <Pagination
              count={Math.round((data?.teamMany.items.length ?? 0) / 6)}
              onChange={(event, value) => setPage(value)}
            />
          </Grid>

          <Grid
            container
            item
            xs={12}
            md={12}
            lg={12}
            justify="center"
            style={{
              paddingTop: '1.5em',
              paddingLeft: '4%'
            }}
          >
            <Button
              className={classes.createTeamBtn}
              onClick={() => setOpenCreateTeam(true)}
            >
              Vytvoriť nový tím
            </Button>
          </Grid>
        </Grid>
      </Grid>

      <Grid
        container
        item
        xs={1}
        md={1}
        lg={1}
        justify="center"
        alignContent="center"
      >
        <div
          style={{
            width: '0.115em',
            backgroundColor: '#CACACA',
            height: '90%'
          }}
        ></div>
      </Grid>

      <Grid container item xs={6} md={6} lg={6} alignContent="center">
        <Paper
          style={{
            height: '88%',
            width: '100%',
            background: '#fff',
            boxShadow: 'none',
            borderRadius: '30px'
          }}
        >
          <TeamDetail
            team={selectedTeam}
            didChange={didChange}
            setDidChange={setDidChange}
          />
        </Paper>
      </Grid>
      <CreateTeam
        open={openCreateTeam}
        onClose={() => setOpenCreateTeam(false)}
      />
    </Grid>
  );
}

function TeamDetail({
  team,
  didChange,
  setDidChange
}: {
  team: PickTeam;
  didChange: boolean;
  setDidChange: React.Dispatch<React.SetStateAction<boolean>>;
}) {
  const classes = useStyles();

  const { data } = useTeamMeetingTaskManyQuery({
    variables: {
      teamId: team.id,
      dateFrom: new Date(new Date().setUTCHours(0, 0, 0, 0)),
      dateTo: new Date(new Date().setUTCHours(23, 59, 59, 59))
    }
  });

  const variants = {
    show: { opacity: [0, 1], transition: { duration: 0.75 } },
    hide: { opacity: [1, 0], transition: { duration: 0.75 } }
  };

  const opened = () => setDidChange(false);

  const [open, setOpen] = useState<boolean>(false);
  const [openCompactPlanner, setOpenCompact] = useState<boolean>(false);

  return (
    <Grid container justify="center">
      <Grid container item xs={12} md={12} lg={12}>
        <Grid item xs={12} md={12} lg={12} style={{ paddingTop: '1em' }}>
          <motion.div variants={variants} animate={didChange ? 'hide' : 'show'}>
            <Typography
              variant="h5"
              style={{
                fontFamily: 'Poppins',
                fontSize: '1.5em',
                fontWeight: 600,
                textAlign: 'center'
              }}
            >
              {team.name}
            </Typography>
          </motion.div>
        </Grid>
        <Grid item xs={12} md={12} lg={12} style={{ paddingBottom: '1em' }}>
          <motion.div variants={variants} animate={didChange ? 'hide' : 'show'}>
            <Typography
              variant="body2"
              style={{
                fontFamily: 'Poppins',
                fontSize: '0.9em',
                fontWeight: 400,
                color: '#737373',
                textAlign: 'center'
              }}
            >
              Počet členov: {team.members.length}
            </Typography>
          </motion.div>
        </Grid>
      </Grid>
      <Grid container item xs={9} md={9} lg={9}>
        <motion.div
          variants={variants}
          animate={didChange ? 'hide' : 'show'}
          style={{ width: '100%' }}
        >
          <Paper
            style={{
              width: '100%',
              boxShadow: 'none',
              height: '18em',
              overflow: 'auto'
            }}
          >
            {team.members.map((member) => {
              opened();

              return (
                <div
                  className={classes.teamMember}
                  style={{
                    display: 'flex',
                    flexDirection: 'row',
                    alignItems: 'center',
                    padding: '0px 2em'
                  }}
                  onClick={() => setOpenCompact(true)}
                >
                  <Avatar
                    alt={member.name}
                    src="/static/images/avatar/1.jpg"
                    style={{
                      margin: '0.3em 1em',
                      width: '45px',
                      height: '45px'
                    }}
                  />
                  <Typography
                    style={{
                      fontFamily: 'Poppins',
                      fontSize: '1em',
                      fontWeight: 400
                    }}
                  >
                    {member.name}
                  </Typography>
                </div>
              );
            })}
          </Paper>
        </motion.div>
      </Grid>
      <Grid
        container
        item
        xs={12}
        md={12}
        lg={12}
        justify="center"
        style={{ marginTop: '2em' }}
      >
        <Button className={classes.detailViewBtn} onClick={() => setOpen(true)}>
          Pridať člena
        </Button>
      </Grid>
      <Grid
        container
        item
        xs={12}
        md={12}
        lg={12}
        justify="center"
        style={{ paddingTop: '3em' }}
      >
        <Paper
          style={{
            minHeight: '15em',
            overflow: 'auto',
            width: '100%',
            marginTop: '1.5em',
            boxShadow: 'none',
            textAlign: 'center'
          }}
        >
          {data && (
            <motion.div
              variants={variants}
              animate={didChange ? 'hide' : 'show'}
            >
              <Typography
                style={{
                  fontFamily: 'Poppins',
                  fontSize: '1.1em',
                  fontWeight: 500,
                  marginBottom: '1.5em',
                  textAlign: 'center'
                }}
              >
                {data.teamMeetingTaskMany.total < 2
                  ? data.teamMeetingTaskMany.total === 0
                    ? `Žiadne nadchádzajuce stretnutie`
                    : `${data.teamMeetingTaskMany.total} nadchádzajúce stretnutie`
                  : `${data.teamMeetingTaskMany.total} nadchádzajúce stretnutia`}
              </Typography>
              <Grid container justify="center">
                {data?.teamMeetingTaskMany?.items.map((meeting) => (
                  <Grid
                    container
                    item
                    xs={7}
                    md={7}
                    lg={7}
                    className={classes.taskBubble}
                    style={{
                      backgroundColor: '#ededed',
                      display: 'flex',
                      flexDirection: 'column',
                      width: '100%'
                    }}
                  >
                    <Box
                      style={{
                        display: 'flex',
                        flexDirection: 'row',
                        width: '100%',
                        justifyContent: 'space-between'
                      }}
                    >
                      <Box>
                        <h3 className={classes.bubbleTitle}>{meeting.title}</h3>
                      </Box>
                      <div style={{ display: 'flex', flexDirection: 'row' }}>
                        <ScheduleIcon style={{ marginRight: '5px' }} />
                        <h3 className={classes.bubbleWorkTime}>
                          {meeting.startTime} - {meeting.endTime}
                        </h3>
                      </div>
                    </Box>
                    <Box
                      style={{
                        display: 'flex',
                        flexDirection: 'row',
                        width: '100%',
                        justifyContent: 'flex-end',
                        marginTop: '5px'
                      }}
                    >
                      <PlaceIcon />
                      <h3 className={classes.bubbleWorkTime}>
                        {meeting.address}
                      </h3>
                    </Box>
                  </Grid>
                ))}
              </Grid>
            </motion.div>
          )}
        </Paper>
      </Grid>
      <Grid container item xs={12} md={12} lg={12} justify="center">
        <Button className={classes.detailViewBtn}>
          Vytvoriť tímový meeting
        </Button>
      </Grid>
      <AddMember
        open={open}
        onClose={() => setOpen(false)}
        members={team.members}
      />
      <CompactPlannerView 
        open={openCompactPlanner} 
        onClose={() => setOpenCompact(false)} 
      />
    </Grid>
  );
}

function TeamCard({
  team,
  isSelected,
  setSelectedTeam,
  setDidChange
}: {
  team: PickTeam;
  isSelected: boolean;
  setSelectedTeam: React.Dispatch<React.SetStateAction<PickTeam>>;
  setDidChange: React.Dispatch<React.SetStateAction<boolean>>;
}): JSX.Element {
  const classes = useStyles();

  const style = isSelected
    ? classes.teamCardSelected
    : classes.teamCardNotSelected;
  return (
    <Grid
      item
      xs={12}
      md={12}
      lg={6}
      style={{
        paddingLeft: '5%',
        paddingBottom: '5%'
      }}
    >
      <motion.div
        animate={{ opacity: [0, 1] }}
        style={{ width: '100%', height: '100%' }}
      >
        <Paper
          className={style}
          onClick={() => {
            setDidChange(true);
            setSelectedTeam(team);
          }}
        >
          <Typography
            variant="h5"
            style={{
              fontFamily: 'Poppins',
              fontSize: '1.2em',
              fontWeight: 600
            }}
          >
            {team.name}
          </Typography>
          <Typography
            variant="h5"
            style={{
              fontFamily: 'Poppins',
              fontSize: '0.8em',
              color: '#737373'
            }}
          >
            Počet členov: {team.members.length}
          </Typography>
          <div
            style={{
              display: 'flex',
              flexDirection: 'row',
              justifyContent: 'flex-start'
            }}
          >
            <AvatarGroup max={5} style={{ margin: '0.8em 0em', zIndex: 1 }}>
              {team.members.map((member) => {
                return (
                  <Avatar
                    alt={member.name}
                    src="/static/images/avatar/1.jpg"
                    style={{
                      alignSelf: 'center'
                    }}
                    key={member.id}
                  />
                );
              })}
            </AvatarGroup>
          </div>
        </Paper>
      </motion.div>
    </Grid>
  );
}
