import {
    FormControl,
    Grid,
    IconButton,
    MenuItem,
    Select
  } from '@material-ui/core';
import Dialog from '@material-ui/core/Dialog';
import React, { useContext, useEffect, useState } from 'react';
import TimerIcon from '@material-ui/icons/Timer';
import { useStyles } from '../App';


interface InputProps {
  open: boolean;
  onClose: () => void;
  selectedUser: string;
}

export function CompactPlannerView({
  onClose,
  open,
  selectedUser
}: InputProps) {

  const handleClose = () => {
    onClose();
  };

  const classes = useStyles();

  return (
      <Dialog
        onClose={handleClose}
        open={open}
        fullWidth={true}
        maxWidth={'sm'}
        PaperProps={{
          style: {
            height: '80%',
            backgroundColor: '#fff',
            color: 'black',
            borderRadius: '30px',
            padding: '35px',
            boxShadow: '0px 0px 15px 0px rgba(0,0,0,0.15)',
            transition: 'all 1s'
          },
          elevation: 0
        }}
        BackdropProps={{ style: { backgroundColor: 'rgba(0, 0, 0, 0.25)' } }}
      >
        <div style={{display:'flex', justifyContent:'center', alignItems:'center', fontStyle:'Poppins', fontSize:'1.5em', fontWeight:700, marginBottom: '1.6em'}}>
          {selectedUser}'s tasks
        </div>

        <div
            className={classes.taskBubble}
            style={{
                backgroundColor: '#DFFFEC',
                display: 'flex',
                flexDirection: 'column',
                minHeight: '1.5em',
                margin: '0px',

            }}
        >
            <Grid container>
                <Grid
                container
                item
                xs={8}
                md={8}
                lg={8}
                justify="flex-start"
                >
                <div
                    style={{ width: '90%' }}
                >
                    <h3 className={classes.bubbleTitle}>New task</h3>
                </div>
                </Grid>

                <Grid
                container
                item
                xs={4}
                md={4}
                lg={4}
                justify="flex-end"
                alignItems="center"
                >
                <div
                    style={{ display: 'flex', alignItems: 'center' }}
                >
                    <TimerIcon style={{ marginRight: '5px' }} />
                    <h3 className={classes.bubbleWorkTime}>
                        2h30m
                    </h3>
                </div>
                </Grid>
            </Grid>
        </div>
      </Dialog>
  );
}
