import '../App.css';
import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { GetTaskStatusData } from './TaskStatusData';

import PropTypes from 'prop-types';
import Dialog from '@material-ui/core/Dialog';

import Select from '@material-ui/core/Select';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';

import FormatAlignLeftIcon from '@material-ui/icons/FormatAlignLeft';
import TimerIcon from '@material-ui/icons/Timer';
import IconButton from '@material-ui/core/IconButton';
import CloseIcon from '@material-ui/icons/Close';
import CreateIcon from '@material-ui/icons/Create';
import FlagIcon from '@material-ui/icons/Flag';

const styles = makeStyles((theme) => ({
    detailTitle: {
        fontFamily: 'Poppins',
        color: '#2b2b2b',
        fontWeight: '700',
        fontSize: '1.6em',
        paddingBottom: '15px',
        marginBlockStart: 0,
        marginBlockEnd: 0,
    },
    detailText: {
        fontFamily: 'Poppins',
        color: '#2b2b2b',
        fontWeight: '500',
        fontSize: '1.1em',
        marginBlockStart: 0,
        marginBlockEnd: 0,
        paddingLeft: '20px',
    },
    closeButton: {
        position: 'absolute',
        right: theme.spacing(2),
        top: theme.spacing(3.5),
        color: '#1f1f1d',
    },
    editButton: {
        position: 'absolute',
        right: theme.spacing(8),
        top: theme.spacing(3.5),
        color: '#2B2B28',
    },
    taskStatus: {
        fontFamily: 'Poppins',
        color: '#2b2b2b',
        fontWeight: '500',
        fontSize: '1.1em',
        padding: '0.4em 0.7em',
        borderRadius: '10px',
    },
    taskSelect: {
        width: '90%',
        background: '#FFEAC8',
        borderStyle:'none',
        borderWidth: 2,
        borderRadius: '10px',
        marginLeft: 18,
        padding: '10px 0px',
        "&:focus":{
            borderRadius: '10px',
            background: 'transparent',
        },
    },
}));

const renderTaskStatusBubble = (taskStatusData) => {
    const classes = styles();
    return (
        <span className={classes.taskStatus} style={{backgroundColor: taskStatusData.color}}>
            {taskStatusData.title}
        </span>
    )
}

export function DetailDialog(props) {
    const classes = styles();

    const { onClose, selectedTask, open } = props;

    const handleClose = () => {
        onClose(selectedTask);
    };

    const taskStatusData = GetTaskStatusData(selectedTask.status);

    const [val,setVal] = useState(taskStatusData.id);

    const handleChange = (event) => {
        setVal(event.target.value);
    };

    // moves the menu below the select input
    const menuProps = {
        anchorOrigin: {
        vertical: "bottom",
            horizontal: "left"
        },
        transformOrigin: {
        vertical: "top",
            horizontal: "left"
        },
        getContentAnchorEl: null
    };

    return (
        <Dialog
            onClose={handleClose}
            aria-labelledby="simple-dialog-title"
            open={open}
            onRendered={console.log("hey")}
            fullWidth={true}
            maxWidth={'xs'}
            PaperProps={{ style: { backgroundColor: '#FFEAC8', color: 'black', borderRadius: '30px', padding: '35px', boxShadow: '0px 0px 15px 0px rgba(0,0,0,0.15)', }, elevation: 0 }}
            BackdropProps={{ style: { backgroundColor: "rgba(0, 0, 0, 0.25)" } }}
        >
            <h1 className={classes.detailTitle}>{selectedTask.title}</h1>
            <IconButton aria-label="close" className={classes.editButton} onClick={onClose}>
                <CreateIcon />
            </IconButton>
            <IconButton aria-label="close" className={classes.closeButton} onClick={onClose}>
                <CloseIcon />
            </IconButton>

            <span style={{ display: 'flex', alignItems: 'center', justifyContent: 'start', padding: '10px 0px' }}>
                <FlagIcon style={{ paddingTop: '2px' }} />
                <FormControl>
                    <Select
                        disableUnderline
                        classes={{ root: classes.taskSelect }}
                        MenuProps={menuProps}
                        value={taskStatusData.id}
                        onChange={handleChange}
                    >
                        <MenuItem value={"0"}>{renderTaskStatusBubble(GetTaskStatusData('done'))}</MenuItem>
                        <MenuItem value={"1"}>{renderTaskStatusBubble(GetTaskStatusData('in_progress'))}</MenuItem>
                        <MenuItem value={"2"}>{renderTaskStatusBubble(GetTaskStatusData('not_done'))}</MenuItem>
                    </Select>
                </FormControl>
            </span>


            <span style={{ display: 'flex', alignItems: 'flex-start', justifyContent: 'start', padding: '10px 0px' }}>
                <FormatAlignLeftIcon style={{ paddingTop: '2px' }} />
                <h3 className={classes.detailText}>
                    {selectedTask.desc}
                </h3>
            </span>

            <span style={{ display: 'flex', alignItems: 'flex-start', justifyContent: 'start', padding: '10px 0px' }}>
                <TimerIcon />
                <h3 className={classes.detailText}>
                    {selectedTask.workTime}
                </h3>
            </span>

        </Dialog>
    );
}

DetailDialog.propTypes = {
    onClose: PropTypes.func.isRequired,
    open: PropTypes.bool.isRequired,
    selectedTask: PropTypes.object.isRequired,
};
