import React from 'react';
import {
  CommTasksQuery,
  MeetingTasksQuery,
  WorkTasksQuery
} from '../generated/graphql';
import { DateText } from './DateText';
import { RenderTaskList } from './Task/RenderTaskList';

interface InputProps {
  currWeek: number[];
  commData: CommTasksQuery | undefined;
  meetingData: MeetingTasksQuery | undefined;
  workData: WorkTasksQuery | undefined;
}

export function DayRow({
  currWeek,
  meetingData,
  workData,
  commData
}: InputProps) {
  return currWeek.map((day, i) => {
    const dayMeetingData = meetingData?.meetingTaskMany?.items.filter(
      ({ date }: { date: string }) =>
        new Date(date).setHours(0, 0, 0, 0) ===
        new Date(day).setHours(0, 0, 0, 0)
    );

    const dayWorkData = workData?.workTaskMany?.items.filter(
      ({ date }: { date: string }) =>
        new Date(date).setHours(0, 0, 0, 0) ===
        new Date(day).setHours(0, 0, 0, 0)
    );

    const dayCommData = commData?.communicationTaskMany?.items.filter(
      ({ date }: { date: string }) =>
        new Date(date).setHours(0, 0, 0, 0) ===
        new Date(day).setHours(0, 0, 0, 0)
    );

    return (
      <div
        key={day}
        style={{
          alignItems: 'start',
          display: 'flex',
          flexDirection: 'column',
          width: '100%'
        }}
      >
        <DateText day={day} />
        <RenderTaskList
          date={day}
          meetingData={dayMeetingData}
          workData={dayWorkData}
          commData={dayCommData}
        />
      </div>
    );
  });
}
