import React from 'react';

export function SideBar() {
    const classes = useStyles();
    const [invisible, setInvisible] = React.useState(false);
  
    const handleBadgeVisibility = () => {
      setInvisible(!invisible);
    };
  
    return (
      <Box className={classes.sideBar}>
        <Box alignSelf="center">
          <Avatar alt="Remy Sharp" src="/static/images/avatar/1.jpg" />
        </Box>
        <Badge color="secondary" variant="dot" className={classes.iconButton} onClick={handleBadgeVisibility} invisible={invisible} aria-label="notification" style={{cursor: 'pointer'}}>
          <NotificationsIcon style={{ fontSize: 25 }}/>
        </Badge>
        <Box paddingLeft={2} paddingRight={5} marginTop={5}>
          <Button className={classes.button}>
            Hlavný plán
          </Button>
          <Button className={classes.button}>
            Denný plán
          </Button>
        </Box>
      </Box>
    );
  }
