import { useStyles } from '../App';
import '../App.css';
import React, { useState } from 'react';
import { GetTaskStatusData } from './TaskStatusData';
import { DetailDialog } from './DetailDialog';
import Box from '@material-ui/core/Box';
import moment from 'moment';
import 'moment/locale/sk';

import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";
import TimerIcon from '@material-ui/icons/Timer';

import data from '../MOCK_DATA.json';

const reorder = (list, startIndex, endIndex) => {
    const result = Array.from(list);
    const [removed] = result.splice(startIndex, 1);
    result.splice(endIndex, 0, removed);

    return result;
};

/**
 * Moves an item from one list to another list.
 */
const move = (source, destination, droppableSource, droppableDestination) => {
const sourceClone = Array.from(source);
const destClone = Array.from(destination);
const [removed] = sourceClone.splice(droppableSource.index, 1);

destClone.splice(droppableDestination.index, 0, removed);

const result = {};
result[droppableSource.droppableId] = sourceClone;
result[droppableDestination.droppableId] = destClone;

return result;
};

export function TaskBubble(type, date) {
    const classes = useStyles();

    const filteredData = data.filter((task) => task.type.includes(type)).filter((task) => task.date.includes(moment(date).format("DD/MM/yyyy"))); //filter tasks by specified type

    const [open, setOpen] = React.useState(false);
    const [selectedTask, setSelectedTask] = React.useState({ id: '0', title: 'Test', desc: 'Test', workTime: '100h', status: 'not_done' });

    const handleClickOpen = (task) => {
        setOpen(true);
        setSelectedTask(task);
    };

    const handleClose = () => {
        setOpen(false);
    }

    function onDragEnd(result) {
        const { source, destination } = result;

        // dropped outside the list
        if (!destination) {
          return;
        }
        const sInd = +source.droppableId;
        const dInd = +destination.droppableId;

        if (sInd === dInd) {
          const items = reorder(state[sInd], source.index, destination.index);
          const newState = [...state];
          newState[sInd] = items;
          setState(newState);
        } else {
          const result = move(state[sInd], state[dInd], source, destination);
          const newState = [...state];
          newState[sInd] = result[sInd];
          newState[dInd] = result[dInd];

          setState(newState.filter(group => group.length));
        }
    }

    if (filteredData === undefined || filteredData.length === 0) {
        return (
            <div style={{justifyContent:"center", height:"100%"}}>
                <h3 style={{textAlign:"center", paddingTop:"18px", color:"#bdbdbd", fontFamily:"Poppins", fontSize:"18px", fontWeight:"500"}}>
                    Empty
                </h3>
            </div>
        )

    } else {
        return (
            <DragDropContext onDragEnd={onDragEnd}>
                <Droppable droppableId={`${moment(date).unix()}`}>
                    {(provided) => (
                        <div {...provided.droppableProps} ref={provided.innerRef}>
                            {filteredData.map((item, index) => (
                                <Draggable key={item.id} draggableId={item.id} index={index} >
                                    {(provided) => (
                                        <div {...provided.draggableProps} {...provided.dragHandleProps}>
                                            <Box  className={classes.taskBubble} style={{ backgroundColor: GetTaskStatusData(item.status).color }} onClick={() => handleClickOpen(item)} ref={provided.innerRef}>
                                                <Box flexGrow={1}>
                                                    <h3 className={classes.bubbleTitle}>{item.title}</h3>
                                                </Box>
                                                <TimerIcon style={{marginRight: '5px'}} />
                                                <h3 className={classes.bubbleWorkTime}>{item.workTime}</h3>
                                            </Box>
                                            <DetailDialog open={open} onClose={handleClose} selectedTask={selectedTask} />
                                        </div>
                                    )}
                                </Draggable>
                            ))}
                            {provided.placeholder}
                        </div>
                    )}
                </Droppable>
            </DragDropContext>
        )
    }
};
