import React, { useState, useEffect } from 'react';
import './App.css';
import './index';

import { getISOWeekDates } from './DateCalc';
import { SideBar } from './sideBar/SideBar';
import { TopBar } from './sections/TopBar';
import MainProvider from './providers/MainProvider';
import { makeStyles } from '@material-ui/core/styles';
import moment from 'moment';
import 'moment/locale/sk';
import { RenderTaskList } from './sections/RenderTaskList';



export const useStyles = makeStyles((theme) => ({
  main: {
    backgroundColor: '#f8f8f8',
    height: '-webkit-fill-available',
    scrollBehavior: 'smooth',
  },
  root: {
    padding: '2px 10px',
    display: 'flex',
    alignItems: 'center',
    width: 300,
    height: '-webkit-fill-available',
    borderColor: '#f1d6ab',
    borderRadius: 50,
    background: '#00000040',
  },
  input: {
    marginLeft: theme.spacing(1),
    flex: 1,
    color: '#f1d6ab',
    fontFamily: 'Sen',
    fontSize: 18,
  },
  taskListWindow: {
    justifyContent: 'center',
    margin: '20px 30px 20px 30px',
    display: 'flex',
    width: '95%',
    minHeight: '8em',
    background: '#FAFAFA',
    borderRadius: '20px',
    padding: '20px 0px',
    boxShadow: '0px 0px 10px 0px rgba(0,0,0,0.1)',
    "&:focus":{
      outline: '0px',
    },
  },
  taskBubble: {
    display: 'flex',
    borderRadius: '15px',
    padding: '1.1em 1.6em',
    marginBottom: '1em',
    fontFamily:'Poppins',
    backgroundColor: '#fff',
    color: '#2b2b28',
    width: '95%',
    alignItems: 'center',
    cursor: 'pointer',
    position: 'relative',
    right: '12px',
  },
  bubbleTitle: {
    color: '#2b2b2b',
    fontWeight: '600',
    fontSize: '0.9em',
    marginBlockStart: 0,
    marginBlockEnd: 0,
    textTransform: 'capitalize',
  },
  bubbleDesc: {
    fontWeight: '600',
    fontSize: 15,
    marginBlockStart: 0,
    marginBlockEnd: 0,
  },
  bubbleWorkTime: {
    fontWeight: '600',
    fontSize: '0.8em',
    marginBlockStart: 0,
    marginBlockEnd: 0,
    paddingTop: '2px',
  },
  chevronButtons: {
    cursor: 'pointer',
    fontSize: '2em',
    fontWeight: '500',
    color: 'rgba(43,43,43,0.7)',
  },
  taskSelect: {
    fontFamily: 'Poppins',
  },
  monthTitle: {
    fontFamily: 'Poppins',
    color: '#2b2b2b',
    fontWeight: '500',
    fontSize: '1.4em',
    marginBlockStart: 0,
    marginBlockEnd: 0,
    textTransform: 'capitalize',
    padding: '0px 30px'
  },
  taskTypeTitle: {
    fontFamily: 'Poppins',
    fontSize: '1.3em',
    fontWeight: '600',
  },
}));

function DateText(day) {
  const number = moment(day).format('D');
  const dayText = moment(day).format('dddd');

  if(moment(day).dayOfYear() === moment().dayOfYear()) {
    return (
      <div style={{alignItems:'center', display: 'flex', flexDirection:'column', width:'3em', backgroundColor:'rgba(241, 214, 171, 0.5)', padding:'20px', borderRadius:'20px'}}>
        <h3 style={{fontWeight:'800', fontSize:'1.4em', color:'#2b2b2b'}}>
          {number}.
        </h3>

        <h3 style={{fontWeight:'600', fontSize:'1.1em', color:'#6c6c6c', textTransform:'capitalize'}}>
          {dayText}
        </h3>
      </div>
    )
  } else {
    return (
      <div style={{alignItems:'center', display: 'flex', flexDirection:'column', width:'3em', padding:'20px'}}>
        <h3 style={{fontFamily:'Poppins', fontWeight:'800', fontSize:'1.4em', color:'#2b2b2b'}}>
          {number}.
        </h3>

        <h3 style={{fontFamily:'Poppins', fontWeight:'600', fontSize:'1.1em', color:'#6c6c6c', textTransform:'capitalize'}}>
          {dayText}
        </h3>
      </div>
    )
  }
}


export function App() {
  const classes = useStyles();

  const [selectedMonth,setMonth] = useState(moment().format('M'));

  // const handleMonthChange = (event) => {
  //   setMonth(event.target.value);
  //   //week = getDatesFromMonth(selectedMonth);
  //   setWeek(0);
  // };

  const [weekState, setWeek] = useState(moment().week()); //Current selected week 
  let ISOWeekDates = getISOWeekDates(weekState);
  let week = ISOWeekDates[0];
  let month = ISOWeekDates[1];

  const findCurrDate = () => {
    setWeek(moment().week());

    setTimeout(() => {  
      let currDate = moment().startOf('day').valueOf();
      document.getElementById(currDate).scrollIntoView({
        block: 'center',
        behavior: 'smooth'
      });
    }, 1000);
  };

  const [anchorEl, setAnchorEl] = useState(null);

  const handleClick = (event) => {
      setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
      setAnchorEl(null);
  };


  return (
    <MainProvider>
    <div style={{ width: '100%', height: '100%', backgroundColor: '#f8f8f8', display:'flex', flexDirection:'row',}} >

      <SideBar />

      <div style={{alignItems:'center', display: 'flex', flexDirection:'column', width:'100%', paddingLeft:'10px'}}>

        <TopBar />

        <span style={{display: 'flex', alignItems: 'center', justifyContent: 'space-around', flexDirection:'row', width:'90%', marginLeft:'7em', height:'3em'}}>
            <h2 className={classes.taskTypeTitle}>
                Meetingy
            </h2>
            <h2 className={classes.taskTypeTitle}>
                Práca
            </h2>
            <h2 className={classes.taskTypeTitle}>
                Komunikácia
            </h2>
        </span>


        <div style={{width: '100%', flexDirection:'column', overflowY: 'scroll'}}>
          <div style={{alignItems:'center', display: 'flex', flexDirection:'column', paddingLeft: '20px'}}>
            {week.map((day, i) => (
              <div key={day} style={{alignItems:'center', display: 'flex', flexDirection:'row', width: '100%'}}>
                {DateText(day)}
                <RenderTaskList date={day}/>
              </div>
            ))}
          </div>
        </div>
      </div>

    </div>
    </MainProvider>
  );
}

export default App;
