import '../App.css';
import React, { useState, useContext, useRef, createRef } from 'react';
import { styles } from './css/dialog_css';
import { GetTaskStatusData } from '../taskBubble/TaskStatusData';
import moment from 'moment';

import { MeetEditDialog } from './editModeDialogs/MeetEditDialog';
import Button from '@material-ui/core/Button';
import PropTypes from 'prop-types';
import Dialog from '@material-ui/core/Dialog';
import Select from '@material-ui/core/Select';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import TextField from '@material-ui/core/TextField';
import { MuiPickersUtilsProvider, DatePicker } from "@material-ui/pickers";
import MomentUtils from '@date-io/moment';
import GooglePlacesAutocomplete from 'react-google-places-autocomplete';

import { 
    FormatAlignLeftIcon,
    IconButton,
    CreateIcon,
    CloseIcon,
    FlagIcon,
    PlaceIcon,
    ScheduleIcon,
    TimerIcon,
    TodayIcon
} from '../imports/icons';

import { DataContext } from "../providers/DataProvider";
import { UserContext } from "../providers/UserProvider";
import { renderTaskStatusBubble, menuProps } from '../const/dialogConsts';

export function MeetDetailDialog(props) {
    const classes = styles();

    let taskTitleId = useRef();

    const { onClose, selectedTask, open } = props;
    const { setRefresh } = useContext(DataContext);
    const user = useContext(UserContext);
    const taskStatusData = GetTaskStatusData(selectedTask.status);
    const workTime = moment.duration(moment(selectedTask.endTime, "HH:mm").diff(moment(selectedTask.startTime, "HH:mm")));

    const [editMode, enterEditMode] = useState(false);
    const [titleState, setTitle] = useState("");
    const [descState, setDesc] = useState("");
    const [workTimeState, setWorkTime] = useState("");
    const [selectedDate, handleDateChange] = useState(new Date());
    const [taskAddress, setAddress] = useState("");

    const handleClose = () => {
        enterEditMode(false);
        setTitle('');
        setDesc('');
        setWorkTime('');
        onClose();
    };

    const editModeOn = () => {
        enterEditMode(true);
        setTitle(selectedTask.title);
        setDesc(selectedTask.desc);
        setAddress(selectedTask.address);
        setWorkTime(selectedTask.workTime);
        taskTitleId.current.focus()
    }

    const editModeOff = () => {
        enterEditMode(false);
        setTitle('');
        setDesc('');
        setAddress('');
        setWorkTime('');
    }

    const handleStatusChange = (event) => {
        try {
            const requestOptions = {
                method: 'POST',
                headers: { 'Content-Type': 'application/json', 'Accept': '*/*', 'Authorization': 'Bearer ' + localStorage.getItem("@token") },
                body: JSON.stringify({ 
                    token: user.uid,
                    collection: "meeting_tasks",
                    taskId: selectedTask.id,
                    status: event.target.value,
                    })
            };
            fetch('https://europe-west1-plannerapp-960c1.cloudfunctions.net/app/api/update-task-status', requestOptions)
                .then(response => response.json())
                .then(data => setRefresh(data.taskId));
        } catch(error) {
            console.log(error);
            alert("Something went wrong. Please try again later.")
        }
        selectedTask.status = event.target.value;
    }

    async function updateTask() {
        try {
            const requestOptions = {
                method: 'POST',
                headers: { 'Content-Type': 'application/json', 'Accept': '*/*', 'Authorization': 'Bearer ' + localStorage.getItem("@token") },
                body: JSON.stringify({ 
                    token: user.uid,
                    taskId: selectedTask.id,
                    title: selectedTask.title,
                    desc: selectedTask.desc,
                    date: moment(selectedDate).format("DD/MM/yyy"),
                    status: selectedTask.status,
                    address: selectedTask.address,
                    startTime: selectedTask.startTime,
                    endTime: selectedTask.endTime,
                    category: "",
                })
            };
            await fetch('https://europe-west1-plannerapp-960c1.cloudfunctions.net/app/api/update-meeting-task', requestOptions)
            .then(response => response);
        } catch(error) {
            console.log(error);
            alert("Something went wrong. Please try again later.")
        }

        setRefresh(selectedTask.id);
        editModeOff();
    };

    async function deleteTask() {
        try {
            const requestOptions = {
                method: 'POST',
                headers: { 'Content-Type': 'application/json', 'Accept': '*/*', 'Authorization': 'Bearer ' + localStorage.getItem("@token") },
                body: JSON.stringify({ taskId: selectedTask.id, token: user.uid })
            };
            await fetch('https://europe-west1-plannerapp-960c1.cloudfunctions.net/app/api/delete-meeting-task', requestOptions)
            .then(response => response);
        } catch(error) {
            console.log(error);
            alert("Something went wrong. Please try again later.")
        }

        setRefresh(selectedTask.id);
        handleClose();
    };

    const TaskTitle = () => {
        if (!editMode) {
            return (
                <h1 className={classes.detailTitle} onClick={() => {editModeOn()}}>{selectedTask.title}</h1>
            );
        } else {
            return (
                <TextField style={{width: "70%"}} inputRef={taskTitleId} className={classes.taskTitleInput} value={titleState} onChange={e => setTitle(e.target.value)} InputProps={{ disableUnderline: true }}/>
            );
        }
    }

    const TaskStatus = () => {
        return (
            <span style={{ display: 'flex', alignItems: 'center', justifyContent: 'start', padding: '10px 0px' }}>
                <FlagIcon style={{ paddingTop: '2px' }} />
                <FormControl>
                    <Select
                        disableUnderline
                        classes={{ root: classes.taskSelect }}
                        MenuProps={menuProps}
                        value={taskStatusData.id}
                        onChange={handleStatusChange}
                    >
                        <MenuItem value={"done"}>{renderTaskStatusBubble(GetTaskStatusData('done'))}</MenuItem>
                        <MenuItem value={"in_progress"}>{renderTaskStatusBubble(GetTaskStatusData('in_progress'))}</MenuItem>
                        <MenuItem value={"not_done"}>{renderTaskStatusBubble(GetTaskStatusData('not_done'))}</MenuItem>
                    </Select>
                </FormControl>
            </span>
        );
    }

    const TaskDescription = () => {
        if (!editMode) {
            if (selectedTask.desc !== "") {
                return (
                    <span style={{ display: 'flex', alignItems: 'flex-start', justifyContent: 'start', padding: '10px 0px' }}>
                        <FormatAlignLeftIcon style={{ paddingTop: '2px' }} />
                        <h3 className={classes.detailText}>
                            {selectedTask.desc}
                        </h3>
                    </span>
                );
            } else {
                return ("")
            }
        } else {
            return (
                <span style={{ display: 'flex', alignItems: 'flex-start', justifyContent: 'start', padding: '10px 0px' }}>
                    <FormatAlignLeftIcon style={{ paddingTop: '2px' }} />
                    <TextField multiline style={{width: "70%"}} className={classes.taskDescInput} value={descState} onChange={e => setDesc(e.target.value)}/>
                </span>
            );
        }
    }

    const TaskAddress = () => {
        if (!editMode) {
            if (selectedTask.address !== "") {
                return (
                    <span style={{ display: 'flex', alignItems: 'flex-start', justifyContent: 'start', padding: '10px 0px' }}>
                        <PlaceIcon />
                        <h3 className={classes.detailText}>
                            {selectedTask.address}
                        </h3>
                    </span>
                );
            } else {
                return("")
            }
        } else {
            return (
                <span style={{ display: 'flex', alignItems: 'center', justifyContent: 'start', padding: '10px 0px' }}>
                    <PlaceIcon />
                    <GooglePlacesAutocomplete
                        selectProps={{
                            styles: {
                                control: (provider, state) => ({
                                    ...provider,
                                    background: 'none',
                                    fontFamily: 'Poppins',
                                    width: '97.8%',
                                    height: '3.8em',
                                    borderColor: 'rgba(0, 0, 0, 0.23)',
                                    "&:hover": {
                                        borderColor: state.isFocused ? "-webkit-focus-ring-color" : "black"
                                    }
                                }),
                                container: (provided) => ({
                                    ...provided,
                                    width: '100%',
                                    marginLeft: '10px',
                                }),
                                input: (provided) => ({
                                    ...provided,
                                    fontFamily: 'Poppins',
                                    color: '#2b2b2b',
                                }),
                                option: (provided) => ({
                                    ...provided,
                                    fontFamily: 'Poppins',
                                    color: '#2b2b2b',
                                }),
                                menu: (provided) => ({
                                    ...provided,
                                    width: '97.8%',
                                }),
                                singleValue: (provided) => ({
                                    ...provided,
                                    fontFamily: 'Poppins',
                                    color: '#2b2b2b',
                                }),
                                dropdownIndicator: (provider) => ({
                                    ...provider,
                                    color: 'rgba(0, 0, 0, 0.23)',
                                }),
                                indicatorSeparator: (provider) => ({
                                    ...provider,
                                    color: 'rgba(0, 0, 0, 0.23)',
                                }),
                            },
                            taskAddress,
                            onChange: setAddress(taskAddress),
                        }}
                    />
                </span>
            );
        }
    }

    const TaskDatePicker = () => {
        if (editMode) {
            return (
                <span style={{ display: 'flex', alignItems: 'center', justifyContent: 'start', padding: '10px 0px' }}>
                    <TodayIcon />
                    <DatePicker
                        autoOk
                        disableToolbar
                        variant="inline"
                        inputVariant="outlined"
                        style={{ width: 90, marginLeft: 10, background: 'rgba(171, 171, 171, 0.15)', borderRadius: '5px', border: 'none' }}
                        format="D.M.YYYY"
                        value={selectedDate}
                        onChange={date => handleDateChange(date)}
                    />
                </span>
            );
        } else {
            return("")
        }
    }


    return (
        <MuiPickersUtilsProvider utils={MomentUtils}>
            <Dialog
                onClose={handleClose}
                open={open}
                fullWidth={true}
                maxWidth={'xs'}
                PaperProps={{ style: { backgroundColor: '#FFEAC8', color: 'black', borderRadius: '30px', padding: '35px', boxShadow: '0px 0px 15px 0px rgba(0,0,0,0.15)', transition: 'all 1s'}, elevation: 0 }}
                BackdropProps={{ style: { backgroundColor: "rgba(0, 0, 0, 0.25)" } }}
            >
                <TaskTitle />

                <IconButton aria-label="edit" className={classes.editButton} onClick={() => {editModeOn()}}>
                    <CreateIcon />
                </IconButton>

                <IconButton aria-label="close" className={classes.closeButton} onClick={handleClose}>
                    <CloseIcon />
                </IconButton>

                <TaskStatus />
                <TaskDescription />
                <TaskAddress />

                <span style={{ display: 'flex', alignItems: 'flex-start', justifyContent: 'start', padding: '10px 0px' }}>
                    <ScheduleIcon />
                    <h3 className={classes.detailText}>
                        {selectedTask.startTime} - {selectedTask.endTime}
                    </h3>
                </span>

                <span style={{ display: 'flex', alignItems: 'flex-start', justifyContent: 'start', padding: '10px 0px' }}>
                    <TimerIcon />
                    <h3 className={classes.detailText}>
                        {parseInt(workTime.asHours())}h{parseInt(workTime.asMinutes())%60}m
                    </h3>
                </span>

                <TaskDatePicker />

                {editMode 
                    ?   <Button className={classes.updateTaskButton} onClick={() => {editModeOff();updateTask()}}>
                            Aktualizovať
                        </Button>
                    : ""
                }

            </Dialog>
        </MuiPickersUtilsProvider>
    );
}

MeetDetailDialog.propTypes = {
    onClose: PropTypes.func.isRequired,
    open: PropTypes.bool.isRequired,
    selectedTask: PropTypes.object.isRequired
};
