import { useStyles } from '../App';
import React, { Component } from 'react';
import PropTypes from 'prop-types';
import Box from '@material-ui/core/Box';
import TimerIcon from '@material-ui/icons/Timer';
import Dialog from '@material-ui/core/Dialog';
import DialogTitle from '@material-ui/core/DialogTitle';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import data from '../MOCK_DATA.json';
import { SettingsPowerRounded } from '@material-ui/icons';

function SimpleDialog(props) {
    const classes = useStyles();
    const { onClose, selectedValue, open } = props;

    const handleClose = () => {
      onClose(selectedValue);
    };

    const handleListItemClick = (value) => {
      onClose(value);
    };

    return (
      <Dialog onClose={handleClose} aria-labelledby="simple-dialog-title" open={open}>
        <DialogTitle id="simple-dialog-title">Set backup account</DialogTitle>
        <List>
          {emails.map((email) => (
            <ListItem button onClick={() => handleListItemClick(email)} key={email}>
              <ListItemText primary={email} />
            </ListItem>
          ))}
        </List>
      </Dialog>
    );
}

SimpleDialog.propTypes = {
    onClose: PropTypes.func.isRequired,
    open: PropTypes.bool.isRequired,
    selectedValue: PropTypes.string.isRequired,
};

export function TaskBubble(type) {
    const classes = useStyles();

    const filteredData = data.filter((task) => task.type.includes(type)); //filter tasks by type specified

    const [open, setOpen] = React.useState(false);
    const [selectedTask, setSelectedTask] = React.useState();

    const handleClickOpen = (task) => {
        setOpen(true);
        setSelectedTask(task);
    };

    const handleClose = () => {
        setOpen(false);
    }

    return (
        <div>
            {filteredData.map((item, index) => (
                <React.Fragment key={item.id} >
                    <Box className={classes.taskBubble} style={{ backgroundColor: item.state }} onClick={handleClickOpen}>
                        <Box flexGrow={1}>
                            <h3 className={classes.bubbleTitle}>{item.title}</h3>
                        </Box>

                        <TimerIcon style={{marginRight:'5px'}}/>
                        <h3 className={classes.bubbleWorkTime}>{item.workTime}</h3>
                    </Box>

                    <SimpleDialog open={open} onClose={handleClose} />
                </React.Fragment>
            ))}
        </div>
    )
  };
