import { Grid, Typography } from '@material-ui/core';
import { toast } from 'material-react-toastify';
import React from 'react';
import NotificationsIcon from '@material-ui/icons/Notifications';

interface InputProps {
  text: string;
  background: 'black-background' | 'green-background';
}

export function notification({
  text,
  background
}: InputProps): React.ReactText {
  return toast(
    <div
      style={{
        width: '100%',
        height: '5em'
      }}
    >
      <Grid
        container
        justify="space-evenly"
        alignContent="center"
        style={{ width: '100%', height: '100%' }}
      >
        <Grid
          container
          item
          xs={2}
          md={2}
          lg={2}
          justify="flex-start"
          alignContent="center"
        >
          <NotificationsIcon style={{ fontSize: '30px', color: 'white' }} />
        </Grid>
        <Grid
          item
          xs={10}
          md={10}
          lg={10}
          container
          justify="flex-start"
          alignItems="center"
        >
          <Typography
            variant="h5"
            style={{
              fontFamily: 'Poppins',
              fontSize: '1.2em',
              fontWeight: 600,
              color: 'white'
            }}
          >
            {text}
          </Typography>
          {/* <Typography
          variant="h5"
          style={{
            fontFamily: 'Poppins',
            fontSize: '0.8em',
            color: 'white'
          }}
        >
          Tim: {data?.notification?.team?.name}
        </Typography> */}
        </Grid>
      </Grid>
    </div>,
    {
      className: background,
      closeButton: false
    }
  );
}
